        // if(PALETTE == 0){col = contrast(col, 1.01); }
        return col;

    }
}

vec3 sanitize(vec3 dc){
    dc.r = min(1., dc.r);
    dc.g = min(1., dc.g);
    dc.b = min(1., dc.b);

    if(!(dc.r>=0.) && !(dc.r<0.)){
        return vec3(1,0,0);
    } else if(!(dc.g>=0.) && !(dc.g<0.)){
        return vec3(1,0,0);
    } else if(!(dc.b>=0.) && !(dc.b<0.)){
        return vec3(1,0,0);
    } else {
        return dc;
    }

}

