    } else if(!(dc.g>=0.) && !(dc.g<0.)){
        return vec3(1,0,0);
    } else if(!(dc.b>=0.) && !(dc.b<0.)){
        return vec3(1,0,0);
    } else {
        return dc;
    }

}


