// HSL to RGB color conversion function
vec3 hslToRgb(vec3 c){return c.z+c.y*(clamp(abs(mod(c.x*6.+vec3(0,4,2),6.)-3.)-1.,0.,1.)-.5)*(1.-abs(2.*c.z-1.));}

// Main Mandelbrot calculation function
void calculateMandelbrot(out vec4 outputColor, vec2 pixelCoords) {
    // Normalize coordinates to maintain aspect ratio
    vec2 normalizedCoords = pixelCoords / iResolution.y;

    // Set up mouse interaction or automatic animation
    vec2 mouseParams;
    if (iMouse.z > 0.0) {
        // If mouse is clicked, use mouse position
        mouseParams = iMouse.xy / iResolution.xy;
    } else {
        // Otherwise, animate automatically
        mouseParams = vec2(sin(iTime / 2.0) + 1.0, cos(iTime) + 1.0) / 2.0 + 0.0001;
    }

    // Mandelbrot parameters
    float zoom = 3.0;           // Zoom level
    float centerX = -1.0;       // X center of view
    float centerY = -0.5;       // Y center of view
    float escapeRadius = mouseParams.y * 60.0;  // Escape radius threshold

    // Convert pixel coordinates to complex plane coordinates
    float realPart = centerX + normalizedCoords.x;
    float imagPart = centerY + normalizedCoords.y;

    // Initialize z = 0 for Mandelbrot iteration (z = z² + c)
    float zReal = realPart * zoom;
    float zImag = imagPart * zoom;

    // Iterate the Mandelbrot equation
    int iterations;
    float magnitudeSquared;
    int maxIterations = int(mouseParams.x * 30.0) + 1;

    for (iterations = 0; iterations < maxIterations; iterations++) {
        // Calculate z² + c
        float newReal = zReal * zReal - zImag * zImag + realPart * zoom;
        float newImag = 2.0 * zReal * zImag + imagPart * zoom;

        zReal = newReal;
        zImag = newImag;

        // Check if point escaped
        magnitudeSquared = zReal * zReal + zImag * zImag;
        if (magnitudeSquared > escapeRadius) {
            break;
        }
    }

    // Color the pixel based on escape time and magnitude
    float hue, saturation, lightness;

    if (magnitudeSquared > escapeRadius) {
        // Point escaped - color based on iterations
        hue = magnitudeSquared / escapeRadius;
        saturation = 1.0;
        lightness = 0.4 + float(iterations) / 50.0;
    } else {
        // Point is in the Mandelbrot set
        hue = magnitudeSquared;
        saturation = 1.0;
        lightness = 0.5 + mouseParams.x / 2.0;
    }

    // Convert HSL to RGB and apply artistic transformation
    vec3 color = hslToRgb(vec3(hue, saturation, lightness));
    outputColor = vec4(atan(sqrt(color)), 1.0);  // Artistic color transformation
}

// Main shader entry point with anti-aliasing
void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float sampleCount = 16.0;

    // Random offset for jittered sampling (pseudo-random)
    vec2 jitter = vec2(0.5);

    // Initialize output color
    fragColor = vec4(0.0);

    // Perform multiple samples for anti-aliasing
    for (float k = sampleCount; k > 0.5; k--) {
        vec4 sampleColor;

        // Calculate color at jittered position
        calculateMandelbrot(sampleColor, fragCoord + jitter - 0.5);
        fragColor += sampleColor;

        // Update jitter position (creates pseudo-random pattern)
        jitter = fract(jitter + vec2(0.755, 0.57).yx);
    }

    // Average all samples
    fragColor /= sampleCount;
    fragColor.a = 1.0;  // Set full opacity
}

