vec2 getGridPoint(int idx, float time) {
    int x = idx % 4;
    int y = idx / 4;

    vec2 base = vec2(float(x), float(y)) / 3.0 - vec2(0.5);
    float dist = length(base);
    if(dist < 0.001) return base;

    vec2 normalized = base / dist;

    float phase = float(idx) * 0.5;
    vec2 offset = vec2(
        sin(time * 1.5 + phase) * 0.03,
        cos(time * 1.7 + phase * 1.3) * 0.03
    );

    offset += base * sin(time * 3.0 - length(base) * 10.0) * 0.02;
    base += offset;

    float scaleFactor = abs(normalized.x) + abs(normalized.y);
    vec2 distorted = 1.5 * base / pow(scaleFactor, 3.);

    return distorted;
}

float sdTriangle(vec2 p, vec2 a, vec2 b, vec2 c) {
    vec2 e0 = b - a;
    vec2 e1 = c - b;
    vec2 e2 = a - c;

    vec2 v0 = p - a;
    vec2 v1 = p - b;
    vec2 v2 = p - c;

    vec2 pq0 = v0 - e0 * clamp(dot(v0, e0) / dot(e0, e0), 0.0, 1.0);
    vec2 pq1 = v1 - e1 * clamp(dot(v1, e1) / dot(e1, e1), 0.0, 1.0);
    vec2 pq2 = v2 - e2 * clamp(dot(v2, e2) / dot(e2, e2), 0.0, 1.0);

    float s = sign(e0.x * e2.y - e0.y * e2.x);
    vec2 d = min(min(vec2(dot(pq0, pq0), s * (v0.x * e0.y - v0.y * e0.x)),
                     vec2(dot(pq1, pq1), s * (v1.x * e1.y - v1.y * e1.x))),
                     vec2(dot(pq2, pq2), s * (v2.x * e2.y - v2.y * e2.x)));

    return -sqrt(d.x) * sign(d.y);
}

const int N = 4;
float sdPolygon(in vec2[N] v, in vec2 p) {
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for(int i=0, j=N-1; i<N; j=i, i++) {
        vec2 e = v[j] - v[i];
        vec2 w = p - v[i];
        vec2 b = w - e*clamp(dot(w,e)/dot(e,e), 0.0, 1.0);
        d = min(d, dot(b,b));
        bvec3 c = bvec3(p.y>=v[i].y,p.y<v[j].y,e.x*w.y>e.y*w.x);
        if(all(c) || all(not(c))) s*=-1.0;  
    }
    return s*sqrt(d);
}

float sdQuad(vec2 p, vec2 tl, vec2 tr, vec2 br, vec2 bl) {
    vec2[] polygon = vec2[](tl,tr,br,bl);
    return sdPolygon(polygon, p);
}

float distToSegment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

float sdEllipseSimple(vec2 p, vec2 center, vec2 radii, float rotation) {
    p -= center;
    float c = cos(rotation);
    float s = sin(rotation);
    p = mat2(c, s, -s, c) * p;
    vec2 np = p / max(radii, vec2(0.001));
    float d = length(np) - 1.0;
    return d * min(radii.x, radii.y);
}

vec4 getIncircle(vec2 a, vec2 b, vec2 c) {
    float la = length(b - c);
    float lb = length(c - a);
    float lc = length(a - b);
    float p = la + lb + lc;
    vec2 center = (la * a + lb * b + lc * c) / p;
    float s = p * 0.5;
    float area = abs((b.x - a.x) * (c.y - a.y) - (c.x - a.x) * (b.y - a.y)) * 0.5;
    float radius = area / s;
    return vec4(center, radius, 1.0);
}

vec4 fitOvalToQuad(vec2 tl, vec2 tr, vec2 bl, vec2 br) {
    vec2 center = (tl + tr + bl + br) * 0.25;
    float distTop = distToSegment(center, tl, tr);
    float distRight = distToSegment(center, tr, br);
    float distBottom = distToSegment(center, bl, br);
    float distLeft = distToSegment(center, tl, bl);
    float width = (distLeft + distRight) * 0.5 * 0.95;
    float height = (distTop + distBottom) * 0.5 * 0.95;
    vec2 horizontal = ((tr - tl) + (br - bl)) * 0.5;
    float rotation = atan(horizontal.y, horizontal.x);
    return vec4(width, height, rotation, 0.0);
}

// Get 2D cross-section at a specific Z position
vec4 getCrossSection(vec2 p, float z) {
    // Create a wave that travels along the extrusion
    float waveSpeed = 2.0;
    float waveOffset = iTime * waveSpeed;
    float localTime = z * 2.0 - waveOffset;

    vec2 grid[16];
    for(int i = 0; i < 16; i++) {
        grid[i] = getGridPoint(i, localTime);
    }

    float minDist = 1e10;
    vec3 nearestColor = vec3(0.0);

    for(int row = 0; row < 3; row++) {
        for(int col = 0; col < 3; col++) {
            int quadIdx = row * 3 + col;
            int tl = row * 4 + col;
            int tr = row * 4 + col + 1;
            int bl = (row + 1) * 4 + col;
            int br = (row + 1) * 4 + col + 1;

            float sblend = 1.-min(length(p)*1.5, 1.);//0.4 + sin(iTime * 1.5) * 0.4;
            bool useOval = (quadIdx == 0 || quadIdx == 4 || quadIdx == 8);

            if(useOval) {
                float quadSdf = sdQuad(p, grid[tl], grid[bl], grid[br], grid[tr]);
                vec2 center = (grid[tl] + grid[tr] + grid[bl] + grid[br]) * 0.25;
                vec4 oval = fitOvalToQuad(grid[tl], grid[tr], grid[bl], grid[br]);
                float ovalSdf = sdEllipseSimple(p, center, oval.xy, oval.z);
                float blendedSdf = mix(ovalSdf, quadSdf, sblend);

                if(blendedSdf < minDist) {
                    minDist = blendedSdf;
                    nearestColor = 0.5 + 0.5 * cos(vec3(0.0, 2.0, 4.0) + float(quadIdx) * 1.5);
                }
            } else {
                float tri1Sdf = sdTriangle(p, grid[tl], grid[tr], grid[bl]);
                vec4 circ1 = getIncircle(grid[tl], grid[tr], grid[bl]);
                float circle1Sdf = length(p - circ1.xy) - circ1.z;
                float blend1 = mix(circle1Sdf, tri1Sdf, sblend);

                if(blend1 < minDist) {
                    minDist = blend1;
                    nearestColor = 0.5 + 0.5 * cos(vec3(0.0, 2.0, 4.0) + float(quadIdx * 2));
                }

                float tri2Sdf = sdTriangle(p, grid[tr], grid[br], grid[bl]);
                vec4 circ2 = getIncircle(grid[tr], grid[br], grid[bl]);
                float circle2Sdf = length(p - circ2.xy) - circ2.z;
                float blend2 = mix(circle2Sdf, tri2Sdf, sblend);

                if(blend2 < minDist) {
                    minDist = blend2;
                    nearestColor = 0.5 + 0.5 * cos(vec3(1.0, 3.0, 5.0) + float(quadIdx * 2 + 1));
                }
            }
        }
    }

    return vec4(nearestColor, minDist);
}

// 3D SDF - extrude along a C-shaped curve
float map(vec3 p, out vec3 color) {
    // C-shape curve in XZ plane
    float angle = p.z * 0.8 + 1.57; // Maps Z to angle, starting at π/2
    float radius = 1.5;

    // C-shape centered at origin, opening toward +X (camera)
    vec2 curveCenter = vec2(-radius * 0.3, 0.0); // Shift center back
    vec2 targetPoint = curveCenter + vec2(cos(angle), sin(angle)) * radius;

    // Transform to local cross-section space
    vec2 localP = vec2(p.x - targetPoint.x, p.y);

    // Get the 2D cross-section SDF and color
    vec4 crossSection = getCrossSection(localP, p.z);
    color = crossSection.xyz;

    // Limit the C-shape to about 270 degrees
    float minZ = -2.0;
    float maxZ = 2.0;
    float cappedZ = max(max(minZ - p.z, p.z - maxZ), 0.0);

    return max(crossSection.w, cappedZ);
}

// Raymarching
vec3 raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    vec3 color = vec3(0.0);

    for(int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        vec3 marchColor;
        float d = map(p, marchColor);

        if(d < 0.001) {
            color = marchColor;
            break;
        }

        t += d * 0.68;
        if(t > 30.0) break;
    }

    return vec3(t, color.x, color.y);
}

// Calculate normal
vec3 calcNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    vec3 dummy;
    return normalize(vec3(
        map(p + e.xyy, dummy) - map(p - e.xyy, dummy),
        map(p + e.yxy, dummy) - map(p - e.yxy, dummy),
        map(p + e.yyx, dummy) - map(p - e.yyx, dummy)
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Fixed camera position
    vec3 ro = vec3(-0.5, 1.5, 5.0);
    vec3 lookAt = vec3(0.0, 0.0, 0.0);
    vec3 up = vec3(0.0, 1.0, 0.0);

    vec3 f = normalize(lookAt - ro);
    vec3 r = normalize(cross(f, up));
    vec3 u = cross(r, f);

    vec3 rd = normalize(f + uv.x * r + uv.y * u);

    // Raymarch
    vec3 result = raymarch(ro, rd);
    float t = result.x;

    vec3 color = vec3(0.05, 0.05, 0.1);

    if(t < 20.0) {
        vec3 p = ro + rd * t;
        vec3 n = calcNormal(p);
        vec3 objColor;
        map(p, objColor);

        // Lighting
        vec3 lightPos = vec3(3.0, 4.0, 2.0);
        vec3 l = normalize(lightPos - p);
        float diff = max(dot(n, l), 0.0);
        float spec = pow(max(dot(reflect(-l, n), -rd), 0.0), 32.0);

        color = objColor * (0.3 + 0.7 * diff) + vec3(0.5) * spec;

        // Fog
        color = mix(color, vec3(0.05, 0.05, 0.1), 1.0 - exp(-t * 0.1));
    }

    fragColor = vec4(color, 1.0);
}

vec2 inv(vec2 p,vec2 c,float r){vec2 d=p-c;return c+r*r*d/dot(d,d);}

vec3 hsl2rgb( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

vec4 frac(vec2 z){
    vec2 p=z;float s=5.,d=10.,n=7.+sin(iTime)*5.;
    int lastFold=0; // Track which operation happened last
    float totalRot=0.; // Track accumulated rotation
    float geom=0.; // Geometric signature
    for(int i=0;i<10;i++){
        float a=atan(p.y,p.x),pi=3.14159;
        a=mod(a+pi/n,2.*pi/n)-pi/n;
        totalRot+=abs(a);
        p=length(p)*vec2(cos(a),sin(a));
                geom+=length(p)*0.1; // Accumulate "path signature"

        p=abs(p);
        if(p.x+0.57735*p.y>1.0){
            float dist=(p.x+0.57735*p.y-1.0)/1.1547;
            p-=2.0*dist*vec2(0.8660,0.5);
            lastFold=1; // Domain fold
            geom+=1.; // Fold signature
        }

        vec2 c=vec2(.5,.289);float r=.4+sin(iTime/3.1)*.15,l=length(p-c);
        if(l<r){
            p=inv(p,c,r);
            s*=r/l;
            lastFold=2; // Circle inversion
            geom*=2.;
        }
        d=min(d,abs(l-r)/s);
    }
    return vec4(d,s,float(lastFold)+totalRot, geom);
}

