}

float sdTorusDisks(vec3 p, out float diskIndex) {
    float torusRadius = 1.5;
    float diskRadius = 0.3;
    float diskThickness = 0.003;
    float spacing = 0.001;

    float angle = atan(p.z, p.x);

    // Check if in wedge gap - just return moderate distance to keep marching
    if (p.z > 0.0 && abs(p.x) < p.z * 1.0) {
        return 1.;  // Keep marching through gap
    }

    // Snap to nearest disk angle
    float nearestDiskAngle = round(angle / spacing) * spacing;
    diskIndex = nearestDiskAngle / spacing;  // For interaction

    // Rotate point so the nearest disk is at angle 0
    float c = cos(-nearestDiskAngle);
    float s = sin(-nearestDiskAngle);
    vec3 pRot;
    pRot.x = c * p.x - s * p.z;
    pRot.y = p.y;
    pRot.z = s * p.x + c * p.z;

    // SDF for disk
    float distAlongRadius = pRot.x - torusRadius;
    float distInDisk = length(vec2(distAlongRadius, pRot.y)) - diskRadius;

    return max(distInDisk, abs(pRot.z) - diskThickness);
}

