// 3D Apollonian Gasket with individually colored, tangent cylinders
// Raymarched with SDF for Shadertoy

#define MAX_STEPS 256
#define MAX_DIST 100.0
#define SURF_DIST 0.0005

// Rotation matrix
mat2 rot2(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

// SDF for a cylinder along Z axis, returns (distance, cylinderID)
vec2 sdCylinderID(vec3 p, vec2 center, float radius, float id) {
    float d = length(p.xy - center) - radius;
    return vec2(d, id);
}

// Combine two distance/ID pairs, keeping the closest
vec2 opUnionID(vec2 d1, vec2 d2) {
    return (d1.x < d2.x) ? d1 : d2;
}

// Hash function for consistent colors per cylinder
vec3 hash3(float n) {
    return fract(sin(vec3(n, n + 1.0, n + 2.0)) * vec3(43758.5453, 22578.1459, 19642.3490));
}

// Create the Apollonian gasket with proper tangent positioning
vec2 apollonianCylinders(vec3 p) {
    float extrusionLength = 3.5;

    // Apply twist along z-axis for winding effect
    float twist = p.z * 0.18 + iTime * 0.12;
    p.xy *= rot2(twist);

    // Apply gentle wave for organic feel
    vec2 wave = vec2(
        sin(p.z * 0.8 + iTime * 0.4) * 0.06,
        cos(p.z * 1.2 - iTime * 0.3) * 0.04
    );
    p.xy += wave;

    // Start with impossibly far distance
    vec2 result = vec2(1000.0, 0.0);

    // Calculate proper Apollonian gasket positions
    // Using Descartes Circle Theorem for exact tangency

    // Three main circles with calculated radii for perfect fit
    float R = 1.0; // Outer boundary (implicit)
    float r1 = 0.333333; // 1/3
    float r2 = 0.333333; // 1/3  
    float r3 = 0.333333; // 1/3

    // Position the three main circles (120 degrees apart)
    vec2 c1 = vec2(0.0, 0.666667);     // Top
    vec2 c2 = vec2(0.57735, -0.333333); // Bottom right  
    vec2 c3 = vec2(-0.57735, -0.333333); // Bottom left

    result = opUnionID(result, sdCylinderID(p, c1, r1, 1.0));
    result = opUnionID(result, sdCylinderID(p, c2, r2, 2.0));
    result = opUnionID(result, sdCylinderID(p, c3, r3, 3.0));

    // Central circle (tangent to all three main circles)
    float r4 = 0.166667; // Calculated using Descartes theorem
    vec2 c4 = vec2(0.0, 0.0);
    result = opUnionID(result, sdCylinderID(p, c4, r4, 4.0));

    // Second generation circles (in the outer gaps)
    float r5 = 0.083333;

    // Between c1 and outer boundary
    vec2 c5a = vec2(0.25, 0.85);
    vec2 c5b = vec2(-0.25, 0.85);
    result = opUnionID(result, sdCylinderID(p, c5a, r5, 5.0));
    result = opUnionID(result, sdCylinderID(p, c5b, r5, 6.0));

    // Between c2 and outer boundary
    vec2 c6a = vec2(0.75, -0.15);
    vec2 c6b = vec2(0.65, -0.6);
    result = opUnionID(result, sdCylinderID(p, c6a, r5, 7.0));
    result = opUnionID(result, sdCylinderID(p, c6b, r5, 8.0));

    // Between c3 and outer boundary
    vec2 c7a = vec2(-0.75, -0.15);
    vec2 c7b = vec2(-0.65, -0.6);
    result = opUnionID(result, sdCylinderID(p, c7a, r5, 9.0));
    result = opUnionID(result, sdCylinderID(p, c7b, r5, 10.0));

    // Third generation - smaller circles
    float r8 = 0.05;

    // Around the perimeter
    for(int i = 0; i < 8; i++) {
        float angle = float(i) * 0.785398; // 45 degree increments
        vec2 pos = vec2(cos(angle), sin(angle)) * 0.9;
        result = opUnionID(result, sdCylinderID(p, pos, r8, 11.0 + float(i)));
    }

    // Fourth generation - tiny circles in remaining gaps
    float r9 = 0.035;

    // Between main circles
    vec2 gap1 = (c1 + c2) * 0.5 + vec2(0.1, 0.0);
    vec2 gap2 = (c2 + c3) * 0.5 + vec2(0.0, -0.1);
    vec2 gap3 = (c3 + c1) * 0.5 + vec2(-0.1, 0.0);

    result = opUnionID(result, sdCylinderID(p, gap1, r9, 19.0));
    result = opUnionID(result, sdCylinderID(p, gap2, r9, 20.0));
    result = opUnionID(result, sdCylinderID(p, gap3, r9, 21.0));

    // More tiny circles
    result = opUnionID(result, sdCylinderID(p, vec2(0.15, 0.25), r9, 22.0));
    result = opUnionID(result, sdCylinderID(p, vec2(-0.15, 0.25), r9, 23.0));
    result = opUnionID(result, sdCylinderID(p, vec2(0.3, -0.15), r9, 24.0));
    result = opUnionID(result, sdCylinderID(p, vec2(-0.3, -0.15), r9, 25.0));

    // Fifth generation - fill more gaps
    float r10 = 0.025;
    result = opUnionID(result, sdCylinderID(p, vec2(0.45, 0.3), r10, 26.0));
    result = opUnionID(result, sdCylinderID(p, vec2(-0.45, 0.3), r10, 27.0));
    result = opUnionID(result, sdCylinderID(p, vec2(0.0, 0.45), r10, 28.0));
    result = opUnionID(result, sdCylinderID(p, vec2(0.5, -0.4), r10, 29.0));
    result = opUnionID(result, sdCylinderID(p, vec2(-0.5, -0.4), r10, 30.0));
    result = opUnionID(result, sdCylinderID(p, vec2(0.0, -0.7), r10, 31.0));

    // Sixth generation - micro circles
    float r11 = 0.018;
    for(int i = 0; i < 6; i++) {
        float angle = float(i) * 1.047197; // 60 degrees
        vec2 pos = vec2(cos(angle), sin(angle)) * 0.82 + vec2(cos(angle * 2.0), sin(angle * 2.0)) * 0.08;
        result = opUnionID(result, sdCylinderID(p, pos, r11, 32.0 + float(i)));
    }

    // Apply extrusion limits
    result.x = max(result.x, abs(p.z) - extrusionLength);

    return result;
}

// Scene SDF
vec2 getDist(vec3 p) {
    // Rotate for viewing
    p.xz *= rot2(iTime * 0.06);
    p.yz *= rot2(sin(iTime * 0.08) * 0.15);

    return apollonianCylinders(p);
}

// Raymarching - returns distance and material ID
vec2 rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    float matID = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        vec2 res = getDist(p);
        float dS = res.x;
        matID = res.y;
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return vec2(dO, matID);
}

// Get normal
vec3 getNormal(vec3 p) {
    vec2 res = getDist(p);
    float d = res.x;
    vec2 e = vec2(0.001, 0);

    vec3 n = d - vec3(
        getDist(p - e.xyy).x,
        getDist(p - e.yxy).x,
        getDist(p - e.yyx).x
    );

    return normalize(n);
}

// Get color based on cylinder ID
vec3 getCylinderColor(float id) {
    if(id < 0.5) return vec3(0.1, 0.1, 0.1); // Background

    // Use hash function for varied but consistent colors
    vec3 baseColors[10];
    baseColors[0] = vec3(0.8, 0.2, 0.3); // Red
    baseColors[1] = vec3(0.2, 0.7, 0.3); // Green  
    baseColors[2] = vec3(0.3, 0.4, 0.9); // Blue
    baseColors[3] = vec3(0.9, 0.7, 0.2); // Orange
    baseColors[4] = vec3(0.8, 0.3, 0.8); // Magenta
    baseColors[5] = vec3(0.2, 0.8, 0.8); // Cyan
    baseColors[6] = vec3(0.9, 0.9, 0.3); // Yellow
    baseColors[7] = vec3(0.6, 0.3, 0.1); // Brown
    baseColors[8] = vec3(0.4, 0.1, 0.7); // Purple
    baseColors[9] = vec3(0.1, 0.6, 0.2); // Dark Green

    // For cylinders beyond the predefined colors, use hash
    if(id < 10.5) {
        return baseColors[int(id - 1.0)];
    }

    // Hash-based colors for remaining cylinders
    return hash3(id * 7.234) * 0.8 + 0.2;
}

// Lighting
float getLight(vec3 p, vec3 lightPos) {
    vec3 l = normalize(lightPos - p);
    vec3 n = getNormal(p);

    float dif = clamp(dot(n, l), 0.0, 1.0);

    // Soft shadow
    vec2 res = rayMarch(p + n * SURF_DIST * 2.0, l);
    float d = res.x;
    if(d < length(lightPos - p)) dif *= 0.4;

    return dif;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera
    vec3 ro = vec3(0, 2.5, 6.5);
    vec3 lookAt = vec3(0, 0, 0);

    // Camera orbit
    float camAngle = iTime * 0.15;
    ro.xz = vec2(sin(camAngle), cos(camAngle)) * 6.5;
    ro.y = 3.0 + sin(iTime * 0.25) * 1.2;

    // Camera matrix
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0, 1, 0), forward));
    vec3 up = cross(forward, right);

    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    // Raymarch
    vec2 result = rayMarch(ro, rd);
    float d = result.x;
    float matID = result.y;

    vec3 col = vec3(0.01, 0.02, 0.03); // Dark background

    if(d < MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = getNormal(p);

        // Get cylinder-specific color
        vec3 baseColor = getCylinderColor(matID);

        // Lighting setup
        vec3 lightPos1 = vec3(6, 8, 5);
        vec3 lightPos2 = vec3(-4, 6, -4);
        vec3 lightPos3 = vec3(2, -4, 3);

        float dif1 = getLight(p, lightPos1);
        float dif2 = getLight(p, lightPos2) * 0.6;
        float dif3 = getLight(p, lightPos3) * 0.4;

        // Add some variation within each cylinder
        float variation = sin(p.z * 8.0 + matID * 3.14159) * 0.1 + 1.0;
        baseColor *= variation;

        // Metallic/plastic look
        float fresnel = pow(1.0 - abs(dot(rd, n)), 2.0);
        vec3 specColor = mix(vec3(1.0), baseColor, 0.5);

        // Combine lighting
        col = baseColor * (dif1 + dif2 + dif3) * 0.8;
        col += specColor * fresnel * 0.6;
        col += baseColor * 0.1; // Ambient

        // Subtle rim lighting
        col += vec3(0.4, 0.5, 0.7) * pow(fresnel, 4.0) * 0.3;

        // Distance fog
        col = mix(col,vec3(0.01, 0.02, 0.03), smoothstep(8.0, 20.0, d));
    }

    // Tone mapping and gamma correction
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

vec3 fireGradient(float t) {
    // Black -> Red -> Orange -> Yellow -> White
    return vec3(
        smoothstep(0.0, 0.35, t),
        smoothstep(0.25, 0.7, t) * smoothstep(0.0, 0.2, t),
        smoothstep(0.6, 1.0, t)
    ) * smoothstep(0.0, 0.1, t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    float fire = texture(iChannel0, uv).r;

    // Apply fire colors
    vec3 col = fireGradient(fire) * (0.8 + fire * 1.5);

    // Optional: add glow
    col += fireGradient(fire * 0.5) * 0.3;

    fragColor =  atan(texture(iChannel1, uv) + vec4(col, 1.0)/2.);
}

