    //vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;
    float rotX = (mouse.x - 0.5) * 6.28318;
    float rotY = (mouse.y - 0.5) * 3.14159;

    if (iMouse.z <= 0.0) {
        rotX = iTime * 0.2;
        rotY = 0.3;
    }

    vec3 ro = vec3(0.0, 0.0, CAM_DIST);
    vec3 rd = normalize(vec3(uv, -FOV));
    // XZ rotation
    float angle1 = iTime * 2.0* (-0.5+hash11(cid.x+cid.y + 2.0));
    float c1 = cos(angle1), s1 = sin(angle1);
    ro.xz = mat2(c1, s1, -s1, c1) * ro.xz;
    rd.xz = mat2(c1, s1, -s1, c1) * rd.xz;

    // YZ rotation (second axis)
    float angle2 = iTime * 2.0*(-0.5+hash11(cid.x+cid.y + 4.0));
    float c2 = cos(angle2), s2 = sin(angle2);
    ro.yz = mat2(c2, s2, -s2, c2) * ro.yz;
    rd.yz = mat2(c2, s2, -s2, c2) * rd.yz;
    //rd.yz *= rot2D(-rotY);
    //rd.xz *= rot2D(rotX);
    //ro.yz *= rot2D(-rotY);
    //ro.xz *= rot2D(rotX);

    vec3 col = vec3(BG);
    if(VOLUMETRIC==0){
        float t = rayMarch(ro, rd);
        vec3 p = ro + rd * t;
        if (t < MAX_DIST) { col = shade(p, rd, t); }
    } else {
        col += raymarch_volumetric(ro, rd).rgb*10.;
    }
    //col = col / (col + vec3(1.0));
   col = pow(col, vec3(0.4));
    col = contrast(col, 1.4);
    return col;
}

void compute_things(in vec2 p, out vec2 h, out vec2 ddx, out vec2 ddy) {
    const float TAU = 6.28318530718;
    const mat2 M = mat2(8.0, 5.0, -5.0, 8.0);

    vec2 r = iResolution.xy;
    vec2 s = sqrt(r / r.yx);
    vec2 a = 2.0 / r * s;
    vec2 u = a * p - s;

    float S = dot(u, u) + 1.0;
    float invS = 1.0 / S;
    float invS2 = invS * invS;

    vec3 g = vec3(1.0, u.yx) * invS + vec3(-0.5, 0.0, 0.5);

    float T = dot(g, g);
    float invT = 1.0 / T;
    float invT2 = invT * invT;

    vec3 dg_dux = vec3(-2.0 * u.x * invS2, -2.0 * u.x * u.y * invS2, invS - 2.0 * u.x * u.x * invS2);
    vec3 dg_duy = vec3(-2.0 * u.y * invS2, invS - 2.0 * u.y * u.y * invS2, -2.0 * u.x * u.y * invS2);

    vec3 dg_dx = dg_dux * a.x;
    vec3 dg_dy = dg_duy * a.y;

    vec2 q = g.xy * invT;

    float dTdx = 2.0 * dot(g, dg_dx);
    float dTdy = 2.0 * dot(g, dg_dy);

    vec2 dq_dx = dg_dx.xy * invT - g.xy * dTdx * invT2;
    vec2 dq_dy = dg_dy.xy * invT - g.xy * dTdy * invT2;

    float denom = dot(q, q);
    float scale = 1.0 / (TAU * denom);

    ddx = vec2(dq_dx.x * q.y - dq_dx.y * q.x, dot(dq_dx, q)) * scale;
    ddy = vec2(dq_dy.x * q.y - dq_dy.y * q.x, dot(dq_dy, q)) * scale;

    h = vec2(atan(q.x, q.y), log(length(q))) / TAU;
    h += iTime * 0.07;

    h *= M;
    ddx *= M;
    ddy *= M;
}

mat2 rotate(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage0(out vec4 c, vec2 p) {

    vec2 h, ddx, ddy;
    compute_things(p, h, ddx, ddy);

    float a = atan(ddx.x, ddy.x);
    cellID = floor(h);
    cellID = mod(cellID, vec2(8, 5));
    vec2 cellUV = fract(h);

    vec2 uv = cellUV.yx * 2.0 - 1.0;
    //uv = mix(  uv*rotate(3.2)  ,    uv*rotate(-a), guv.y*guv.y);
    uv *= rotate(-a);
    //uv *= rotate(2.6);
    vec3 col = mainImage1(uv, cellID);
    c = vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {

    vec2 j = vec2(.5);
    fragColor = vec4(0);
    vec4 c;
    mainImage0(c, fragCoord);
    float s = float(AA);
    float k;
    for (k = s; k-- > .5;) {
        mainImage0(c, fragCoord + j - .5);
        fragColor += c;
        j = fract(j + vec2(.755, .57).yx);
    }
    fragColor /= s;
    fragColor.a = 1.;

    vec3 color = fragColor.rgb;


    fragColor = vec4(color, 1.0);
}

float hash11(float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 px = 1.0 / iResolution.xy;

    // Wobble noise (coherent)
    float wobble = sin(uv.y * 90.0 + iTime * (15.0+ hash11(uv.x))) * hash11(uv.y)
                 + sin(uv.y * 120.0 - iTime * (15.0 + hash11(uv.y))+ uv.x * 10.0) * hash11(uv.x);

    // Sample from below with horizontal wobble (fire rises)
    vec2 offset = vec2(wobble*3.,-2.+wobble*-3.) * px *0.3;

    // Blur/diffuse while sampling previous frame
    float fire = 0.0;
    fire += texture(iChannel0, uv + offset).r * 0.5;
    fire += texture(iChannel0, uv + offset + vec2(px.x, 0.0)).r * 0.15;
    fire += texture(iChannel0, uv + offset - vec2(px.x, 0.0)).r * 0.15;
    fire += texture(iChannel0, uv + offset + vec2(px.x*2.0, px.y)).r * 0.1;
    fire += texture(iChannel0, uv + offset - vec2(px.x*2.0, -px.y)).r * 0.1;

    // Cooling/decay
    fire *= 0.985;

    // Add fresh fuel from source
    float fuel = texture(iChannel1, uv).r;
    fire = max(fire, fuel);

    fragColor = vec4(clamp(fire, 0.0, 1.0));
}

// I wanted to make a shadertoy version of this very old webaudio experiment: https://www.johnc.pro/spectrogram/
// (You can draw and play the sound of your drawing using additive synthesis, turning the spectrograph back into sound.)
// But this is not possible on shadertoy because the buffers aren't connected, so sound isnt interactive.

// However, next best thing is a way to play any predefined drawing.
// Replace drawFunction in common and both the image and sound tabs will display and play it!
// Try the three modes to see examples
// You can create sound using SDFs or anything that returns meaningful values in the 0-1 range.
// You can even use the passed time data to change the output over time.
// Frequency range is logarithminc so musical notes are the same distance apart.
// Sound fidelity has it's limits, this is just a toy really.
// I'm sure it can be improved a lot, my DSP knowledge is rudimentary. Open to ideas.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = drawFunction(uv, iTime);
    float scanX = mod(iTime, DURATION) / DURATION;
    float scanLine = step(0.995, 1.-abs(uv.x - scanX));
    col = mix(col, vec3(1.0, 0.0, 0.0), scanLine);  
    fragColor = vec4(col, 1.0);
}

vec2 mainSound( int samp, float time )
{
    vec3 t = vec3(float(samp-1) / SAMPLE_RATE , float(samp) / SAMPLE_RATE , float(samp+1) / SAMPLE_RATE );
    vec3 x = mod(t, DURATION) / DURATION;

    float sampl = 0.0;

    // Sample vertically across frequencies
    for (int row = 0; row < NUM_SAMPLES; row++) {
        float y = float(row) / float(NUM_SAMPLES);

        // Sample at exact x position (no need to interpolate between columns)
        vec3 pixel = vec3(drawFunction(vec2(x.x, y), t.x).r ,  drawFunction(vec2(x.y, y), t.y).r , drawFunction(vec2(x.z, y), t.z).r ) ; 
        pixel = clamp(pixel, 0., 1.);

        vec3 brightness = 1.0 - pixel;

        float freq = frequencyFromY(y);

        vec3 triple = brightness * sin(TWO_PI * freq * fract(t));

        if(triple.x == 0. && abs(triple.y)>0.4) { triple.y /=4.; }

        if(triple.z == 0. && abs(triple.y)>0.4) { triple.y /=4.; }

        sampl += triple.y;

    }

    // Soft clip using tanh - compresses loudness gracefully
    sampl= tanh(sampl * 0.5);

    return vec2(sampl);
}

