float frequencyFromY(float y) {return floor(MIN_FREQ * pow(2.0, y * log(MAX_FREQ / MIN_FREQ) / log(2.0)));}
float freqToY(float freq) {return log(freq / MIN_FREQ) / log(MAX_FREQ / MIN_FREQ);}

