float noteToFreq(int noteIndex) {
    // F# F# D B E  G# G# A B A A D F#  F#  E E F# E
    if (noteIndex == 0) return 369.99;  // F#4
    if (noteIndex == 1) return 369.99;  // F#4
    if (noteIndex == 2) return 293.66;  // D4
    if (noteIndex == 3) return 246.94;  // B3 (low)
    if (noteIndex == 4) return 329.63;  // E4
    if (noteIndex == 5) return 415.30;  // G#4
    if (noteIndex == 6) return 415.30;  // G#4
    if (noteIndex == 7) return 440.00;  // A4
    if (noteIndex == 8) return 493.88;  // B4 (high)
    if (noteIndex == 9) return 440.00;  // A4
    if (noteIndex == 10) return 440.00; // A4
    if (noteIndex == 11) return 293.66; // D4
    if (noteIndex == 12) return 369.99; // F#4
    if (noteIndex == 13) return 369.99; // F#4
    if (noteIndex == 14) return 329.63; // E4
    if (noteIndex == 15) return 329.63; // E4
    if (noteIndex == 16) return 369.99; // F#4
    return 329.63; // E4
}

vec3 testFunction(vec2 uv) {  //take on me.
    vec3 col = vec3(1.0);
    int numNotes = 18;
    float noteDuration = 1.0 / float(numNotes);
    int noteIndex = int(uv.x / noteDuration) % numNotes;
    float freq = noteToFreq(noteIndex);
    float y = freqToY(freq);
    float notePos = fract(uv.x / noteDuration);
    float noteEnd = 0.3;
    float fadeIn = smoothstep(0.0, 0.7, notePos);
    float fadeOut = smoothstep(noteEnd, noteEnd + 0.7, notePos);
    float envelope = fadeIn * (1.0 - fadeOut);
    if (abs(uv.y - y) < 0.004) {
        col = mix(vec3(1.0), vec3(0.0), envelope);
    }
    return col;
}

vec3 drawFunction(vec2 uv, float t) { 
    vec3 col = testFunction(uv);
    return col;
}

