     float nv = distance(uvc, pa);

    float ancl = ano+(anga-ang)-nv;
    float ancr = ano-(ang*anga)+nv;

    float len = length(rv);
    float tlen = 1. + length(pd);
    vec2 rvnl = vec2(cos(ancl)*len, sin(ancl)*len);
    vec2 tcl = vec2(0.5, 0.05)+rvnl*tlen;
    vec2 rvnr = vec2(cos(ancr)*len, sin(ancr)*len);
    vec2 tcr = vec2(0.5, 0.05)+rvnr*tlen;
    col = max(col, texture(iChannel1, tcl, 0.).rgb*fade);
   col = max(col, texture(iChannel0, tcr, 0.).rgb*fade);
  col = max(col, texture(iChannel0, tcl, 0.).rgb*fade);
   col = max(col, texture(iChannel1, tcr, 0.).rgb*fade);
    fragColor = vec4(col,1.0);
    if(iFrame<2||uv.x>.99||uv.x<0.01||uv.y>.99||uv.y<0.01){fragColor = vec4(0);}
}

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}
float fade  = 0.97;
vec3 contrast(vec3 color, float value) {
  return 0.5 + value * (color - 0.5);
}
vec3 gmc(vec3 colour, float gamma) {
  return pow(colour, vec3(1. / gamma));
}
float capsuleSDF(in vec2 P,in vec2 B,in vec2 E,in float R)
{
    vec2 BP = P - B;
    vec2 BE = E - B;
    float t = clamp(dot(BP, BE) / dot(BE, BE), 0.0, 1.0);
    vec2 K = BP - BE * t;
    return sqrt(dot(K, K)) - R;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvc = fragCoord/iResolution.y;
    float ar = iResolution.x/iResolution.y;
    vec2 pa = vec2(0.5*ar, 0.05);
    vec2 pb = vec2(ar/2.+(sin(iTime*1.15)/5.), .18+cos(iTime*2.3)/-21.);
    pb.y += .03 + pow(abs(pb.x-(ar*.5)),2.);
    if(iMouse.z>0.001) {pb = vec2(iMouse.x/iResolution.x*ar, iMouse.y/iResolution.y);}
    vec2 pd = pb-pa;
    float csdf = capsuleSDF(uvc, pa, pb, .001);
    vec3 col = vec3(mix(1.,0., csdf*200.));

    vec2 rv = uvc-pb;
    float ano = atan(rv.y, rv.x);
    float and = atan(pd.y,pd.x);
    float anga = abs(uv.x-.5)*3.;
    float ang = pow(abs(uv.x-.5),.4);
   float nv = distance(uvc, pa);

    float ancl = ano+(anga)+uvc.y;
    float ancr = ano+(ang)*uvc.y;

    float len = length(rv);
    float tlen = 1. + length(pd);
    vec2 rvnl = vec2(cos(ancl)*len, sin(ancl)*len);
    vec2 tcl = vec2(0.5, 0.05)+rvnl*tlen;
    vec2 rvnr = vec2(cos(ancr)*len, sin(ancr)*len);
    vec2 tcr = vec2(0.5, 0.05)+rvnr*tlen;
  col = max(col, texture(iChannel1, tcl, 0.).rgb*fade);
    col = max(col, texture(iChannel0, tcr, 0.).rgb*fade);
    col = max(col, texture(iChannel0, tcl, 0.).rgb*fade);
    col = max(col, texture(iChannel1, tcr, 0.).rgb*fade);
    fragColor = vec4(col,1.0);
    if(iFrame<2||uv.x>.99||uv.x<0.01||uv.y>.99||uv.y<0.01){fragColor = vec4(0);}
}

// Forked from: "Binary Black Holes + Cubemap" by Cotterzz https://sleditor.com/#id=ajg43c1hu

//         And: "Binary Black Holes" by Nxrix: "https://www.shadertoy.com/view/Wc3BRl"

//         And: "Nebula in Space" by LizardLover42 https://www.shadertoy.com/view/33cSWX

// The sleditor version uses a space cubemap as a skybox.
// I had to create one using an adapted version of the Nebula in Space shader with Cube A buffer
// Doesnt look as good as I had to change the noise to 3D and rework the stars, but I'm sure it can be improved.


const float  PI = 3.14159265;
const float TAU = 6.28318530;

//#define AA 2

const float Rs1 = 2.0;
const float Rs2 = 1.0;

// Lensing strength multiplier (1.0 = physically accurate, <1.0 = reduced lensing)
const float LENSING_STRENGTH = 1.0;

// Roche potential parameters
// The disk exists where potential is between these values
const float POTENTIAL_INNER = -0.3;  // Inner edge (closer to black holes, more negative)
const float POTENTIAL_OUTER = -0.09;  // Outer edge (farther out, less negative)
const float DISK_THICKNESS = 0.5;

const vec3 BH1_POS = vec3(-27.0, 0.0, 0.0);
const vec3 BH2_POS = vec3( 32.0, 0.0, 0.0);

// Disk lies in XZ plane (Y = 0)
const vec3 DISK_AXIS = vec3(0.0, 1.0, 0.0);

const int MAX_STEPS = 100;

const float INV_PI = 1.0 / PI;
const float INV_SIX_RS1 = 1.0 / (6.0 * Rs1);
const float INV_SIX_RS2 = 1.0 / (6.0 * Rs2);

float ls(float x) {
    return 1.0 - abs(mod(x * INV_PI, 2.0) - 1.0) * 2.0;
}

float noise(vec2 p) {
    return texture(iChannel1, p / 128.0).r;
}

// Calculate Roche-like gravitational potential
// More negative = deeper in gravity well
float rochePotential(vec3 p) {
    float r1 = length(p - BH1_POS);
    float r2 = length(p - BH2_POS);

    // Gravitational potential from both black holes
    // Using Schwarzschild radius as mass proxy
    float phi = -Rs1 / max(r1, 0.1) - Rs2 / max(r2, 0.1);

    // Optional: Add centrifugal term for rotating frame
    vec3 centerOfMass = (BH1_POS * Rs1 + BH2_POS * Rs2) / (Rs1 + Rs2);
    float rCM = length(p.xz - centerOfMass.xz);
    // phi -= 0.001 * rCM * rCM;  // Centrifugal term

    return phi;
}

vec3 diskColor(float temp, float angle, float dop, float blend) {
    float t_noise = temp * 0.6 + 0.5 * noise(vec2((temp - iTime * 0.1) * 40.0, ls(angle - iTime) * 6.0));
    float d_factor = -clamp(dop, -1.0, 1.0) * 0.15 + 1.1;
    float t_adjusted = t_noise * 1.5 / (t_noise * d_factor + 0.5);
    float t_factor = t_adjusted * 0.5 + 0.5;
    float t_half = t_adjusted * 0.5 + 0.4;
    float t_pow5 = exp(5.0 * log(max(t_adjusted, 0.001)));
    float t_pow20 = exp(20.0 * log(max(t_adjusted, 0.001)));

    // Base warm color (orange/yellow)
    vec3 warm = vec3(t_half, t_pow5 * 0.6, t_pow20 * 0.3) * t_factor;
    warm = mix(warm, vec3(temp, 0.0, 0.0), clamp(1.0 - pow(temp, 0.3), 0.0, 1.0));

    // Cool color (blue/cyan) - swap R and B channels
    vec3 cool = warm.zyx;

    return mix(warm, cool, blend) * 1.2;
}

vec3 bend(vec3 ro, vec3 rd, vec3 p, float rs) {
    vec3 r = ro - p;
    float r2 = dot(r, r);
    float r1 = inversesqrt(r2);
    vec3 L = cross(r, rd);
    return -1.5 * rs * LENSING_STRENGTH * (r * dot(L, L)) * (r1 / (r2 * r2));
}

void advance(inout vec3 o, inout vec3 d, float h) {
    vec3 acc = bend(o, d, BH1_POS, Rs1) + bend(o, d, BH2_POS, Rs2);
    d = normalize(d + acc * h);
    o += d * h;
}

// Disk opacity (0 = fully transparent, 1 = fully opaque)
const float DISK_OPACITY = 0.85;

// Check if ray crosses the Roche potential disk
// Returns opacity in col.a (using vec4)
bool hit_roche_disk(vec3 oro, vec3 ro, vec3 rd, inout vec4 colAlpha) {
    // Check if we crossed the disk plane (Y = 0)
    float s1 = oro.y;
    float s2 = ro.y;
    if (s1 * s2 >= 0.0) return false;

    // Find intersection point
    float t = s1 / (s1 - s2);
    if (t < 0.0 || t > 1.0) return false;

    vec3 p = mix(oro, ro, t);

    // Distance to each black hole
    float d1 = length(p - BH1_POS);
    float d2 = length(p - BH2_POS);

    // Minimum distance from event horizons
    float minDist1 = Rs1 * 3.0;
    float minDist2 = Rs2 * 3.0;
    if (d1 < minDist1 || d2 < minDist2) return false;

    // Calculate Roche potential at this point
    float potential = rochePotential(p);

    // Check if within disk bounds
    if (potential < POTENTIAL_INNER || potential > POTENTIAL_OUTER) return false;

    // Temperature based on potential (hotter = deeper in well)
    float temp = (potential - POTENTIAL_OUTER) / (POTENTIAL_INNER - POTENTIAL_OUTER);
    temp = pow(temp, 0.6);

    // Boost temperature near the L1 point (saddle point of potential)
    vec3 L1 = mix(BH1_POS, BH2_POS, Rs1 / (Rs1 + Rs2));
    float distToL1 = length(p.xz - L1.xz);
    float pinchBoost = exp(-distToL1 * 0.08) * 0.4;
    temp = min(temp + pinchBoost, 1.0);

    // Color blend: 0 = near BH1 (warm), 1 = near BH2 (cool)
    float blend = d1 / (d1 + d2);

    // Angle for texture - use center of mass as reference
    vec3 centerOfMass = (BH1_POS * Rs1 + BH2_POS * Rs2) / (Rs1 + Rs2);
    vec2 relCM = p.xz - centerOfMass.xz;
    float globalAngle = atan(relCM.y, relCM.x);

    // Add swirl based on potential gradient for organic flow
    float potentialPhase = potential * 20.0;
    float angle = globalAngle + potentialPhase;

    // Doppler shift - gas orbits around nearer black hole
    vec3 toBH1 = normalize(vec3(p.x - BH1_POS.x, 0.0, p.z - BH1_POS.z));
    vec3 toBH2 = normalize(vec3(p.x - BH2_POS.x, 0.0, p.z - BH2_POS.z));
    vec3 toNearest = normalize(mix(toBH1, toBH2, blend));
    vec3 tangent = normalize(vec3(-toNearest.z, 0.0, toNearest.x));

    // Add flow toward L1 point in the connecting stream
    vec3 toL1 = normalize(vec3(L1.x - p.x, 0.0, L1.z - p.z));
    float streamFactor = exp(-distToL1 * 0.05);
    tangent = normalize(mix(tangent, toL1, streamFactor * 0.5));

    float dop = dot(tangent, rd);
    vec3 diskCol = diskColor(temp, angle, dop, blend);

    // Opacity based on temperature (hotter = more opaque) and base opacity
    float opacity = DISK_OPACITY * (0.5 + 0.5 * temp);

    colAlpha = vec4(diskCol, opacity);
    return true;
}

// Escape distance - rays further than this from both black holes sample the skybox
const float ESCAPE_DIST = 200.0;

// Camera distance from scene center (adjust to frame the black holes nicely)
const float CAM_DIST = 75.0;

vec3 trace(vec3 ro, vec3 rd) {
    // Accumulated disk color and opacity
    vec3 accumulatedColor = vec3(0.0);
    float accumulatedAlpha = 0.0;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 to_bh1 = ro - BH1_POS;
        vec3 to_bh2 = ro - BH2_POS;
        float r1 = length(to_bh1);
        float r2 = length(to_bh2);

        // Ray fell into a black hole - return accumulated disk over black
        if (r1 < Rs1 || r2 < Rs2) {
            return accumulatedColor;  // Black background, disk on top
        }

        // Ray escaped far enough - blend disk over lensed skybox
        if (r1 > ESCAPE_DIST && r2 > ESCAPE_DIST) {
            vec3 skybox = texture(iChannel0, rd).rgb;
            // Composite: disk over background
            return accumulatedColor + (1.0 - accumulatedAlpha) * skybox;
        }

        vec3 oro = ro;
        float h = clamp(min(r1 * INV_SIX_RS1, r2 * INV_SIX_RS2), 1.0, 16.0);
        advance(ro, rd, h);

        vec4 diskHit = vec4(0.0);
        if (hit_roche_disk(oro, ro, rd, diskHit)) {
            // Front-to-back alpha compositing
            float alpha = diskHit.a * (1.0 - accumulatedAlpha);
            accumulatedColor += diskHit.rgb * alpha;
            accumulatedAlpha += alpha;

            // Early exit if nearly opaque
            if (accumulatedAlpha > 0.99) {
                return accumulatedColor;
            }
        }
    }

    // Max steps reached - blend disk over skybox
    vec3 skybox = texture(iChannel0, rd).rgb;
    return accumulatedColor + (1.0 - accumulatedAlpha) * skybox;
}

vec3 render(vec2 fragCoord) {

    float rotX = (iMouse.x / iResolution.x) * 2.0 * PI;
    float rotY = (iMouse.y / iResolution.y) * PI;
    if (iMouse.z <= 0.0) {
        rotX = iTime * 0.2;
        rotY = PI * 0.5;
    }
    vec2 uv = 2.5 * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.xx;
    vec3 camO = vec3(cos(rotX), cos(rotY)+0.3, sin(rotX));
    vec3 camD = normalize(vec3(0.0) - camO);
    vec3 camR = normalize(cross(camD, vec3(0.0, 1.0, 0.0)));
    vec3 camU = cross(camR, camD);
    vec3 rd = normalize(uv.x * camR + uv.y * camU + camD);
    vec3 sceneCenter = vec3(2.5, 0.0, 0.0);
    vec3 ro = sceneCenter + camO * CAM_DIST;

    return tanh(trace(ro, rd)*1.5);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 col = vec3(0);
    #ifdef AA
    for (int x = 0; x < AA; x++) {
        for (int y = 0; y < AA; y++) {
            vec2 jitter = vec2(float(x), float(y)) / float(AA) - 0.5;
            col += render(fragCoord + jitter);
        }
    }
    col /= float(AA * AA);
    #else
    col = render(fragCoord);
    #endif
    fragColor = vec4(col, 1);
}

// Space Background for Shadertoy Cubemap A Buffer
// Put this in "Cubemap A" buffer, then sample it in your main shader with:
//   texture(iChannel0, rayDirection)
//
// This only renders on frame 0, then is cached by Shadertoy.
// Based on space.glsl, uses 3D noise for seamless sphere mapping.

// ============================================
// 3D NOISE - seamless on sphere, no seams!
// ============================================

float hash3(vec3 p) {
    p = fract(p * vec3(443.897, 441.423, 437.195));
    p += dot(p, p.yzx + 19.19);
    return fract((p.x + p.y) * p.z);
}

// 3D value noise - naturally seamless when sampling by direction
float noise3(vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);  // Smoothstep

    return mix(
        mix(mix(hash3(i + vec3(0,0,0)), hash3(i + vec3(1,0,0)), f.x),
            mix(hash3(i + vec3(0,1,0)), hash3(i + vec3(1,1,0)), f.x), f.y),
        mix(mix(hash3(i + vec3(0,0,1)), hash3(i + vec3(1,0,1)), f.x),
            mix(hash3(i + vec3(0,1,1)), hash3(i + vec3(1,1,1)), f.x), f.y),
        f.z
    );
}

// 3D FBM - completely seamless
float fbm3(vec3 p) {
    float value = 0.0;
    float amplitude = 0.5;
    for (int i = 0; i < 5; i++) {  // 5 octaves (was 6, slightly faster)
        value += amplitude * noise3(p);
        p *= 2.0;
        amplitude *= 0.5;
    }
    return value;
}

// ============================================
// STARS - triplanar projection, fixed blending
// ============================================

// 2D hash for star grid
float hash2(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float metaDiamond(vec2 p, vec2 pixel, float r) {
    vec2 d = abs(p - pixel);
    return r / (d.x + d.y + 0.001);
}

// Generate stars on one projection plane
vec3 starsOnPlane(vec2 uv, vec2 cellOffset) {
    vec3 col = vec3(0.0);
    vec2 grid = floor(uv);

    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            vec2 cell = grid + vec2(x, y) + cellOffset;
            vec2 starPos = cell + vec2(hash2(cell), hash2(cell.yx)) - 0.5;
            float starSize = hash2(cell * 1.5) * 0.008;
            float brightness = metaDiamond(uv, starPos, starSize);
            vec3 tint = vec3(1.0, 0.95, 0.9) + 0.1 * vec3(
                hash2(cell * 2.0) - 0.5,
                hash2(cell * 3.0) - 0.5,
                hash2(cell * 4.0) - 0.5
            );
            col += tint * brightness;
        }
    }
    return col;
}

vec3 generateStars(vec3 dir) {
    dir = normalize(dir);
    vec3 starColor = vec3(0.0);

    // Blend weights - use squared instead of pow 4 for gentler falloff
    vec3 blend = abs(dir);
    blend =sqrt( blend );  // Gentler than pow 4
    blend /= (blend.x + blend.y + blend.z + 0.01);


    {
        vec2 uv = dir.yz * sign(dir.x) * 16.0;
        starColor += starsOnPlane(uv, vec2(0.0, 0.0)) * blend.x;
    }

    {
        vec2 uv = dir.xz * sign(dir.y) * 16.0;
        starColor += starsOnPlane(uv, vec2(100.0, 0.0)) * blend.y;
    }

    {
        vec2 uv = dir.xy * sign(dir.z) * 16.0;
        starColor += starsOnPlane(uv, vec2(0.0, 100.0)) * blend.z;
    }

    return starColor*1.4;
}



vec3 generateNebula(vec3 dir) {
    dir = normalize(dir);

    vec3 p = dir * 2.0;

    float q = fbm3(p * 3.5);
    float r = fbm3(p + q + vec3(1.7, 9.2, 3.1));
    float f = fbm3(p + r * 1.5);

    f = f*f*f;

    vec3 nebulaColor = mix(vec3(0.05, 0.0, 0.1), vec3(0.5, 0.1, 0.4), f * 2.0);
    nebulaColor = mix(nebulaColor, vec3(0.7, 0.2, 0.5), q * 0.8);
    nebulaColor = mix(nebulaColor, vec3(0.9, 0.5, 0.2), r * 0.4);
    nebulaColor = mix(nebulaColor, vec3(0.3, 0.1, 0.4), smoothstep(0.3, 0.7, f));

    nebulaColor = nebulaColor * f * f + nebulaColor * f * 0.1;

    nebulaColor += vec3(0.02, 0.0, 0.03);

    return nebulaColor;
}

vec3 generateGalacticBand(vec3 dir) {
    dir = normalize(dir);

    float bandDist = abs(dir.y);
    float band = smoothstep(0.4, 0.0, bandDist);

    float variation = fbm3(dir * 3.0) * 0.5 + 0.5;
    band *= variation;

    vec3 bandColor = mix(vec3(0.4, 0.1, 0.3), vec3(0.6, 0.2, 0.4), variation);

    return bandColor * band * 0.4;
}

void mainCubemap(out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir) {
    // BUFFERING: Only render on first frame, Shadertoy caches the result!
    // After frame 0, this buffer is static and free to sample.
    if (iFrame > 0) {
        fragColor = textureLod(iChannel0, rayDir, 0.0);  // Return cached
        return;
    }

    // rayDir is provided by Shadertoy for cubemap buffers
    vec3 dir = rayDir;

    // Generate space background for this direction
    vec3 stars = generateStars(dir);
    vec3 nebula = generateNebula(dir);
    vec3 galactic = generateGalacticBand(dir);

    vec3 finalColor = nebula + galactic + stars;

    // Slight color grade - boost the pink/purple
    finalColor *= vec3(1.0, 0.92, 1.15);

    fragColor = vec4(finalColor, 1.0);
}

// Fork of "One-Pass Fire" by Xor. https://shadertoy.com/view/tf2SWc
// 2025-03-22 17:16:30

/*
    "One-Pass Fire" by @XorDev

    Here's the continuation of my experiments with fire effects

    Also see Turbulent Flame:
    https://www.shadertoy.com/view/wffXDr
*/
//Fire ring radius
#define COLOR vec3(0.06, 0.06, 0.01)
//Scroll speed
#define SCROLL 1.2

//Number of turbulence waves
#define FIRE_NUM 8.0
//Turbulence wave amplitude
#define FIRE_AMP 0.4
//Turbulence wave speed
#define FIRE_SPEED 6.0
//Turbulence frequency (inverse of scale)
#define FIRE_FREQ 12.0
//Turbulence frequency multiplier
#define FIRE_EXP 1.2
float nTime = 0.;
//Bicubic noise texture sample
vec4 noise(vec2 p)
{
    vec2 f = floor(p);
    vec2 s = p-f;
    s *= s * (3.0 - 2.0*s);

    return texture(iChannel0, (f+s+.5) / 256.0);
}

//https://mini.gmshaders.com/p/turbulence
vec2 turbulence(vec2 p, float F, float N, float S, float A, float E)
{
    //Turbulence starting scale
    float freq = F;

    //Turbulence rotation matrix
    mat2 rot = mat2(0.6, -0.8, 0.8, 0.6);

    //Loop through turbulence octaves
    for(float i=0.0; i<N; i++)
    {
        //Scroll along the rotated y coordinate
        float phase = freq * (p * rot).y + S*nTime + i;
        //Add a perpendicular sine wave offset
        p += A * rot[0] * sin(phase) / freq;

        //Rotate for the next octave
        rot *= mat2(0.6, -0.8, 0.8, 0.6);
        //Scale down for the next octave
        freq *= E;
    }

    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    nTime = iTime/10.;
    //Screen coordinates, centered and aspect corrected
    vec2 p = (fragCoord.xy*2.0-iResolution.xy) / iResolution.y;

    //Expand vertically
    float xstretch = 2.0 - 1.5*smoothstep(-2.0,2.0,p.y);
    //Decelerate horizontally
    float ystretch = 1.0 - 0.3 / (1.0+p.x*p.x);
    //Combine
    vec2 stretch = vec2(xstretch, ystretch);
    //Stretch coordinates
    p *= stretch;

    //Scroll upward
    float scroll = SCROLL*nTime;
    p.y -= scroll;

    //Apply turbulence
    p = turbulence(p, FIRE_FREQ, FIRE_NUM, FIRE_SPEED, FIRE_AMP, FIRE_EXP);

    //Scrolling coordinates
    vec2 sp = p;

    //Reverse the scrolling offset
    p.y += scroll + 0.5;

    //Set radius with noise
    float radius = 0.1 + 0.5*noise(sp/0.1).x;
    //Distance to fire
    float dist = length(p) - radius;

    //Scrolling texture uvs
    vec2 uv = sp * FIRE_FREQ*1.5;
    //Sample noise
    float n = noise(uv).x + noise(uv*0.3).y + noise(uv*0.1).z;
    //Glow brightness
    float light = smoothstep(0.3, 0.1, dist) / (0.1 - 0.03*n + dist*dist);

    //Spark highlights
    //float spec = 1.0 / noise(sp*3e1).a;
    //Blend color, intensity and fad edges
    //vec3 sparks = 0.02 * spec * spec * COLOR / (1.0+20.0*p.x*p.x);

    //Combine ambient light and fire
    vec3 col = 1.0 - (n * light * COLOR);// + sparks;

    //Exponential tonemap
    //https://mini.gmshaders.com/p/tonemaps
    //col = 1.5-exp(-col);
    fragColor = vec4(col,1);
}

