#define AA 2
// The porcelain looks better with anti-aliasing, but it tanks the fps on slower machines.
// 1 is off, 2 is 2x2 AA, 3 is 3x3 AA etc..

#define SAA 2
// Selective anti-aliasing tries to target edges and doesn't look quite as good, but is faster.
// 1 is off, 2 is on

float sdHead( vec3 p, float r ){
    // TOP - Cranium
    float cran = sdSphere(p, r);
    // BOTTOM - Jaw/Cheekbones
    vec2 os = vec2(-0.14,0.05);
    float ms = 0.1215;
    vec3 ql = p - vec3(ms,os);
    vec3 qr = p - vec3(-ms,os);
    ql = ql*rotateZ(0.26);
    qr = qr*rotateZ(-0.26);
    ql = ql*rotateX(0.42);
    qr = qr*rotateX(0.42);
    vec3 a = vec3(0.,-0.4, 0.0);
    vec3 b = vec3( 0., 0.4, 0.0);
    float l = length(b-a);
    float wv = sqrt(0.12/l);
    float jaw = opSmoothUnion(sdVerticalVesicaSegment( ql-a, l, wv*0.85 ) - wv*0.15, 
    sdVerticalVesicaSegment( qr-a, l, wv*0.85 ) - wv*0.15, 0.07);
    cran = opSmoothUnion( cran, jaw, 0.03);
    // SHAPING - sides
    float d2 = sdRoundBox(p-vec3(0.76,-0.6,-0.15), vec3(0.4,0.4,0.4), 0.02 ); 
    float d1 = sdRoundBox(p-vec3(-0.76,-0.6,-0.15), vec3(0.4,0.4,0.4), 0.02); 
    float d3 = sdRoundBox(p-vec3(0.76,0.35,-0.2), vec3(0.4,0.4,0.4), 0.02 ); 
    float d4 = sdRoundBox(p-vec3(-0.76,0.35,-0.2), vec3(0.4,0.4,0.4), 0.02 ); 
    cran = opSmoothSubtraction(d4,opSmoothSubtraction(d3,cran, .2), .2);
    cran = opSmoothSubtraction(d2,opSmoothSubtraction(d1,cran, .2), .2);
    // SHAPING - top
    float d6 = sdRoundBox(p-vec3(0.,0.8,-0.0), vec3(0.4,0.4,0.4), 0.03 ); 
    cran = opSmoothSubtraction(d6,cran,.5);
    // SHAPING - front
    float d7 = sdRoundBox(p-vec3(0.,0.42,0.82), vec3(0.4,0.4,0.4), 0.03 ); 
    cran = opSmoothSubtraction(d7,cran,.17);
    // Eye Sockets
    float d5 = sdRoundBox(p-vec3(0.0,-0.22,0.39), vec3(0.18,0.001,0.001), 0.03 );
    cran = opSmoothSubtraction( d5, cran, .08);
    // Nose
    cran = opSmoothUnion( cran, sdRoundCone(   p-vec3( 0.0,-0.3, 0.34), vec3(0.0,0.0,0), vec3(0.0,0.17,-0.06), 0.03, 0.001), 0.05);
    // Eyes
    float eyel = sdSphere(p-vec3(0.11,-0.2,0.257), 0.05);
    float eyer = sdSphere(p-vec3(-0.11,-0.2,0.257), 0.05);
    cran = opSmoothUnion( cran, eyer, 0.025);
    cran = opSmoothUnion( cran, eyel, 0.025);

    return cran;
}

float map( in vec3 pos )
{
    float d = 1e10;
    pos *= 0.9;
    // Shoulders-Arms-Torso
    vec3 q = pos - vec3(0.0,0.0,0.0);
    q.z-=0.3;
    q.y+=1.8 - q.x/20.;
    q.x+=0.35 + q.y/20.;;
    q = q*rotateX(1.7);
    q = q*rotateZ(0.4);
    q = q*rotateY(0.25);
    float an = 1.0;
    vec2 c = vec2(sin(an),cos(an));
    vec4 w = opElongate( q, vec3(0.13,0.27,0.78) );
    float shoulder = sdCappedTorus(w.xyz, c, 0.7, 0.2);
    shoulder = shoulder+ sin((1.75+q.x)*3.)/15.;
    shoulder = shoulder+0.04;
    d = min( d, shoulder);
    // Collar Bones
    vec3 qt = pos - vec3(-0.8,-1.1,-0.7);
    float collar = rounding( udTriangle( qt, vec3(0.6,0.0,0.19), vec3(0.4,-0.7,0.2), vec3(-0.1,-0.2,0.03) ), 0.05 );
    collar = opSmoothUnion(collar, rounding( udTriangle( qt, vec3(0.7,0.0,0.1), vec3(0.8,-0.7,0.2), vec3(1.1,0.2,0.5) ), 0.01 ), 0.1);
    d = opSmoothUnion(d, collar , 0.13);
    // Extra Arm Padding
    vec3 qv = pos - vec3(-0.9,-2.3,-0.6);
    vec3 a = vec3(0.,-0.5, 0.0);
    vec3 b = vec3( 0., 0.5, 0.0);
    float l = length(b-a);
    float wv = sqrt(0.1/l);
    d = opSmoothUnion( d, sdVerticalVesicaSegment( qv-a, l, wv*0.85 ) - wv*0.15 , 0.2);
    // Extra Chest Padding
    qv = pos - vec3(-0.1,-1.8,-0.5);
    a = vec3(0.,-0.35, 0.0);
    b = vec3( 0., 0.35, 0.0);
    l = length(b-a);
    wv = sqrt(0.2/l);
    d = opSmoothUnion( d, sdVerticalVesicaSegment( qv-a, l, wv*0.85 ) - wv*0.15 , 0.3);
    // Neck
    qv = pos - vec3(0.2,-1.5,-0.3);
    a = vec3(0.,-0.2, 0.0);
    b = vec3( 0., 0.3, 0.0);
    l = length(b-a);
    qv = pos - vec3(-0.2,-0.9,-0.55);
    qv = qv*rotateX(-0.3);
    float neck = sdCylinder(qv, vec2(0.185, 0.65));
    d = opSmoothUnion( d, neck, 0.2);
    // Head
    vec3 qh = pos - vec3(-0.1,-0.07,-0.3);
    qh = qh*rotateY(-0.8);
    float head = sdHead(qh, 0.36);
    d = opSmoothUnion( d, head , 0.15);
    return d;
}

float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
    float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
        float h = map( ro + rd*t );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) +
					  e.xxx*map( pos + e.xxx*eps ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.9+(0.3*sin(iTime/1.5));
    //float an = iMouse.x/100.;
	vec3 ro = vec3( 1.8*cos(an), 0.3,1.8*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 edge = ww;
           bool close = false;
        bool miss = false;
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));
    // render
    vec3 tot = vec3(0.0);
    int count = 0;
    #if AA>1
    for( int m=0; m<AA; m++ )
    #if SAA<2
    for( int n=0; n<AA; n++ )
    #endif
    {
        count++;
        // pixel coordinates
         #if SAA>1
        int n = 0;
        #endif
        vec2 mn = vec2(float(m),float(n));
        #if SAA>1
        if(fragCoord.y>iResolution.y/2.){mn = mn.yx;}
        #endif
        vec2 o =  mn/ float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif
	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );
        // raymarch
        const float tmax = 5.0;

        float cth = 0.01;
        float mth = 0.02;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            #if SAA>1
            close = h<cth?true:close;
            miss = close&&h>mth?true:miss;
            #endif
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }
        // shading/lighting	
        vec3 bg = vec3(0.7,0.75,0.9);
        vec3 col = bg;
        // paint blotches
        float blotchAmt = smoothstep(30.,44.5, magicBox((p+5.2)*5.));
        blotchAmt = pow(blotchAmt, 3.);// attenuate
        blotchAmt = .7*smoothstep(.2,.4,blotchAmt);// sharpen
        col *= vec3(1.-blotchAmt,1.-blotchAmt,1.0);
        // grain
        col.rgb += (rand(p)-.5)*.08;
        col.rgb = saturate(col.rgb);
        vec3 bd = col;
        vec2 hres = vec2(800., 450.)/iResolution.xy;
       // edge = cross(edge, nor);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            edge = abs(cross(edge, nor));
            vec3  lig = normalize(vec3(0.5,5.5,3.0));
            vec3 ref = reflect(rd, nor);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            float spe = pow(max(0.0, dot(lig, ref)),32.0);
            float sha = calcSoftshadow( pos, lig, 0.03,3.0, 8.0 );
            float amb = 0.6 + 0.2*dot(nor,normalize(vec3(1.0,1.0,0.0)));
            vec2 tpoz = pos.xy*200.;tpoz.y+=90.0;tpoz.x+=460.; tpoz = abs(tpoz);
            vec2 tpoy = pos.zy*200.;tpoy.y+=300.;tpoy.x-=450.; tpoy = abs(tpoy);
            vec3 alb = texture(iChannel0, (tpoy.xy/hres)/iResolution.xy, 0.).rgb*0.75;
            alb = min(alb, texture(iChannel0, (tpoz.xy/hres)/iResolution.xy, 0.).rgb*0.75);
            //vec3 alb = texelFetch(iChannel0, ivec2(tpoy.xy/hres), 0).rgb*0.75;
            //alb = min(alb, texelFetch(iChannel0, ivec2(tpoz.xy/hres), 0).rgb*0.75);
            col = alb*amb + (spe*sha) + vec3(0.4,0.4,0.6)*dif*sha;
        }
        float f = smoothstep(-0.7,-1.0, p.y);
        col = mix(col, bg, f);
        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
        #if SAA>1
        vec3 edgef = fwidth(edge);
        float edget = edgef.x+edgef.y+edgef.z;
        if(edget<0.1&&!miss){m=AA;}//else{tot=vec3(0);}
        #endif
    }
    tot /= float(count);
    #endif
    //if(miss){tot=vec3(0);}
	fragColor = vec4( tot, 1.0 );
}

