const float CURVATURE = 3.0;

const float BLUR = 0.031;

const float CA_AMT = 1.024;



float randInRange(vec2 range, vec2 seed) {

    float r = (rand(seed) + 1.0) / 2.0;

    return range.x + r * (range.y - range.x);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // FLICKER
    float flickerPeriod = randInRange(vec2(1.5, 3.0), vec2(iTime, iTime));
    float noiseScale = 0.01;
    if (fract(iTime / flickerPeriod) > 0.99) {
        noiseScale = 0.1;

    }

    vec2 flickerSeed = vec2(iTime, uv.y);
    uv.x += randInRange(vec2(-0.01, 0.01), flickerSeed);

    // CURVATURE
    vec2 crtUV = uv * 2.0 - 1.0;
    vec2 offset = crtUV.yx / CURVATURE;
    crtUV += crtUV * offset * offset;
    crtUV = crtUV * 0.5 + 0.5;

    // EDGE BLUR
    vec2 edge = smoothstep(0.0, BLUR, crtUV) *
                (1.0 - smoothstep(1.0 - BLUR, 1.0, crtUV));

    // CHROMATIC ABERRATION
    float r = texture(iChannel0, (crtUV - 0.5) * CA_AMT + 0.5).r;
    float g = texture(iChannel0, crtUV).g;
    float b = texture(iChannel0, (crtUV - 0.5) / CA_AMT + 0.5).b;
    vec3 color = vec3(r, g, b) * edge.x * edge.y;

    // SCANLINES
    if (mod(fragCoord.y, 2.0) < 1.0)
        color *= 0.7;
    else if (mod(fragCoord.x, 3.0) < 1.0)
        color *= 0.7;
    else
        color *= 1.2;

    float noise = randInRange(vec2(0.0, noiseScale), flickerSeed);
    color += vec3(noise);
    color = (color + sqrt( color )*2.)/3.;
    fragColor = vec4(color, 1.0);
    if(r>0.5&&b<0.3&&g<0.3){fragColor = vec4(1,0,0,1);}

}

