        } else if(fragColor.r>0.15){fragColor.g=0.1; fragColor.r=0.1;fragColor.b=0.7;}else{fragColor = vec4(1);}
    } else {fragColor = texture(iChannel1, uv);}
    }

#define AA 1
// The porcelain looks better with anti-aliasing, but it tanks the fps on slower machines.
// 1 is off, 2 is 2x2 AA, 3 is 3x3 AA etc..

#define SAA 1
// Selective anti-aliasing tries to target edges and doesn't look quite as good, but is faster.
// 1 is off, 2 is on

int area = 0;
vec2 facepos = vec2(0);
bool necka = false;
bool roses = false;
float sdHead( vec3 p ){
    // TOP - Cranium

    float cran = sdSphere(p-vec3(0,0,0.03),0.35);
    // BOTTOM - Jaw/Cheekbones
    vec2 os = vec2(-0.14,0.05);
    float ms = 0.127;
    vec3 ql = p - vec3(ms,os);
    vec3 qr = p - vec3(-ms,os);
    ql = ql*rotateZ(0.26);
    qr = qr*rotateZ(-0.26);
    ql = ql*rotateX(0.42);
    qr = qr*rotateX(0.42);
    vec3 a = vec3(0.,-0.4, 0.0);
    vec3 b = vec3( 0., 0.4, 0.0);
    float l = length(b-a);
    float wv = sqrt(0.105/l);
    float jaw = opSmoothUnion(sdVerticalVesicaSegment( ql-a, l, wv*0.85 ) - wv*0.15, 
    sdVerticalVesicaSegment( qr-a, l, wv*0.85 ) - wv*0.15, 0.07);
    cran = opSmoothUnion( cran, jaw, 0.03);
    // SHAPING - sides
    float d2 = sdRoundBox(p-vec3(0.74,-0.4,-0.15), vec3(0.4,0.6,0.4), 0.02 ); 
    float d1 = sdRoundBox(p-vec3(-0.74,-0.4,-0.15), vec3(0.4,0.6,0.4), 0.02); 

    cran = opSmoothSubtraction(d2,opSmoothSubtraction(d1,cran, .2), .2);
    // SHAPING - top
    float d6 = sdRoundBox(p-vec3(0.,0.75,-0.0), vec3(0.4,0.4,0.4), 0.04 ); 
    cran = opSmoothSubtraction(d6,cran,.52);
    // SHAPING - front
    float d7 = sdRoundBox(p-vec3(0.,0.25,0.77), vec3(0.4,0.4,0.4), 0.01 ); 
    cran = opSmoothSubtraction(d7,cran,.15);
    // Eye Sockets
    float d5 = sdRoundBox(p-vec3(0.0,-0.21,0.385), vec3(0.18,0.001,0.001), 0.06 );
    cran = opSmoothSubtraction( d5, cran, .04);
    // nose
    float nose = sdRoundCone(   p-vec3( 0.0,-0.32, 0.32), vec3(0.0,0.0,0), vec3(0.0,0.2,-0.06), 0.033, 0.01);
    nose = opSmoothUnion( nose, sdRoundCone(   p-vec3( 0.0,-0.34, 0.3), vec3(-0.026,0,0), vec3(0.026,0,0), 0.015, 0.015), 0.03);
    float nos = sdSphere(p-vec3(0.,-0.21,0.45), 0.11);
    nose = opSmoothSubtraction( nos, nose, .08);
    cran = opSmoothUnion( cran, nose, 0.02);


    // Eyes
    float eyel = sdSphere(p-vec3(0.11,-0.22,0.257), 0.035);


    float eyer = sdVerticalVesicaSegment(p-vec3(-0.15,-0.43,0.2), 0.35, 0.23);

    eyer+=sin(p.y*70.)/300.;
    eyer+=sin(p.x*80.)/400.;
    eyer+=sin(p.z*90.)/500.;
    if(eyer<0.01){roses = true;}
    cran = opSmoothUnion( cran, eyer, 0.01);

    cran = opSmoothUnion( cran, eyel, 0.03);

    float fan = sdRoundBox(p-vec3(0.,0.37,-0.7), vec3(0.8,0.8,0.8), 0.01 ); 

    cran = min(fan,cran);

    if(cran<0.001){area = 1;facepos = p.xy;}
    if(fan<0.02){area = 2;facepos = p.xy;}

    return cran;
}

float map( in vec3 pos )
{
    float d = 1e10;
    pos *= 0.9;
    pos = pos*rotateZ(-0.08);
    // Shoulders-Arms-Torso
    vec3 q = pos - vec3(0.0,0.0,0.0);
    q.z-=0.3;
    q.y+=1.8 - q.x/20.;
    q.x+=0.35 + q.y/20.;;
    q = q*rotateX(1.7);
    q = q*rotateZ(0.4);
    q = q*rotateY(0.25);
    float an = 1.0;
    vec2 c = vec2(sin(an),cos(an));
    vec4 w = opElongate( q, vec3(0.13,0.27,0.78) );
    float shoulder = sdCappedTorus(w.xyz, c, 0.7, 0.2);
    shoulder = shoulder+ sin((1.75+q.x)*3.)/15.;
    shoulder = shoulder+0.04;
    d = min( d, shoulder);
    // Collar Bones
    vec3 qt = pos - vec3(-0.8,-1.1,-0.7);
    float collar = rounding( udTriangle( qt, vec3(0.6,0.0,0.1), vec3(0.4,-0.7,0.2), vec3(-0.1,-0.2,0.03) ), 0.05 );
    collar = opSmoothUnion(collar, rounding( udTriangle( qt, vec3(0.7,0.0,0.1), vec3(0.8,-0.7,0.2), vec3(1.1,0.2,0.4) ), 0.01 ), 0.1);
    d = opSmoothUnion(d, collar , 0.13);

    // Neck
     vec3 qv = pos - vec3(0.2,-1.5,-0.3);
     vec3 a = vec3(0.,-0.2, 0.0);
     vec3 b = vec3( 0., 0.3, 0.0);
   float l = length(b-a);
    qv = pos - vec3(-0.2,-0.9,-0.55);
    qv = qv*rotateX(-0.3);
    float neck = sdCylinder(qv, vec2(0.16, 0.65));

    d = opSmoothUnion( d, neck, 0.2);
    if(d<0.01){necka=true;}
    // Head
    vec3 qh = pos - vec3(-0.15,-0.12,-0.36);
    qh = qh*rotateY(-0.9-(0.1*sin(iTime/1.5)));
    float head = sdHead(qh/1.03);
    d = opSmoothUnion( d, head , 0.15);
    return d;
}

float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
    float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
        float h = map( ro + rd*t );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) +
					  e.xxx*map( pos + e.xxx*eps ) );
}

