    if (xs >= threshold) {
        float range = iResolution.x - threshold;
        float t = (xs - threshold) / range;  // 0 to 1

        // This always increases, but slows down toward the edge
        mappedX = threshold + range * t / (1.0 + stretch * t);
    }

