    int count = 0;
    #if AA>1
    for( int m=0; m<AA; m++ )
    #if SAA<2
    for( int n=0; n<AA; n++ )
    #endif
    {
        count++;
        // pixel coordinates
         #if SAA>1
        int n = 0;
        #endif
        vec2 mn = vec2(float(m),float(n));
        #if SAA>1
        if(fragCoord.y>iResolution.y/2.){mn = mn.yx;}
        #endif
        vec2 o =  mn/ float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif
	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );
        // raymarch
        const float tmax = 5.0;

        float cth = 0.01;
        float mth = 0.02;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            #if SAA>1
            close = h<cth?true:close;
            miss = close&&h>mth?true:miss;
            #endif
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }
        // shading/lighting	
        vec3 bg = vec3(0.7,0.75,0.9);
        vec3 col = bg;
        // paint blotches
        float blotchAmt = smoothstep(30.,44.5, magicBox((p+5.2)*5.));
        blotchAmt = pow(blotchAmt, 3.);// attenuate
        blotchAmt = .7*smoothstep(.2,.4,blotchAmt);// sharpen
        col *= vec3(1.-blotchAmt,1.-blotchAmt,1.0);
        // grain
        col.rgb += (rand(p)-.5)*.08;
        col.rgb = saturate(col.rgb);
        vec3 bd = col;
        vec2 hres = vec2(800., 450.)/iResolution.xy;
       // edge = cross(edge, nor);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            edge = abs(cross(edge, nor));
            vec3  lig = normalize(vec3(0.5,5.5,3.0));
            vec3 ref = reflect(rd, nor);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            float spe = pow(max(0.0, dot(lig, ref)),32.0);
            float sha = calcSoftshadow( pos, lig, 0.03,3.0, 8.0 );
            float amb = 0.6 + 0.2*dot(nor,normalize(vec3(1.0,1.0,0.0)));
            vec2 tpoz = pos.xy*200.;tpoz.y+=90.0;tpoz.x+=460.; tpoz = abs(tpoz);
            vec2 tpoy = pos.zy*200.;tpoy.y+=300.;tpoy.x-=450.; tpoy = abs(tpoy);
            vec3 alb = texture(iChannel0, (tpoy.xy/hres)/iResolution.xy, 0.).rgb*0.75;
            //alb = min(alb, texture(iChannel0, (tpoz.xy/hres)/iResolution.xy, 0.).rgb*0.75);
            //vec3 alb = texelFetch(iChannel0, ivec2(tpoy.xy/hres), 0).rgb*0.75;
            //alb = min(alb, texelFetch(iChannel0, ivec2(tpoz.xy/hres), 0).rgb*0.75);
            col = alb*amb + (spe*sha) + vec3(0.4,0.4,0.6)*dif*sha;
        }
        float f = smoothstep(-0.7,-1.0, p.y);
        col = mix(col, bg, f);
        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
        #if SAA>1
        vec3 edgef = fwidth(edge);
        float edget = edgef.x+edgef.y+edgef.z;
        if(edget<0.1&&!miss){m=AA;}//else{tot=vec3(0);}
        #endif
    }
    tot /= float(count);
    #endif
    //if(miss){tot=vec3(0);}
	fragColor = vec4( tot, 1.0 );
}

