        ret = mix(vec3(0,0,.5), vec3(.5,.5,1), sin(dist * pi2 * 50.));// red = negative / inside geometry.
    else
	    ret = mix(vec3(1.,.5,.5), vec3(.5,0,0), sin(dist * pi2 * 50.));// blue = positive, of of geometry.
    ret = mix(ret, vec3(0), clamp(abs(dist),0.,1.));// falloff
    return ret;
}

