#define AA 2
// The porcelain looks better with anti-aliasing, but it tanks the fps on slower machines.
// 1 is off, 2 is 2x2 AA, 3 is 3x3 AA etc..

#define SAA 1
// Selective anti-aliasing tries to target edges and doesn't look quite as good, but is faster.
// 1 is off, 2 is on

int area = 0;
vec2 facepos = vec2(0);
bool necka = false;
bool roses = false;
float sdHead( vec3 p ){
    // TOP - Cranium

    float cran = sdSphere(p-vec3(0,0,0.03),0.35);
    // BOTTOM - Jaw/Cheekbones
    vec2 os = vec2(-0.14,0.05);
    float ms = 0.127;
    vec3 ql = p - vec3(ms,os);
    vec3 qr = p - vec3(-ms,os);
    ql = ql*rotateZ(0.26);
    qr = qr*rotateZ(-0.26);
    ql = ql*rotateX(0.42);
    qr = qr*rotateX(0.42);
    vec3 a = vec3(0.,-0.4, 0.0);
    vec3 b = vec3( 0., 0.4, 0.0);
    float l = length(b-a);
    float wv = sqrt(0.105/l);
    float jaw = opSmoothUnion(sdVerticalVesicaSegment( ql-a, l, wv*0.85 ) - wv*0.15, 
    sdVerticalVesicaSegment( qr-a, l, wv*0.85 ) - wv*0.15, 0.07);
    cran = opSmoothUnion( cran, jaw, 0.03);
    // SHAPING - sides
    float d2 = sdRoundBox(p-vec3(0.74,-0.4,-0.15), vec3(0.4,0.6,0.4), 0.02 ); 
    float d1 = sdRoundBox(p-vec3(-0.74,-0.4,-0.15), vec3(0.4,0.6,0.4), 0.02); 

    cran = opSmoothSubtraction(d2,opSmoothSubtraction(d1,cran, .2), .2);
    // SHAPING - top
    float d6 = sdRoundBox(p-vec3(0.,0.75,-0.0), vec3(0.4,0.4,0.4), 0.04 ); 
    cran = opSmoothSubtraction(d6,cran,.52);
    // SHAPING - front
    float d7 = sdRoundBox(p-vec3(0.,0.25,0.77), vec3(0.4,0.4,0.4), 0.01 ); 
    cran = opSmoothSubtraction(d7,cran,.15);
    // Eye Sockets
    float d5 = sdRoundBox(p-vec3(0.0,-0.21,0.385), vec3(0.18,0.001,0.001), 0.06 );
    cran = opSmoothSubtraction( d5, cran, .04);
    // nose
    float nose = sdRoundCone(   p-vec3( 0.0,-0.32, 0.32), vec3(0.0,0.0,0), vec3(0.0,0.2,-0.06), 0.033, 0.01);
    nose = opSmoothUnion( nose, sdRoundCone(   p-vec3( 0.0,-0.34, 0.3), vec3(-0.026,0,0), vec3(0.026,0,0), 0.015, 0.015), 0.03);
    float nos = sdSphere(p-vec3(0.,-0.21,0.45), 0.11);
    nose = opSmoothSubtraction( nos, nose, .08);
    cran = opSmoothUnion( cran, nose, 0.02);


    // Eyes
    float eyel = sdSphere(p-vec3(0.11,-0.22,0.257), 0.035);


    float eyer = sdVerticalVesicaSegment(p-vec3(-0.15,-0.43,0.2), 0.35, 0.23);

    eyer+=sin(p.y*70.)/300.;
    eyer+=sin(p.x*80.)/400.;
    eyer+=sin(p.z*90.)/500.;
    if(eyer<0.01){roses = true;}
    cran = opSmoothUnion( cran, eyer, 0.01);

    cran = opSmoothUnion( cran, eyel, 0.03);

    float fan = sdRoundBox(p-vec3(0.,0.37,-0.7), vec3(0.8,0.8,0.8), 0.01 ); 

    cran = min(fan,cran);

    if(cran<0.001){area = 1;facepos = p.xy;}
    if(fan<0.02){area = 2;facepos = p.xy;}

    return cran;
}

float map( in vec3 pos )
{
    float d = 1e10;
    pos *= 0.9;
    pos = pos*rotateZ(-0.08);
    // Shoulders-Arms-Torso
    vec3 q = pos - vec3(0.0,0.0,0.0);
    q.z-=0.3;
    q.y+=1.8 - q.x/20.;
    q.x+=0.35 + q.y/20.;;
    q = q*rotateX(1.7);
    q = q*rotateZ(0.4);
    q = q*rotateY(0.25);
    float an = 1.0;
    vec2 c = vec2(sin(an),cos(an));
    vec4 w = opElongate( q, vec3(0.13,0.27,0.78) );
    float shoulder = sdCappedTorus(w.xyz, c, 0.7, 0.2);
    shoulder = shoulder+ sin((1.75+q.x)*3.)/15.;
    shoulder = shoulder+0.04;
    d = min( d, shoulder);
    // Collar Bones
    vec3 qt = pos - vec3(-0.8,-1.1,-0.7);
    float collar = rounding( udTriangle( qt, vec3(0.6,0.0,0.1), vec3(0.4,-0.7,0.2), vec3(-0.1,-0.2,0.03) ), 0.05 );
    collar = opSmoothUnion(collar, rounding( udTriangle( qt, vec3(0.7,0.0,0.1), vec3(0.8,-0.7,0.2), vec3(1.1,0.2,0.4) ), 0.01 ), 0.1);
    d = opSmoothUnion(d, collar , 0.13);

    // Neck
     vec3 qv = pos - vec3(0.2,-1.5,-0.3);
     vec3 a = vec3(0.,-0.2, 0.0);
     vec3 b = vec3( 0., 0.3, 0.0);
   float l = length(b-a);
    qv = pos - vec3(-0.2,-0.9,-0.55);
    qv = qv*rotateX(-0.3);
    float neck = sdCylinder(qv, vec2(0.16, 0.65));

    d = opSmoothUnion( d, neck, 0.2);
    if(d<0.01){necka=true;}
    // Head
    vec3 qh = pos - vec3(-0.15,-0.12,-0.36);
    qh = qh*rotateY(-0.9-(0.1*sin(iTime/1.5)));
    float head = sdHead(qh/1.03);
    d = opSmoothUnion( d, head , 0.15);
    return d;
}

float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
    float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
        float h = map( ro + rd*t );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) +
					  e.xxx*map( pos + e.xxx*eps ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
     // camera movement	
	float an = 0.8+(0.2*sin(iTime/1.5));
    //float an = iMouse.x/100.;
	vec3 ro = vec3( 1.8*cos(an), 0.3,1.8*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 edge = ww;
    float ed = 0.;
    vec3 ndif = vec3(0);
    bool close = false;
    bool miss = false;
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));
    // render
    vec3 tot = vec3(0.0);
    int count = 0;
    #if AA>1
    for( int m=0; m<AA; m++ )
    #if SAA<2
    for( int n=0; n<AA; n++ )
    #endif
    {
        count++;
        // pixel coordinates
         #if SAA>1
        int n = 0;
        #endif
        vec2 mn = vec2(float(m),float(n));
        #if SAA>1
        if(fragCoord.y>iResolution.y/2.){mn = mn.yx;}
        #endif
        vec2 o =  mn/ float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif
	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );
                // shading/lighting	
        vec3 bg = vec3(0.6,0.65,0.8);
        vec3 col = bg;
        // paint blotches
        float blotchAmt = smoothstep(30.,44.5, magicBox((p+5.2)*5.));
        blotchAmt = pow(blotchAmt, 3.);// attenuate
        blotchAmt = .7*smoothstep(.2,.4,blotchAmt);// sharpen
        col *= vec3(1.-blotchAmt,1.-blotchAmt,1.0);
        // grain
        col.rgb += (rand(p)-.5)*.08;
        col.rgb = saturate(col.rgb);
        vec3 bd = col;
        // raymarch
        if(uv.x>0.4 && uv.x<0.7 && uv.y<0.7){
        const float tmax = 5.0;

        float cth = 0.01;
        float mth = 0.02;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            #if SAA>1
            close = h<cth?true:close;
            miss = close&&h>mth?true:miss;
            #endif
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        vec2 hres = vec2(800., 450.)/iResolution.xy;
       // edge = cross(edge, nor);
            vec2 tpoz = facepos.xy*370.;tpoz.y+=234.;tpoz.x+=400.; tpoz = abs(tpoz); //350
            vec2 rpoz = tpoz; rpoz.y += 116.;
            vec3 alb = vec3(1);
            if(area>0){alb = textureLod(iChannel0, (tpoz/hres)/iResolution.xy, 0.5).rgb;}
        if( t<tmax )
        {


            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            ed = 10.*(dot(nor,rd)+0.3);
            //ndif = normalize(vec3(0,0,1)-nor);
            edge = abs(cross(edge, nor));
            vec3  lig = normalize(vec3(0.5,2.0,1.5));
            //vec3  lig2 = normalize(vec3(1.0,0.5,1.5));
            vec3 ref = reflect(rd, nor);
            ndif = normalize(ref+nor);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            //float dif2 = clamp( dot(nor,lig2), 0.0, 1.0 );


           // vec2 tpoy = pos.zy*200.;tpoy.y+=300.;tpoy.x-=450.; tpoy = abs(tpoy);

            //if(necka){alb = vec3(0,1,0);}
            //vec3 alb = texture(iChannel0, (tpoy.xy/hres)/iResolution.xy, 0.).rgb;
            //alb=alb*alb;
            //alb = min(alb, texture(iChannel0, (tpoz.xy/hres)/iResolution.xy, 0.).rgb*0.75);
            //vec3 alb = texelFetch(iChannel0, ivec2(tpoy.xy/hres), 0).rgb*0.75;
            //alb = min(alb, texelFetch(iChannel0, ivec2(tpoz.xy/hres), 0).rgb*0.75);
            if(area!=2||pos.y<-0.7) {

                if(necka){
                  vec2 bpoz = pos.xy*570.;bpoz.y+=400.;bpoz.x+=600.; bpoz = abs(bpoz);
                  alb = textureLod(iChannel0, (bpoz/hres)/iResolution.xy, 0.5).rgb;
                  vec2 bpox = pos.xz*370.;bpox.y+=225.;bpox.x+=310.; bpox = abs(bpox);
                  alb = min(alb,textureLod(iChannel0, (bpox/hres)/iResolution.xy, 0.5).rgb);
                }

                float spe = pow(max(0.0, dot(lig, ref)),32.0);
                float sha = 1.;//calcSoftshadow( pos, lig, 0.03,3.0, 8.0 );
                float amb = 0.55 + 0.2*dot(nor,normalize(vec3(0.0,1.0,0.0)));
                if(roses){
                  vec3 rcol = textureLod(iChannel1, (rpoz/hres)/iResolution.xy, 0.5).rgb;
                  if(rcol.r>0.3&&rcol.g<0.3&&rcol.b<0.3){
                        alb=rcol;
                  }
                  alb = min(alb, rcol);
                  spe=0.;
                  dif=0.4;
                }
                col =alb*amb + (spe*sha) + vec3(0.4,0.4,0.6)*dif/2.*sha;
            } else {
                col=min(col,alb);

            }

            if(alb.r>0.3&&alb.g<0.3&&alb.b<0.3){
                col=alb;
            }
        }
        }

        float f = smoothstep(-0.7,-1.0, p.y);
        col = mix(col, bg, f);
        // gamma        
        col = (col + sqrt( col ) + sqrt( col ))/3.;
	    tot += col;
    #if AA>1
        #if SAA>1
        vec3 edgef = fwidth(edge);
        float edget = edgef.x+edgef.y+edgef.z;
        if(edget<0.1&&!miss){m=AA;}//else{tot=vec3(0);}
        #endif
    }
    tot /= float(count);
    #endif
    //if(miss){tot=vec3(0);}
    //if(sqrt((ndif.x*ndif.x) + (ndif.y*ndif.y)+ (ndif.z*ndif.z))>1.){tot=ndif;}

	fragColor = vec4( tot, 1.0 );
}

