const float zoomSpeed = 0.05;
const float noiseScale = 4.;
const float noiseSpeed = 0.0;

const float PI = 3.1415;
float hash(float n) { return fract(sin(n) * 1e4); }
float noise(vec3 x) {
    const vec3 step = vec3(110, 241, 171);
    vec3 i = floor(x);
    vec3 f = fract(x);
    float n = dot(i, step);
    vec3 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(mix( hash(n + dot(step, vec3(0, 0, 0))), hash(n + dot(step, vec3(1, 0, 0))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 0))), hash(n + dot(step, vec3(1, 1, 0))), u.x), u.y),
               mix(mix( hash(n + dot(step, vec3(0, 0, 1))), hash(n + dot(step, vec3(1, 0, 1))), u.x),
                   mix( hash(n + dot(step, vec3(0, 1, 1))), hash(n + dot(step, vec3(1, 1, 1))), u.x), u.y), u.z);
}
float fbm (vec3 p) {
  float amplitude = .5;
  float result = 0.0;
  for (float index = 0.0; index <= 5.0; ++index) {
    result += noise(p / amplitude) * amplitude;
    amplitude /= 2.;
  }
  return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 color = vec3(1);
  float timeline = iTime*zoomSpeed;
  vec2 unit = 1./iResolution.xy;
  vec2 uv = fragCoord.xy/iResolution.xy;
  vec2 p = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
  float stage = floor(timeline);
  float ratio = fract(timeline);
  const float iterations = 3.;
  float spawn = 1.;
  float zoom = .5;
  float scale = noiseScale * iResolution.y / 320.;
  for (float index = iterations; index > 0.; --index) {
    ratio = mod(ratio+1./iterations, 1.);
    vec3 s = vec3(p*scale*(zoom-ratio*zoom), 1. + timeline*noiseSpeed);
    float salty = fbm(s) * 2. - 1.;
    float angle = salty * PI * 8.;
    uv += vec2(cos(angle),sin(angle)) * unit * sin(ratio*PI);
    spawn *= 1. - abs(sin(angle)) * sin(ratio*PI);
  }
  color *= spawn;
  float blend = (.5+.5*(1.-spawn));
  fragColor = texture(iChannel0, uv)*blend + (1.-blend)*vec4(color, 1);
}

