        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif
	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );
                // shading/lighting	
        vec3 bg = vec3(0.6,0.65,0.8);
        vec3 col = bg;
        // paint blotches
        float blotchAmt = smoothstep(30.,44.5, magicBox((p+5.2)*5.));
        blotchAmt = pow(blotchAmt, 3.);// attenuate
        blotchAmt = .7*smoothstep(.2,.4,blotchAmt);// sharpen
        col *= vec3(1.-blotchAmt,1.-blotchAmt,1.0);
        // grain
        col.rgb += (rand(p)-.5)*.08;
        col.rgb = saturate(col.rgb);
        vec3 bd = col;
        // raymarch
        if(uv.x>0.4 && uv.x<0.7 && uv.y<0.7){
        const float tmax = 5.0;

        float cth = 0.01;
        float mth = 0.02;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            #if SAA>1
            close = h<cth?true:close;
            miss = close&&h>mth?true:miss;
            #endif
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        vec2 hres = vec2(800., 450.)/iResolution.xy;
       // edge = cross(edge, nor);
            vec2 tpoz = facepos.xy*370.;tpoz.y+=234.;tpoz.x+=400.; tpoz = abs(tpoz); //350
            vec2 rpoz = tpoz; rpoz.y += 116.;
            vec3 alb = vec3(1);
            if(area>0){alb = texture(iChannel0, (tpoz/hres)/iResolution.xy, 0.).rgb;}
        if( t<tmax )
        {


            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            edge = abs(cross(edge, nor));
            vec3  lig = normalize(vec3(0.5,2.0,1.5));
            //vec3  lig2 = normalize(vec3(1.0,0.5,1.5));
            vec3 ref = reflect(rd, nor);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            //float dif2 = clamp( dot(nor,lig2), 0.0, 1.0 );


           // vec2 tpoy = pos.zy*200.;tpoy.y+=300.;tpoy.x-=450.; tpoy = abs(tpoy);

            //if(necka){alb = vec3(0,1,0);}
            //vec3 alb = texture(iChannel0, (tpoy.xy/hres)/iResolution.xy, 0.).rgb;
            //alb=alb*alb;
            //alb = min(alb, texture(iChannel0, (tpoz.xy/hres)/iResolution.xy, 0.).rgb*0.75);
            //vec3 alb = texelFetch(iChannel0, ivec2(tpoy.xy/hres), 0).rgb*0.75;
            //alb = min(alb, texelFetch(iChannel0, ivec2(tpoz.xy/hres), 0).rgb*0.75);
            if(area!=2||pos.y<-0.7) {

                if(necka){
                  vec2 bpoz = pos.xy*570.;bpoz.y+=400.;bpoz.x+=600.; bpoz = abs(bpoz);
                  alb = texture(iChannel0, (bpoz/hres)/iResolution.xy, 0.).rgb;
                  vec2 bpox = pos.xz*370.;bpox.y+=225.;bpox.x+=310.; bpox = abs(bpox);
                  alb = min(alb,texture(iChannel0, (bpox/hres)/iResolution.xy, 0.).rgb);
                }

                float spe = pow(max(0.0, dot(lig, ref)),32.0);
                float sha = 1.;//calcSoftshadow( pos, lig, 0.03,3.0, 8.0 );
                float amb = 0.55 + 0.2*dot(nor,normalize(vec3(0.0,1.0,0.0)));
                if(roses){
                  vec3 rcol = texture(iChannel1, (rpoz/hres)/iResolution.xy, 0.).rgb;
                  if(rcol.r>0.3&&rcol.g<0.3&&rcol.b<0.3){
                        alb=rcol;
                  }
                  alb = min(alb, rcol);
                  spe=0.;
                  dif=0.2;
                }
                col =alb*amb + (spe*sha) + vec3(0.4,0.4,0.6)*dif/2.*sha;
            } else {
                col=min(col,alb);

            }

            if(alb.r>0.3&&alb.g<0.3&&alb.b<0.3){
                col=alb;
            }

        }
        }

        float f = smoothstep(-0.7,-1.0, p.y);
        col = mix(col, bg, f);
