        if(edget<0.1&&!miss){m=AA;}//else{tot=vec3(0);}
        #endif
    }
    tot /= float(count);
    #endif
    //if(miss){tot=vec3(0);}
	fragColor = vec4( tot, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
