    float dist = pow(distance(uv, vec2(0.5))*2.5, 0.7);
    vec3 cL =  mix(vec3(0,0,1), vec3(0,0,0.5),min(1.,max(0., dist-0.1)));

    float cRb = 0.3 +  max(0., mix(1., 0.,max(0., dist-0.1)));
    float cRg = max(0., mix(1., 0.,max(0., dist-0.1)));
    float cRr = max(0., mix(1., 0.,max(0., dist-0.1)));

    if(abs(iMouse.z)>0.){
    cRg = pow(cRg, (iMouse.x/iResolution.x)+0.5);
    cRb = pow(cRb, (iMouse.y/iResolution.y)+0.3);
    }
    vec3 cR = vec3(cRr, cRg, cRb);
    fragColor = vec4(uv.x>0.5?cR:cL,1.0);
