// Shadertoy raymarched sphere with texture-based displacement
// Eye with transparent lens covering

#define MAX_STEPS 150
#define MAX_DIST 100.0
#define SURF_DIST 0.001

// Texture mapping and displacement parameters
#define UV_SCALE_X 0.4
#define UV_SCALE_Y 0.6
#define UV_OFFSET 0.5
#define DISPLACE_AMOUNT 0.02

// Flatten parameters
#define FLATTEN_ANGLE 0.76   // 45 degrees - outer edge of displacement area
#define FLATTEN_DEPTH 0.5        // Inner radius where displacement is full strength

#define LIGHTP vec3(1.5,1.5,-1.5)

// iris texture functions
float sdCateye(vec2 p, float height, float dilation)
{
    float vrad = height/2.;
    dilation = min(.999, max(0.01, dilation));
    float rad = (vrad)/dilation;
    float dist = sqrt((rad*rad)-(vrad*vrad));
    p = abs(p);
    vec2 center = vec2(-dist, 0.);
    float tdist = distance(p, center);
    float result = tdist - rad;
    vec2 nearest = center+(p-center)*rad/tdist;
    if(nearest.y>vrad){result=distance(p,vec2(0.,vrad));}
    return result;
}
vec3 contrast(vec3 color, float value) {return 0.5 + value * (color - 0.5);}
vec3 gmc(vec3 colour, float gamma) {return pow(colour, vec3(1. / gamma));}

vec4 eyeTexture( vec2 uv ){
    vec2 p = (uv*2.)-1.;
    p.x/=iResolution.y/iResolution.x;
    p*=1.3;
    float eHeight = 1.;
    float eDilation = 0.4*(sin(iTime)+1.3);
    if( iMouse.z>0.001 ) {eHeight = 1.0; eDilation = iMouse.x/iResolution.x/0.9;}
    eHeight*=1.5-(eDilation/6.);
	float dA = sdCateye( p, eHeight, 0.0);
    float dB = sdCateye( p, eHeight, eDilation);
    float d = dB-dA;
    d=d+abs(atanh((p.x*p.x)/1.3));
    if(p.x<0.){d=-d;}
    float cd = length(p*vec2(.85,1));
    float eb = distance(p, vec2(0.1, -0.6));
    float el = distance(p, vec2(0.3, -1.2));
    float et = distance(p, vec2(-.2, 0.6));
    float ee = max(et, eb);
    vec3 col = (dB>0.0) ? (texture(iChannel0, (p.xy+vec2(d,0))/6., 0.).rgb+0.2)/(0.7+dB): vec3(0);
    col = mix(col*col*col, col, min(1.,dB*10.));
    col = mix(vec3(0), col, min(1.,dB*25.));
    dB = dB*dB;
    col = contrast(col, 1.-(dB/5.));
    col = gmc(col, 0.4+dB);
    if(col.r<0.3-(dB/max(0.0001,eDilation*1.5))){col=vec3(0);}
    if(col.g<0.2-(dB/max(0.0001,eDilation*1.5))){col=vec3(0);}
    col = mix(col, vec3(0,0,0.1), max(0.,cd-.93 )*7.);
    col.g*=1.5;
    col = mix(col, vec3(1,1,1), min(1.,max(0.,cd-1.05 )*15.));
    return vec4(col, 1.);
}

// Eye sphere SDF (with flattening and displacement)
float sdEye(vec3 p, float r) {
    // Start with basic sphere
    float d = length(p) - r;

    // Flatten the negative z side by intersecting with a plane
    float cutDepth = r * cos(FLATTEN_ANGLE);
    float planeD = -p.z - cutDepth;
    d = max(d, planeD);

    // Apply texture displacement to the area
    float outerRadius = r * sin(FLATTEN_ANGLE);
    float innerRadius = FLATTEN_DEPTH;
    float distFromCenter = length(p.xy);

    if (p.z <= -cutDepth + 0.1 && distFromCenter <= outerRadius) {
        vec2 uv;
        uv.x = p.x * UV_SCALE_X + UV_OFFSET;
        uv.y = p.y * UV_SCALE_Y + UV_OFFSET;
        uv = clamp(uv, 0.0, 1.0);

        vec4 texCol = eyeTexture( uv);
        float darkness = 1.0 - (texCol.r * 0.299 + texCol.g * 0.587 + texCol.b * 0.114);

        float falloff = 1.0;
        if (distFromCenter > innerRadius) {
            falloff = 1.0 - smoothstep(innerRadius, outerRadius, distFromCenter);
        }

        float displaceAmount = darkness * DISPLACE_AMOUNT * falloff;
        d += displaceAmount;
    }

    return d;
}

// Lens SDF - simple sphere shell - Thin shell slightly outside eye
float sdLens(vec3 p, float r) {return abs(length(p) - r - 0.02) - 0.003; }

// Get normal
vec3 getEyeNormal(vec3 p) {
    float d = sdEye(p, 1.0);
    vec2 e = vec2(0.001, 0);

    vec3 n = d - vec3(
        sdEye(p - e.xyy, 1.0),
        sdEye(p - e.yxy, 1.0),
        sdEye(p - e.yyx, 1.0)
    );

    return normalize(n);
}

vec3 getLensNormal(vec3 p) {

    return normalize(p); // For a sphere centered at origin

    // keep this in case we need it again
    float d = sdLens(p, 1.0);
    vec2 e = vec2(0.001, 0);
    vec3 n = d - vec3(
        sdLens(p - e.xyy, 1.0),
        sdLens(p - e.yxy, 1.0),
        sdLens(p - e.yyx, 1.0)
    );
    return normalize(n);

}

// Separate raymarching for each object
float rayMarchEye(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = sdEye(p, 1.0);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}

float rayMarchLens(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = sdLens(p, 1.0);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}

// Get texture color at hit point
vec3 getTextureColor(vec3 p) {
    if (p.z < 0.0) {
        vec2 uv;
        uv.x = p.x * UV_SCALE_X + UV_OFFSET;
        uv.y = p.y * UV_SCALE_Y + UV_OFFSET;
        uv = clamp(uv, 0.0, 1.0);

        return eyeTexture( uv).rgb;
    }
    return vec3(0.8, 0.8, 0.8);
}

// Eye lighting
vec3 getEyeLight(vec3 p, vec3 rd) {
    vec3 l = normalize(LIGHTP - p);
    vec3 n = getEyeNormal(p);
    vec3 v = -rd;
    vec3 r = reflect(-l, n);

    vec3 albedo = getTextureColor(p);

    float dif = clamp(dot(n, l), 0.0, 1.0);
    float spec = pow(max(dot(r, v), 0.0), 32.0);

    vec3 ambient = albedo * 0.1;
    vec3 diffuse = albedo * dif *1.1;
    vec3 specular = vec3(1.0) * spec * 0.01;

    return ambient + diffuse + specular;
}

// Lens lighting
vec3 getLensLight(vec3 p, vec3 rd) {

    vec3 l = normalize(LIGHTP - p);
    vec3 n = getLensNormal(p);
    vec3 v = -rd;
    vec3 r = reflect(-l, n);

    float spec = pow(max(dot(r, v), 0.0), 32.0);
    float fresnel = pow(1.0 - abs(dot(v, n)), 1.0);

    // Lens base color - slightly bluish/clear glass tint
    vec3 lensColor = vec3(0.95, 0.97, 1.0); // Slight blue tint

    // Add some base brightness to the lens
    vec3 baseGlass = lensColor * 0.2; // Base glass brightness
    vec3 specular = vec3(1.) * spec * 50.0; // Increased from your settings
    vec3 fresnelColor = lensColor * fresnel *0.2;

    return baseGlass + specular + fresnelColor;
}

// Refraction using Snell's law
vec3 refract_ray(vec3 incident, vec3 normal, float eta) {
    float cosI = -dot(normal, incident);
    float sinT2 = eta * eta * (1.0 - cosI * cosI);
    if (sinT2 > 1.0) return vec3(0.0); // Total internal reflection
    float cosT = sqrt(1.0 - sinT2);
    return eta * incident + (eta * cosI - cosT) * normal;
}

// Enhanced lens rendering with refraction
vec4 renderWithRefraction(vec3 ro, vec3 rd) {
    vec3 col = vec3(0.04, 0.04, 0.05); // Background
    float nearestD = MAX_DIST;
    // First, check if we hit the lens
    float lensD = rayMarchLens(ro, rd);
    nearestD = min(nearestD, lensD);
    if(lensD < MAX_DIST) {
        vec3 lensP = ro + rd * lensD;
        vec3 lensN = getLensNormal(lensP);

        // Calculate refraction
        float eta = 1.0 / 1.1; // Air to glass (1.52 is typical for eye lens)
        vec3 refractedRay = refract_ray(rd, lensN, eta);

        if(length(refractedRay) > 0.0) {
            // Continue raymarching with refracted ray
            vec3 newRo = lensP + refractedRay * 0.01; // Small offset to avoid self-intersection

            // Check if refracted ray hits the eye
            float eyeD = rayMarchEye(newRo, refractedRay);

            if(eyeD < MAX_DIST) {
                vec3 eyeP = newRo + refractedRay * eyeD;
                vec3 eyeCol = getEyeLight(eyeP, refractedRay);

                // Apply some color shift/distortion based on refraction angle
                float refractionStrength = length(rd - refractedRay);
                eyeCol = mix(eyeCol, eyeCol.gbr, refractionStrength * 0.1); // Slight chromatic aberration

                col = eyeCol;
            }
        }

        // Add lens surface effects
        vec3 lensCol = getLensLight(lensP, rd);
        float fresnel = pow(1.0 - abs(dot(-rd, lensN)), 2.);
        col = mix(col, lensCol, fresnel * 0.2); // Blend lens surface

    } else {
        // No lens hit, render eye directly
        float eyeD = rayMarchEye(ro, rd);
        nearestD = min(nearestD, eyeD);
        if(eyeD < MAX_DIST) {
            vec3 eyeP = ro + rd * eyeD;
            col = getEyeLight(eyeP, rd);
        }
    }

    return vec4(col, nearestD);
}


void mainMS(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;

    // Camera
    vec3 ro = vec3(0, 0, -3);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    // Mouse rotation
    float angleY = (m.x - 0.5) * 6.28;
    float angleX = (m.y - 0.5) * 3.14;

    mat3 rotY = mat3(
        cos(angleY), 0, sin(angleY),
        0, 1, 0,
        -sin(angleY), 0, cos(angleY)
    );

    mat3 rotX = mat3(
        1, 0, 0,
        0, cos(angleX), -sin(angleX),
        0, sin(angleX), cos(angleX)
    );

    ro = rotY * rotX * ro;
    rd = rotY * rotX * rd;

    // Render
    // Replace the existing render section with:
    vec4 colPND = renderWithRefraction(ro, rd);
    vec3 col = colPND.rgb;

    // Apply fog

    float nearestD = colPND.a;
    if(nearestD < MAX_DIST) {
        col = mix(col, vec3(0.1, 0.1, 0.15), 1.0 - exp(-0.0005 * nearestD * nearestD));
    }

    // Gamma correction
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

// AA multisampling
void mainImage(out vec4 o, vec2 u) 
{ 
    float s = 2., k; 
    vec2 j = vec2(.5); 
    o = vec4(0); 
    vec4 c; 
    mainMS(c, u); 
    for (k = s; k-- > .5; ) { 
        mainMS(c, u + j - .5); 
        o += c; 
        j = fract(j + vec2(.755, .57).yx); 
    };o /= s;o.a=1.;
}

