// Calculate focused light intensity for iris - like a torch beam at angle
float calculateLensFocusedLight(vec3 irisPoint, vec3 lightPos) {
    // Eye forward direction (where lens is pointing)
    vec3 eyeForward = vec3(0.0, 0.0, -1.0);

    // Light direction from eye center
    vec3 lightDir = normalize(lightPos);

    // Angle between eye forward and light
    float lightAngle = acos(clamp(dot(eyeForward, lightDir), -1.0, 1.0));

    // Cut off light if angle is too large - with smooth transition
    if (lightAngle > LENS_CUTOFF_ANGLE) {
        return 0.0;
    }

    // Smooth cutoff transition
    float angleFade = 1.0 - smoothstep(LENS_CUTOFF_ANGLE * 0.8, LENS_CUTOFF_ANGLE, lightAngle);

    float normalizedAngle = lightAngle / LENS_CUTOFF_ANGLE;

    // Calculate where the focused light beam hits the iris
    // The beam center moves opposite to light direction (projected onto iris plane)
    vec2 beamCenter = -lightDir.xy * normalizedAngle * 0.5;

    // Distance from iris point to beam center
    vec2 irisPos2D = irisPoint.xy;
    vec2 toBeam = irisPos2D - beamCenter;

    float na2 = pow(normalizedAngle, 3.0);
    // Beam gets more elongated and focused as angle increases
    float stretch = 1.0 + normalizedAngle * 3.0; // Elongation factor
    float beamWidth = 0.4 * (1.0 - na2 * 0.7); // Gets narrower with angle

    // Apply stretching along the light direction
    vec2 lightDir2D = normalize(lightDir.xy + vec2(0.001)); // Avoid division by zero
    vec2 perpDir = vec2(-lightDir2D.y, lightDir2D.x);

    // Transform to stretched coordinates
    float alongLight = dot(toBeam, lightDir2D) / stretch;
    float perpToLight = dot(toBeam, perpDir);

    float distToBeamAxis = sqrt(alongLight * alongLight + perpToLight * perpToLight);

    // Gaussian falloff for the focused beam
    float beamIntensity = exp(-distToBeamAxis * distToBeamAxis / (beamWidth * beamWidth));

    // Increase intensity as angle increases (lens focusing effect)
    float focusMultiplier = 1.0 + normalizedAngle * LENS_FOCUS_STRENGTH;

    // Smooth blend between uniform and focused lighting based on angle
    // At angle 0: uniform lighting (intensity = 1.0)
    // At higher angles: focused beam lighting
    float focusAmount = smoothstep(0.0, 0.3, normalizedAngle);
    float uniformLighting = 1.0 + normalizedAngle * LENS_FOCUS_STRENGTH * 0.2;
    float focusedLighting = beamIntensity * focusMultiplier;

    float finalIntensity = mix(uniformLighting, focusedLighting, focusAmount);

    return finalIntensity * angleFade * BASE_BRIGHTNESS;
}

vec3 getEyeLight(vec3 p, vec3 rd) {
    vec3 lightInEyeSpace = g_rotation * LIGHTP;
    vec3 l = normalize(lightInEyeSpace - p);
    vec3 n = getEyeNormal(p);
    vec3 v = -rd;
    vec3 r = reflect(-l, n);
    vec3 albedo = getTextureColor(p);

    float dif = clamp(dot(n, l), 0.0, 1.0);

    // Check if we're on the iris
    float cutDepth = cos(FLATTEN_ANGLE);
    float outerRadius = sin(FLATTEN_ANGLE);
    bool isIris = p.z <= -cutDepth + 0.1 && length(p.xy) <= outerRadius;

    // Apply lens focusing effect only to iris
    if (isIris) {
        float lensFocus = calculateLensFocusedLight(p, lightInEyeSpace);
        dif *= lensFocus;
    }

    float spec = pow(max(dot(r, v), 0.0), 16.0);
    vec3 ambient = albedo * 0.15;
    vec3 diffuse = albedo * dif * 0.5;
    vec3 specular = vec3(1.0) * spec * albedo.g * albedo.r *albedo.b;

    return ambient + diffuse + specular;
}


vec3 getLensLight(vec3 p, vec3 rd) {
    vec3 lightInEyeSpace = g_rotation * LIGHTP;

    vec3 l = normalize(lightInEyeSpace - p);
    vec3 n = getLensNormal(p);
    vec3 v = -rd;
    vec3 r = reflect(-l, n);
    float spec = pow(max(dot(r, v), 0.0), 32.0);
    float fresnel = pow(1.0 - abs(dot(v, n)), 1.0);
    vec3 lensColor = vec3(0.95, 0.97, 1.0); 
    vec3 baseGlass = lensColor * 0.2; 
    vec3 specular = vec3(1.) * spec * 50.0; 
    vec3 fresnelColor = lensColor * fresnel *0.2;

    return baseGlass + specular + fresnelColor;
}

/* Refraction using Snell's law - not needed because refract()
vec3 refract_ray(vec3 incident, vec3 normal, float eta) {
    float cosI = -dot(normal, incident);
    float sinT2 = eta * eta * (1.0 - cosI * cosI);
    if (sinT2 > 1.0) return vec3(0.0); // Total internal reflection
    float cosT = sqrt(1.0 - sinT2);
    return eta * incident + (eta * cosI - cosT) * normal;
}
*/

vec4 renderWithRefraction(vec3 ro, vec3 rd) {
    vec3 col = vec3(0.04, 0.04, 0.05);
    float nearestD = MAX_DIST;
    // First, check if we hit the lens
    float lensD = rayMarchLens(ro, rd);
    nearestD = min(nearestD, lensD);
    if(lensD < MAX_DIST) {
        vec3 lensP = ro + rd * lensD;
        vec3 lensN = getLensNormal(lensP);

        // Calculate refraction
        float eta = 1.0 / REFRACT;
        vec3 refractedRay = refract(rd, lensN, eta);

        if(length(refractedRay) > 0.0) {
            // Continue raymarching with refracted ray
            vec3 newRo = lensP + refractedRay * 0.01; // Small offset to avoid self-intersection
            // Check if refracted ray hits the eye
            float eyeD = rayMarchEye(newRo, refractedRay);

            if(eyeD < MAX_DIST) {
                vec3 eyeP = newRo + refractedRay * eyeD;
                vec3 eyeCol = getEyeLight(eyeP, refractedRay);

                // Apply slight chromatic aberration based on refraction angle :)
                float refractionStrength = length(rd - refractedRay);
                eyeCol = mix(eyeCol, eyeCol.gbr, refractionStrength * 0.1);

                col = eyeCol;
            }
        }

        // Add lens surface effects
        vec3 lensCol = getLensLight(lensP, rd);
        float fresnel = pow(1.0 - abs(dot(-rd, lensN)), 2.);
        col = mix(col, lensCol, fresnel * 0.2); // Blend lens surface

    } else {
        // No lens hit, render eye directly
        float eyeD = rayMarchEye(ro, rd);
        nearestD = min(nearestD, eyeD);
        if(eyeD < MAX_DIST) {
            vec3 eyeP = ro + rd * eyeD;
            col = getEyeLight(eyeP, rd);
        }
    }

    return vec4(col, nearestD);
}


void mainMS(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 m = vec2(0.5);

    vec2 pa = vec2 (sin (floor( iTime ) * 57.678)/10., sin (floor( iTime ) * 97.678)/10.);
    vec2 pb = vec2 (sin (floor( iTime+1. ) * 57.678)/10., sin (floor( iTime+1. ) * 97.678)/10.);

    vec2 pm = mix (pa, pb, pow( (  max(0.,fract(iTime)-0.9)  *10.), 4.));

    m+= pm;

    if(iMouse.z>0.001){m = iMouse.xy / iResolution.xy;} 

    vec3 ro = vec3(0, 0, -3);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float angleY = (m.x - 0.5) * -6.28;
    float angleX = (m.y - 0.5) * 3.14;

    mat3 rotY = mat3(
        cos(angleY), 0, sin(angleY),
        0, 1, 0,
        -sin(angleY), 0, cos(angleY)
    );

    mat3 rotX = mat3(
        1, 0, 0,
        0, cos(angleX), -sin(angleX),
        0, sin(angleX), cos(angleX)
    );

    g_rotation = rotY * rotX;

    g_lightIntensity = calculateIrisLightIntensity(g_rotation);

    ro = g_rotation * ro;
    rd = g_rotation * rd;

    vec4 colPND = renderWithRefraction(ro, rd);
    vec3 col = colPND.rgb;

    float nearestD = colPND.a;
    if(nearestD < MAX_DIST) {
        col = mix(col, vec3(0.1, 0.1, 0.15), 1.0 - exp(-0.0005 * nearestD * nearestD));
    }

   col = pow(col, vec3(0.4545));


    float getli = texture(iChannel1, vec2(0), 0.).r;
    r_lightIntensity = (g_lightIntensity+getli+getli)/3.;

    if(fragCoord.x<2. && fragCoord.y<2.) {col.r = r_lightIntensity;}
    fragColor = (texture(iChannel1, fragCoord/iResolution.xy, 0.)*MBLUR + vec4(col, 1.0))/(1.+MBLUR);
}

void mainImage(out vec4 o, vec2 u) 
{ 
    float s = AA, k; 
    vec2 j = vec2(.5); 
    o = vec4(0); 
    vec4 c; 
    mainMS(c, u); 
    for (k = s; k-- > .5; ) { 
        mainMS(c, u + j - .5); 
        o += c; 
        j = fract(j + vec2(.755, .57).yx); 
    };o /= s;o.a=1.;
}

// Fork of "Cat Eye SDF" by Cotterzz. https://shadertoy.com/view/tXKXzV
// 2025-08-17 19:10:26

float sdCateye(vec2 p, float height, float dilation)
{
    float vrad = height/2.;
    dilation = min(.999, max(0.01, dilation));
    float rad = (vrad)/dilation;
    float dist = sqrt((rad*rad)-(vrad*vrad));
    p = abs(p);
    vec2 center = vec2(-dist, 0.);
    float tdist = distance(p, center);
    float result = tdist - rad;
    vec2 nearest = center+(p-center)*rad/tdist;
    if(nearest.y>vrad){result=distance(p,vec2(0.,vrad));}

    return result;
}
vec3 contrast(vec3 color, float value) {
  return 0.5 + value * (color - 0.5);
}
vec3 gmc(vec3 colour, float gamma) {
  return pow(colour, vec3(1. / gamma));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    p*=1.3;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float eHeight = 1.;
    float eDilation = 0.4*(sin(iTime)+1.3);

    if( iMouse.z>0.001 ) {eHeight = 1.0;//iMouse.y/iResolution.y;
        eDilation = iMouse.x/iResolution.x/0.9;}
    eHeight*=1.5-(eDilation/6.);
	float dA = sdCateye( p, eHeight, 0.0);
    float dB = sdCateye( p, eHeight, eDilation);
    float d = dB-dA;
    if(p.x<0.){d=-d;}
    float cd = length(p*vec2(.9,1));
    d = mix(d,0. , min(1.,max(0.,(cd-.8)*5.)));
    float eb = distance(p, vec2(0.1, -0.6));
    float el = distance(p, vec2(0.3, -1.2));
    float et = distance(p, vec2(-.2, 0.6));
    float ee = max(et, eb);
    vec3 col = (dB>0.0) ? (texture(iChannel0, (p.xy+vec2(d,0))/6., 0.).rgb+0.2)/(0.7+dB): vec3(0);
    col = mix(col*col*col, col, min(1.,dB*10.));
    col = mix(vec3(0), col, min(1.,dB*45.));
    dB = dB*dB;
    col = contrast(col, 1.-(dB/5.));
    col = gmc(col, 0.4+dB);
    if(col.r<0.1-(dB/max(0.0001,eDilation*1.5))){col=vec3(0);}
    if(col.g<0.1-(dB/max(0.0001,eDilation*1.5))){col=vec3(0);}
    col = mix(col, vec3(0), max(0.,cd-.93 )*7.);
    col = mix(col, vec3(0.9,1,1.0), min(1.,max(0.,cd-1.05 )*15.));
    col = mix(col, col*vec3(.01,.015,.01), min(1.,max(0.,el-1.6 )*2.));
    float ref = texture(iChannel1, (p.xy + vec2(-1.5,1))*vec2(-0.5,0.5), 0.).b;
    ref=0.4+ref/2.;
    if(ee<1.53){col = col+max(0.,(ref-0.8)*15.);}
    col = mix(col, vec3(0.1,.11,0.1), min(1.,max(0.,ee-1.6 )*15.));
    col.g*=1.5;
	fragColor = vec4(col,1.);
}

// Cat eye SDF.
// It's almost identical to a vesica, but with different params/constraints
// so it's suited for a specific use case.

// See also: https://www.shadertoy.com/view/XtVfRW and https://www.shadertoy.com/view/XlVfz1
// For similar/alternative SDF   

float sdCateye(vec2 p, float height, float dilation)
{
    // height is actual height of pupil, vrad is half height
    float vrad = height/2.;
    // dilation is maxed at 1, a regular circle, zero would not be visible so it's limited to 0.001
    dilation = min(.999, max(0.01, dilation));
    // radius is half height when circle is centred and dilation is 1, and gets bigger as dilation gets smaller
    float rad = (vrad)/dilation;
    // dist is distance between circle centre and eye centre
    float dist = sqrt((rad*rad)-(vrad*vrad));
    // same as IQ's vesica, - we only need to calculate a quarter because of symmetry
    p = abs(p);
    // now we calculate distance from circle centre
    vec2 center = vec2(-dist, 0.);
    float tdist = distance(p, center);
    // and subtract radius for distance from surface
    float result = tdist - rad;

    // This is for correct distance from the sharp top and bottom corners
    // Comment these two lines for pointy SDF shape.
    vec2 nearest = center+(p-center)*rad/tdist;
    if(nearest.y>vrad){result=distance(p,vec2(0.,vrad));}

    return result;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float eHeight = 1.;
    float eDilation = 0.4*(sin(iTime)+1.3);

    //if( iMouse.z>0.001 ) {eHeight = iMouse.y/iResolution.y; eDilation = iMouse.x/iResolution.x;}

	float d = sdCateye( p, eHeight, eDilation);

    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
	col *= 1.0 - exp(-6.0*abs(d)); col *= 0.8 + 0.2*cos(110.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(d)) );

    if( iMouse.z>0.001 ){ d = sdCateye( m, eHeight, eDilation);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));}

	fragColor = vec4(col,1.);
}

const float MULTISAMPLING =3.0; // 1.0 is no multi sampling, 4.0 is 2x2 etc 

const float THICKNESS = 0.03;
const float SPACING = 0.04;
const float INNER_RADIUS = 0.2;
const float OUTER_RADIUS = 1.1;
const float MAX_DROOP = 0.4;
const float CURVE = 0.2; // Curve inwards for droop
const int INVERSE_DROOP_ITERATIONS = 4;

const float CURL_A = 0.0; // Curl Amplitude, typical values from 0.05 to 0.3
const float CURL_F = 10.0; // Curl Frequency, typical values from 10 to 100

const float UNEVEN = 0.0;


struct HairData {
    vec3 straightPos;
    vec2 spherical;
    vec2 gridCell;
    float radialDist;
    float normalizedRadius;
};


float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

vec2 hash2(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)),
             dot(p, vec2(269.5, 183.3)));
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}

float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.78));
    p += dot(p, p + 34.56);
    return fract(p.x * p.y);
}

vec2 cartesianToSpherical(vec3 p) {
    float r = length(p);
    if(r < 0.001) return vec2(0.0);
    vec3 n = p / r;
    return vec2(acos(clamp(n.y, -1.0, 1.0)), atan(n.z, n.x));
}

vec3 applyDroop(vec3 p) {
    float radialDist = length(p);
    float normalizedRadius = (radialDist - INNER_RADIUS) / (OUTER_RADIUS - INNER_RADIUS);

    float droopFactor = normalizedRadius * normalizedRadius;
    vec3 droopedPos = p;
    droopedPos.y -= droopFactor * MAX_DROOP;

    // Add slight inward curl
    vec3 toCenter = -normalize(vec3(p.x, 0.0, p.z));
    droopedPos += toCenter * droopFactor * CURVE;

    return droopedPos;
}

// Compute straight position from drooped position
vec3 computeStraightPos(vec3 p) {
    vec3 straightPos = p;
    for(int i = 0; i < INVERSE_DROOP_ITERATIONS; i++) {
        vec3 drooped = applyDroop(straightPos);
        straightPos += (p - drooped) * 0.7;
    }
    return straightPos;
}

HairData getHairData(vec3 p) {
    HairData data;
    data.straightPos = computeStraightPos(p);
    data.spherical = cartesianToSpherical(data.straightPos);
    data.radialDist = length(data.straightPos);
    data.normalizedRadius = (data.radialDist - INNER_RADIUS) / (OUTER_RADIUS - INNER_RADIUS);

    // Store grid indices for hair ID (without randomization)
    float lat = data.spherical.x;
    float lon = data.spherical.y;
    float gridSpacing = 0.01; // Default value
    float lonSpacing = gridSpacing / max(sin(lat), 0.1);

    data.gridCell = vec2(
        round(lat / gridSpacing),
        round(lon / lonSpacing)
    );

    return data;
}


float sdRadialLineOptimized(vec3 p, float gridSpacing, float thickness, out HairData hairData) {
    hairData = getHairData(p);
    float lat = hairData.spherical.x;
    float lon = hairData.spherical.y;
    if(CURL_A>0.0){ lon += sin(length(p)*CURL_F)*CURL_A; }
    float lonSpacing = gridSpacing / max(sin(lat), 0.1);
    vec2 gridPos = vec2(lat, lon);
    vec2 gridIndex = vec2(
        round(gridPos.x / gridSpacing),
        round(gridPos.y / lonSpacing)
    );


    vec2 randomOffset = hash2(gridIndex) * 0.4;// = 40% of grid spacing
    vec2 gridCell = vec2(
        gridIndex.x * gridSpacing + randomOffset.x * gridSpacing,
        gridIndex.y * lonSpacing + randomOffset.y * lonSpacing
    );

    vec2 toLine = gridPos - gridCell;
    toLine.y *= sin(lat);
    float distToLine = length(toLine);
        // Increase the cutoff distance since hairs can now be offset
    if(distToLine > gridSpacing * 1.0) return 1e10;
    float currentThickness;
    if(UNEVEN>0.0){
    float hairLengthRandom = hash(gridIndex + vec2(42.0, 17.0));
    float minLength = 1.0/(1.0+UNEVEN);
    float maxLength = 1.0*(1.0+UNEVEN);
    float hairLengthFactor = mix(minLength, maxLength, hairLengthRandom);

    // Check if we're beyond this hair's length - if so, early exit
    float adjustedRadius = hairData.normalizedRadius / hairLengthFactor;
    if(adjustedRadius > 1.0) {
        return 1e10; // Hair doesn't exist here at all
    }
    // Calculate taper
    float baseTaperFactor = 1.0 - hairData.normalizedRadius;
    float lengthTaperFactor = 1.0 - adjustedRadius;
    float finalTaperFactor = baseTaperFactor * lengthTaperFactor;

    float thicknessVariation = 0.4 + 0.8 * hash(gridIndex);
    currentThickness = thickness * finalTaperFactor * thicknessVariation;

    // Ensure we never have negative or too-small thickness
    if(currentThickness < 0.0001) {
        return 1e10; // Hair is too thin to render
    }
    } else {
        float taperFactor = 1.0 - hairData.normalizedRadius;
    // Add slight thickness variation per hair
    float thicknessVariation = 0.4 + 0.8 * hash(gridIndex);
    currentThickness = thickness * taperFactor * thicknessVariation;

    }
    return distToLine - currentThickness;

}



// Optimized raymarch function
vec4 raymarchWithLines(vec3 ro, vec3 rd, float lineSpacing, float lineThickness) {
    float t = 0.0;
    HairData hairData; // Reuse this structure

    for(int i = 0; i < 200; i++) {
        vec3 p = ro + rd * t;
        float distFromCenter = length(p);


        float d;
        bool betweenSpheres = distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS;

        if(betweenSpheres) {
            float innerSphere = sdSphere(p, INNER_RADIUS);
            float lines = sdRadialLineOptimized(p, lineSpacing, lineThickness, hairData);
            d = min(innerSphere, lines);
        } else {
            d = sdSphere(p, INNER_RADIUS);
        }

        if(d < 0.0005) {
            float objectId = 0.0;
            float opacity = 1.0;

            if(betweenSpheres) {
                float innerDist = sdSphere(p, INNER_RADIUS);
                float lineDist = hairData.radialDist > 0.0 ? 
                    sdRadialLineOptimized(p, lineSpacing, lineThickness, hairData) : 1e10;



                if(lineDist < innerDist) {
                    objectId = 1.0;
                    opacity = 1.0 - hairData.normalizedRadius * 0.8;
                }
            }

            return vec4(t, objectId, hash(hairData.gridCell), opacity);
        }

        t += d * 0.8;
        if(t > 20.0) break;
    }

    return vec4(-1.0, -1.0, 0.0, 0.0);
}

// Ray-sphere intersection (unchanged)
vec2 raySphere(vec3 ro, vec3 rd, float r) {
    float b = dot(ro, rd);
    float c = dot(ro, ro) - r*r;
    float h = b*b - c;
    if(h < 0.0) return vec2(-1.0);
    h = sqrt(h);
    return vec2(-b - h, -b + h);
}

// Unified map function for shadow marching
float map(vec3 p, float lineSpacing, float lineThickness) {
    float distFromCenter = length(p);

    if(distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) {
        HairData hairData;
        float innerSphere = sdSphere(p, INNER_RADIUS);
        float lines = sdRadialLineOptimized(p, lineSpacing, lineThickness, hairData);
        return min(innerSphere, lines);
    } else {
        return sdSphere(p, INNER_RADIUS);
    }
}

// Modified soft shadow function
float calcSoftshadow(in vec3 ro, in vec3 rd, float tmin, float tmax, float lineSpacing, float lineThickness, const float k)
{
    float res = 1.0;
    float t = tmin;
    for(int i = 0; i < 6; i++)
    {
        float h = map(ro + rd*t, lineSpacing, lineThickness);
        res = min(res, k*h/t);
        t += clamp(h, 0.02, 0.20);
        if(res < 0.005 || t > tmax) break;
    }
    return clamp(res, 0.0, 1.0);
}

// Simplified normal calculation
vec3 calcNormal(vec3 p, float objectId, float lineSpacing, float lineThickness) {
    if(objectId < 0.5) { // Inner sphere
        return normalize(p);
    }

    // Hair normal using finite differences
    vec2 e = vec2(0.001, 0.0);
    float base = map(p, lineSpacing, lineThickness);
    return normalize(vec3(
        map(p + e.xyy, lineSpacing, lineThickness) - base,
        map(p + e.yxy, lineSpacing, lineThickness) - base,
        map(p + e.yyx, lineSpacing, lineThickness) - base
    ));
}
void mainImage0(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera setup
    float time = iTime * 0.5;
    float radius = 3.0;
    float height = sin(time * 0.7) * 0.5;

    vec3 ro = vec3(radius * cos(time), height, radius * sin(time));
    vec3 target = vec3(0.0);
    vec3 forward = normalize(target - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    vec3 col = vec3(0.6, 0.6, 0.6);

    // Mouse-controlled parameters with better defaults
    float lineSpacing = SPACING;
    float lineThickness = THICKNESS;

    vec2 outerHit = raySphere(ro, rd, OUTER_RADIUS);
           float lrad = 2.0;
           float lheight = 1.;
           vec3 lightPos = vec3(lrad, lheight, lrad);//vec3(lrad * cos(time), lheight, lrad * sin(time));
    if(outerHit.x > 0.0) {
        vec3 pEnter = ro + rd * outerHit.x;
        vec3 roInner = pEnter + rd * 0.001;
        vec4 hit = raymarchWithLines(roInner, rd, lineSpacing, lineThickness);

if(hit.x > 0.0) {
    vec3 pHit = roInner + rd * hit.x;
    vec3 nHit = calcNormal(pHit, hit.y, lineSpacing, lineThickness);

    vec3 lightDir = normalize(lightPos - pHit);
    float diff = max(dot(nHit, lightDir), 0.0);

    // Calculate shadow
    float shadow = calcSoftshadow(pHit + nHit*0.001, lightDir, 0.001, length(lightPos - pHit), lineSpacing, lineThickness, 2.0);
    diff = diff*shadow;

    float ambient = 0.4;

            vec3 hitCol;
            if(hit.y > 0.5) {
                // Hair color selection
                float hairRandom = hit.z;
                vec3 hairColors[4] = vec3[4](
                    vec3(0.95, 0.8, 0.4),  // Golden
                    vec3(0.8, 0.5, 0.3),   // Brown
                    vec3(0.9, 0.4, 0.4),   // Reddish
                    vec3(0.5, 0.7, 0.9)    // Blue
                );

                int colorIndex = int(hairRandom * 4.0);
                vec3 hairBaseCol = hairColors[min(colorIndex, 3)];

                hitCol = hairBaseCol* (diff + ambient);

                // Specular highlight
                vec3 viewDir = -rd;
                vec3 halfDir = normalize(lightDir + viewDir);
                float spec = pow(max(dot(nHit, halfDir), 0.0), 16.0);
                hitCol += spec*.7;

                col = mix(col, hitCol, hit.w*1.5);
            } else {
                // Inner sphere
                col = vec3(.9,.8,.8) * (diff + ambient);
            }
        }
    }

    fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 o, vec2 u) 
{ 
