mat2 rot2D(float a) { float s = sin(a), c = cos(a);return mat2(c, -s, s, c); }

mat3 rotX(float a) { float s = sin(a), c = cos(a); return mat3(1, 0, 0, 0, c, -s, 0, s, c); }

mat3 rotY(float a) { float s = sin(a), c = cos(a); return mat3(c, 0, s, 0, 1, 0, -s, 0, c); }

mat3 rotZ(float a) { float s = sin(a), c = cos(a); return mat3(c, -s, 0, s, c, 0, 0, 0, 1); }

vec3 foldPlane(vec3 p, vec3 normal, float offset) {

   float d = dot(p, normal) - offset;
   if (d < 0.0) p -= 2.0 * d * normal;
   return p;
}

vec3 foldBox(vec3 p, vec3 size) { return clamp(p, -size, size) * 2.0 - p; }

vec3 foldSphere(vec3 p, float minRadius, float maxRadius) {
   float r2 = dot(p, p);
   if (r2 < minRadius * minRadius) {
       p *= maxRadius * maxRadius / (minRadius * minRadius);
   } else if (r2 < maxRadius * maxRadius) {
       p *= maxRadius * maxRadius / r2;
   }
   return p;
}

vec3 foldKaleidoscopic(vec3 p, float angle) {
   float a = atan(p.y, p.x);
   float r = length(p.xy);
   a = mod(a + angle, 2.0 * angle) - angle;
   p.xy = vec2(cos(a), sin(a)) * r;
   return p;
}

vec3 foldKaleidoscopic2(vec3 p, float angle) {
   float a = atan(p.z, p.x);
   float r = length(p.xz);
   a = mod(a + angle, 2.0 * angle) - angle;
   p.xz = vec2(cos(a), sin(a)) * r;
   return p;
}


vec3 fold(vec3 p, int ftype) {
    float phi = (1.0 + sqrt(5.0)) / 2.0;
    switch (ftype){
        case 0: return abs(p);
        case 1: return sqrt(p*p + 0.01);
        case 2:  return sqrt(p*p + 0.02);
        case 3:  return sqrt(p*p + 0.04);
        case 4:  return sqrt(p*p + 0.08);
        case 5: p.x = abs(p.x); return p;
        case 6: p.y = abs(p.y); return p;
        case 7: p.z = abs(p.z); return p;
        case 8: p.xy = abs(p.xy); return p;
        case 9: p.xz = abs(p.xz); return p;
        case 10: p.yz = abs(p.yz); return p;
        case 11: //p = abs(p); //octahedral
   if (p.x - p.y<0.0) p.xy = p.yx;
   if (p.x - p.z<0.0) p.xz = p.zx;
   if (p.y - p.z<0.0) p.yz = p.zy;
   return p;
        case 12://p = abs(p);
        if (p.x < p.y) p.xy = p.yx; //menger
        if (p.x < p.y) p.xz = p.zx;
        if (p.y < p.z) p.yz = p.zy;
   return p;
   case 13: //dodeca

   vec3 n = normalize(vec3(0.0, 1.0, phi));
   //p = abs(p);
   p -= 2.0 * min(0.0, dot(p, n)) * n;
   n = normalize(vec3(phi, 0.0, 1.0));
   p -= 2.0 * min(0.0, dot(p, n)) * n;
   n = normalize(vec3(1.0, phi, 0.0));
   p -= 2.0 * min(0.0, dot(p, n)) * n;
   return p;
    case 14: //icosa

   vec3 n1 = normalize(vec3(1.0, phi, 0.0));
   vec3 n2 = normalize(vec3(0.0, 1.0, phi));
   vec3 n3 = normalize(vec3(phi, 0.0, 1.0));
   //p = abs(p);
   if (dot(p, n1) < 0.0) p -= 2.0 * dot(p, n1) * n1;
   if (dot(p, n2) < 0.0) p -= 2.0 * dot(p, n2) * n2;
   if (dot(p, n3) < 0.0) p -= 2.0 * dot(p, n3) * n3;
   return p;
    case 15: //sierp
       if (p.x + p.y < 0.0) p.xy = -p.yx;
   if (p.x + p.z < 0.0) p.xz = -p.zx;
   if (p.y + p.z < 0.0) p.yz = -p.zy;
   return p;

    }
}

