// Volumetric raymarch - samples density throughout the volume
vec4 raymarch_volumetric(vec3 ro, vec3 rd) {
    float t = 0.0;
    vec3 color = vec3(0);
    float alpha = 0.0;

    float stepSize = 0.1;


    for (int i = 0; i < 650; i++) {
        if (alpha > 0.99) break;

        vec3 p = ro + rd * t;
        float d = map(p);
        float density = max(0.0, -d * 10.0); 

        // Alternative: soft density falloff
       density += exp(-max(d, 0.0) * 100.0) * 0.1;

        if (density > 0.01) {

              vec3 sampleCol = vec3(
        0.5 + 0.5 * sin(orbitTrap.x * 3.0),
        0.5 + 0.5 * sin(orbitTrap.y * 3.0 + 2.0),
        0.5 + 0.5 * sin(orbitTrap.z * 3.0 + 4.0)
    );
            // Accumulate color and alpha
            float sampleAlpha = density * stepSize;
            color += sampleCol * sampleAlpha * (1.0 - alpha);
            alpha += sampleAlpha * (1.0 - alpha);
        }

        t += stepSize;
        if (t > MAX_DIST) break;
    }

    return vec4(color, alpha)*alpha*alpha*alpha;
}
