float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);
        if (abs(d) < SURF_DIST) break;
        if (t > MAX_DIST) break;
        t += d * STEP_MUL;
    }
    return t;
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.0003, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

float getAO(vec3 p, vec3 n) {
    float ao = 0.0;
    float w = 1.0;
    for (int i = 1; i <= 5; i++) {
        float d = float(i) * 0.08;
        ao += w * (d - map(p + n * d));
        w *= 0.5;
    }
    return 1.0 - clamp(ao * 2.5, 0.0, 1.0);
}

float getShadow(vec3 ro, vec3 rd, float tmax) {
    float res = 1.0;
    float t = 0.02;
    for (int i = 0; i < 64; i++) {
        float d = map(ro + rd * t);
        if (d < 0.0005) return 0.0;
        res = min(res, 10.0 * d / t);
        t += clamp(d, 0.01, 0.15);
        if (t > tmax) break;
    }
    return clamp(res, 0.0, 1.0);
}

vec3 shade(vec3 p, vec3 rd, float t) {


        vec3 n = getNormal(p);

        // Lighting
        vec3 lightDir = normalize(vec3(1.0, 2.0, 1.5));
        vec3 lightDir2 = normalize(vec3(-1.0, 0.5, -1.0));
        vec3 viewDir = -rd;
        vec3 halfVec = normalize(lightDir + viewDir);

        float diff = max(dot(n, lightDir), 0.0);
        float diff2 = max(dot(n, lightDir2), 0.0);
        float spec = pow(max(dot(n, halfVec), 0.0), 32.0);
        float fresnel = pow(1.0 - max(dot(n, viewDir), 0.0), 3.0);

        // Optional effects
        float ao = getAO(p, n);
        float shadow = getShadow(p + n * 0.002, lightDir, 8.0);

            // Color from orbit trap
    vec3  matCol= vec3(
        0.5 + 0.5 * sin(orbitTrap.x * 3.0),
        0.5 + 0.5 * sin(orbitTrap.y * 3.0 + 2.0),
        0.5 + 0.5 * sin(orbitTrap.z * 3.0 + 4.0)
    );
        matCol = mix(matCol, primitiveColor, ao);
        // Compose lighting
        vec3 col = matCol * 0.08; // Ambient
        col += matCol * diff * shadow * 0.65;
        col += vec3(0.2, 0.3, 0.4) * diff2 * 0.25;
        col += vec3(1.0, 0.95, 0.9) * spec * shadow * 0.6;
        col += vec3(0.3, 0.4, 0.5) * fresnel * 0.15;
        col *= ao;

        // Distance fog
        col = mix(col, vec3(BG), min(1.0,max(0.0,(t-sqrt(FOG))/25.)));
        return col;
}
vec3 contrast(vec3 color, float value) {return 0.5 + value * (color - 0.5);}
