#define AA 0


// can play with color here
#define PALETTE vec3(6,3,2)
void mainImage0(out vec4 o, vec2 u) {

    float xm = iMouse.x==0.0?iResolution.x/2.:iMouse.x; 
    float ym = iMouse.y==0.0?iResolution.y/2.:iMouse.y;
    vec2 uo = u; // get divider values

    float n,i,s,t=iTime*.2, d,v;
    vec3  q,p = iResolution, c;
    u = (u+u-p.xy)/p.y;

    float centerY = iResolution.y / 2.0;

    if(ym > centerY) {
        // Above center: geometric transition
        float transitionWidth = (ym - centerY) * 1.5;
        float blend = smoothstep(xm - transitionWidth, xm + transitionWidth, uo.x);
        float inversionRadius = 0.58;
        vec2 inversionCenter = vec2(0.0, 0.0);
        vec2 uOffset = u - inversionCenter;
        float dist = max(length(uOffset), 0.001);
        float newDist = inversionRadius * pow(dist/inversionRadius, 1.0 - 2.0*blend);
        u = inversionCenter + newDist * normalize(uOffset);

        // Render once with the geometric transition
        vec2 l = u - (u.yx*.9+.3-vec2(-.35,.15));    
        for(; i++ < 5e1 && d < 5e1;
            d += s = min(q.y=.01+.6*abs(24. - length(q.xy)),
                         v = max(s, dot(abs(fract(p)-.5), vec3(.04)))),
            c +=(1.+cos(p.z+PALETTE))/v
              +  d*vec3(5,2,1)/q.y/1e1
              +  7.*vec3(3,4,1)/length(l)
        )
            for(q = p = vec3(u * d, d - 16.),
                s = length(p)-8.,
                p.xy *= mat2(cos(t+p.z*.6+vec4(0,33,11,0))),
                p += cos(t+p.zxy)+cos(t+p.yzx*s)/s/4.,
                p += .5*cos(t+dot(cos(t+p), p) *  p),
                n = .02; n < 2.; n *= 1.6
            )
                q.y -= abs(dot(sin(4.*t+.3*q / n ), q-q+n));

        c = mix(c, c.yzx, smoothstep(2., .1, length(u)*1.));
        o.rgb = tanh(c*c/6e7/length(u-.3)+.1*length(u));
    } else {
        // Below center: render both versions and fade between them
        float fadeWidth = (centerY - ym) / 1.5;
        float blendFactor = smoothstep(xm - fadeWidth, xm + fadeWidth, uo.x);

        // Render non-inverted version
        vec2 u_normal = u;
        vec3 c_normal = vec3(0);
        float d_normal = 0., i_normal = 0.;
        vec3 q_normal;

        vec2 l = u_normal - (u_normal.yx*.9+.3-vec2(-.35,.15));    
        for(; i_normal++ < 5e1 && d_normal < 5e1;
            d_normal += s = min(q_normal.y=.01+.6*abs(24. - length(q_normal.xy)),
                         v = max(s, dot(abs(fract(p)-.5), vec3(.04)))),
            c_normal +=(1.+cos(p.z+PALETTE))/v
              +  d_normal*vec3(5,2,1)/q_normal.y/1e1
              +  7.*vec3(3,4,1)/length(l)
        )
            for(q_normal = p = vec3(u_normal * d_normal, d_normal - 16.),
                s = length(p)-8.,
                p.xy *= mat2(cos(t+p.z*.6+vec4(0,33,11,0))),
                p += cos(t+p.zxy)+cos(t+p.yzx*s)/s/4.,
                p += .5*cos(t+dot(cos(t+p), p) *  p),
                n = .02; n < 2.; n *= 1.6
            )
                q_normal.y -= abs(dot(sin(4.*t+.3*q_normal / n ), q_normal-q_normal+n));
        c_normal = mix(c_normal, c_normal.yzx, smoothstep(2., .1, length(u_normal)*1.));

        // Render inverted version
        vec2 u_inverted = u;
        float inversionRadius = 0.58;
        vec2 inversionCenter = vec2(0.0, 0.0);
        vec2 uOffset = u_inverted - inversionCenter;
        float dist = length(uOffset);
        if(dist > 0.001) {
            u_inverted = inversionCenter + (inversionRadius * inversionRadius / (dist * dist)) * uOffset;
        }

        vec3 c_inverted = vec3(0);
        float d_inverted = 0., i_inverted = 0.;
        vec3 q_inverted;

        l = u_inverted - (u_inverted.yx*.9+.3-vec2(-.35,.15));    
        for(; i_inverted++ < 5e1 && d_inverted < 5e1;
            d_inverted += s = min(q_inverted.y=.01+.6*abs(24. - length(q_inverted.xy)),
                         v = max(s, dot(abs(fract(p)-.5), vec3(.04)))),
            c_inverted +=(1.+cos(p.z+PALETTE))/v
              +  d_inverted*vec3(5,2,1)/q_inverted.y/1e1
              +  7.*vec3(3,4,1)/length(l)
        )
            for(q_inverted = p = vec3(u_inverted * d_inverted, d_inverted - 16.),
                s = length(p)-8.,
                p.xy *= mat2(cos(t+p.z*.6+vec4(0,33,11,0))),
                p += cos(t+p.zxy)+cos(t+p.yzx*s)/s/4.,
                p += .5*cos(t+dot(cos(t+p), p) *  p),
                n = .02; n < 2.; n *= 1.6
            )
                q_inverted.y -= abs(dot(sin(4.*t+.3*q_inverted / n ), q_inverted-q_inverted+n));
        c_inverted = mix(c_inverted, c_inverted.yzx, smoothstep(2., .1, length(u_inverted)*1.));

        // Blend the two rendered colors
        c = mix(c_normal, c_inverted, blendFactor);
        o.rgb = tanh(c*c/6e7/length(mix(u_normal, u_inverted, blendFactor)-.3)+.1*length(mix(u_normal, u_inverted, blendFactor)));
    }
