float sblend = 0.1;
vec2 getGridPoint(int idx, float time) {
    int x = idx % 4;
    int y = idx / 4;

    // Base grid position (normalized to -0.5 to 0.5)
    vec2 base = vec2(float(x), float(y)) / 3.0 - vec2(0.5);
    // Get distance and angle from center
    float dist = length(base);
    if(dist < 0.001) return base; // Center point stays put

    vec2 normalized = base / dist;

    // Add animation
    float phase = float(idx) * 0.5;
    vec2 offset = vec2(
        sin(time * 1.5 + phase) * 0.03,
        cos(time * 1.7 + phase * 1.3) * 0.03
    );

    // Radial wave
    offset += base * sin(time * 3.0 - length(base) * 10.0) * 0.02;

    base += offset;

    // |cos| + |sin| gives us values from 1.0 (at cardinals) to sqrt(2) (at diagonals)
    float scaleFactor = abs(normalized.x) + abs(normalized.y);

    // Pull points inward by this factor
    vec2 distorted = 1.5 * base / pow(scaleFactor, 3.);

    return distorted;
}

float sdTriangle(vec2 p, vec2 a, vec2 b, vec2 c) {
    vec2 e0 = b - a;
    vec2 e1 = c - b;
    vec2 e2 = a - c;

    vec2 v0 = p - a;
    vec2 v1 = p - b;
    vec2 v2 = p - c;

    vec2 pq0 = v0 - e0 * clamp(dot(v0, e0) / dot(e0, e0), 0.0, 1.0);
    vec2 pq1 = v1 - e1 * clamp(dot(v1, e1) / dot(e1, e1), 0.0, 1.0);
    vec2 pq2 = v2 - e2 * clamp(dot(v2, e2) / dot(e2, e2), 0.0, 1.0);

    float s = sign(e0.x * e2.y - e0.y * e2.x);
    vec2 d = min(min(vec2(dot(pq0, pq0), s * (v0.x * e0.y - v0.y * e0.x)),
                     vec2(dot(pq1, pq1), s * (v1.x * e1.y - v1.y * e1.x))),
                     vec2(dot(pq2, pq2), s * (v2.x * e2.y - v2.y * e2.x)));

    return -sqrt(d.x) * sign(d.y);
}
const int N = 4;
float sdPolygon( in vec2[N] v, in vec2 p )
{
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=N-1; i<N; j=i, i++ )
    {
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );
        bvec3 c = bvec3(p.y>=v[i].y,p.y<v[j].y,e.x*w.y>e.y*w.x);
        if( all(c) || all(not(c)) ) s*=-1.0;  
    }
    return s*sqrt(d);
}

float sdQuad(vec2 p, vec2 tl, vec2 tr, vec2 br, vec2 bl) {

    vec2[] polygon = vec2[](tl,tr,br,bl);

	return  sdPolygon(polygon, p);
}

// Distance from point to line segment
float distToSegment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}


float sdEllipseSimple(vec2 p, vec2 center, vec2 radii, float rotation) {
    p -= center;

    float c = cos(rotation);
    float s = sin(rotation);
    p = mat2(c, s, -s, c) * p;

    vec2 np = p / max(radii, vec2(0.001));
    float d = length(np) - 1.0;

    return d * min(radii.x, radii.y);
}

// Original working incircle
vec4 getIncircle(vec2 a, vec2 b, vec2 c) {
    float la = length(b - c);
    float lb = length(c - a);
    float lc = length(a - b);
    float p = la + lb + lc;

    vec2 center = (la * a + lb * b + lc * c) / p;

    float s = p * 0.5;
    float area = abs((b.x - a.x) * (c.y - a.y) - (c.x - a.x) * (b.y - a.y)) * 0.5;
    float radius = area / s;

    return vec4(center, radius, 1.0);
}

// Fit an oval that touches quad edges
vec4 fitOvalToQuad(vec2 tl, vec2 tr, vec2 bl, vec2 br) {
    vec2 center = (tl + tr + bl + br) * 0.25;

    float distTop = distToSegment(center, tl, tr);
    float distRight = distToSegment(center, tr, br);
    float distBottom = distToSegment(center, bl, br);
    float distLeft = distToSegment(center, tl, bl);

    float width = (distLeft + distRight) * 0.5 * 0.95;
    float height = (distTop + distBottom) * 0.5 * 0.95;

    vec2 horizontal = ((tr - tl) + (br - bl)) * 0.5;
    float rotation = atan(horizontal.y, horizontal.x);

    return vec4(width, height, rotation, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec2 grid[16];
    for(int i = 0; i < 16; i++) {
        grid[i] = getGridPoint(i, iTime);
    }

    float minDist = 1e10;
    float minAbsDist = 1e10;
    float containerDist = 1e10;
    vec3 nearestColor = vec3(0.0);

    for(int row = 0; row < 3; row++) {
        for(int col = 0; col < 3; col++) {
            int quadIdx = row * 3 + col;
            int tl = row * 4 + col;
            int tr = row * 4 + col + 1;
            int bl = (row + 1) * 4 + col;
            int br = (row + 1) * 4 + col + 1;
            sblend = 0.4 + sin((iTime+(float(row+(col*row))))*1.5)*0.4;
            bool useOval = (quadIdx == 2 || quadIdx == 6);
            bool flipTri = (quadIdx == 0 || quadIdx == 4 || quadIdx == 8);

            float tri1Sdf = sdTriangle(uv, grid[tl], grid[tr], grid[bl]);
            float tri2Sdf = sdTriangle(uv, grid[tr], grid[br], grid[bl]);
            vec4 circ1 = getIncircle(grid[tl], grid[tr], grid[bl]);
            vec4 circ2 = getIncircle(grid[tr], grid[br], grid[bl]);
            if(flipTri){
            tri1Sdf = sdTriangle(uv, grid[tr], grid[br], grid[tl]);
            tri2Sdf = sdTriangle(uv, grid[br], grid[bl], grid[tl]);
            circ1 = getIncircle(grid[tr], grid[br], grid[tl]);
            circ2 = getIncircle(grid[br], grid[bl], grid[tl]);
            }

            if(useOval) {

                float quadSdf = sdQuad(uv, grid[tl], grid[bl], grid[br], grid[tr]);

                vec2 center = (grid[tl] + grid[tr] + grid[bl] + grid[br]) * 0.25;
                vec4 oval = fitOvalToQuad(grid[tl], grid[tr], grid[bl], grid[br]);
                float ovalSdf = sdEllipseSimple(uv, center, oval.xy, oval.z);

                float blendedSdf = mix(ovalSdf, quadSdf, sblend);

                if(abs(blendedSdf) < minAbsDist) {
                    minAbsDist = abs(blendedSdf);
                }

                if(blendedSdf < minDist) {
                    minDist = blendedSdf;
                    nearestColor = 0.5 + 0.5 * cos(vec3(0.0, 2.0, 4.0) + float(quadIdx) * 1.5);
                    containerDist = quadSdf;
                }
            } else {
                // Triangle 1


                float circle1Sdf = length(uv - circ1.xy) - circ1.z;

                float blend1 = mix(circle1Sdf, tri1Sdf, sblend);
                if(abs(blend1) < minAbsDist) {
                    minAbsDist = abs(blend1);
                }

                if(blend1 < minDist) {
                    minDist = blend1;
                    nearestColor = 0.5 + 0.5 * cos(vec3(0.0, 2.0, 4.0) + float(quadIdx * 2));
                    containerDist = tri1Sdf;
                }

                // Triangle 2


                float circle2Sdf = length(uv - circ2.xy) - circ2.z;


                float blend2 = mix(circle2Sdf, tri2Sdf, sblend);

                if(abs(blend2) < minAbsDist) {
                    minAbsDist = abs(blend2);
                }

                if(blend2 < minDist) {
                    minDist = blend2;
                    nearestColor = 0.5 + 0.5 * cos(vec3(1.0, 3.0, 5.0) + float(quadIdx * 2 + 1));
                    containerDist = tri2Sdf;
                }
            }
        }
    }

    vec3 color = vec3(0.02, 0.02, 0.05);

    if(minDist < 0.0) {
        float gradientFactor = 1.0 - smoothstep(-0.1, 0.0, containerDist);
        color = nearestColor * (0.5 + 0.5 * gradientFactor);
    }

    color += exp(-minAbsDist * 100.0) * vec3(1.0);

    for(int i = 0; i < 16; i++) {
        float d = length(uv - grid[i]);
        color += vec3(1.0) * exp(-d * 200.0);
    }

    fragColor = vec4(color, 1.0);
}

vec3 colourGround = vec3(0.820,0.820,0.820);
vec3 colourPaint  = vec3(0.420,0.420,0.420);
vec3 colourTeamA  = vec3(0.839,0.000,0.000);
vec3 colourTeamB  = vec3(0.212,0.800,0.000);
vec3 colourLight = vec3(1.000,1.000,1.000);
vec3 colourAmbient = vec3(0.188,0.188,0.188);
vec2 centre;
float radius;
float pixel;

vec3 contrast ( in vec3 ccol ){
    ccol.r = ccol.r*(ccol.r+0.5);
    ccol.g = ccol.g*(ccol.g+0.5);
    ccol.b = ccol.b*(ccol.b+0.5);
    return ccol;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec2 tanLineDist( in vec2 C, in float r, in vec2 P, in vec2 F){
    C.y = -C.y;
    vec2 d = P-C;
    vec2 dr = vec2(-d.y, d.x);
    float dl = length(d);
    //if(dl>=r){
        float rho = r/dl;
        float ad = rho*rho;
        float bd = rho*sqrt(1.0-ad);//*(1.0-rho)); // (1-rho)*(1-rho) ?
        vec2 T1 = vec2(C.x + ad*d.x + bd*dr.x, C.y + ad*d.y + bd*dr.y);
        vec2 T2 = vec2(C.x + ad*d.x - bd*dr.x, C.y + ad*d.y - bd*dr.y);

    //}
    float ax = T1.x-P.x;
    float by = P.y-T1.y;
    float c = (T1.y*P.x)-(T1.x*P.y);

    float ax2 = T2.x-P.x;
    float by2 = P.y-T2.y;
    float c2 = (T2.y*P.x)-(T2.x*P.y);

    return  vec2((F.x*-ax)- (F.y*-by)-c, (F.x*-ax2)- (F.y*-by2)-c2);
}

vec3 getGroundTexture( in vec2 p, in float ar , in float pmult)
{
    float gpixel = pixel*pmult*1.2;
    float lineWidth = 1.0/180.0;
    float f ;
    vec2 pn = vec2((p.x-(0.5*ar)), p.y-0.5);
    vec3 gcol = colourGround;
    float gdist = distance(p, vec2(ar, 0.5));
    float cgdist = distance(centre, vec2(ar, 0.5));
    float lpn = length(pn);
    float bdist = sdBox(pn, vec2(ar/2.0, 0.5));
    if(bdist<=0.0-lineWidth){
        if(bdist >= -lineWidth*2.0){
            gcol = colourPaint;
        }

        if(lpn>(0.2-gpixel)-lineWidth){
            if(lpn<0.2){
                f = smoothstep((0.2-gpixel), .2 , lpn);
                gcol = mix(colourPaint,colourGround,  f);
                f = smoothstep(0.2-lineWidth, (0.2-gpixel)-lineWidth , lpn);
                gcol = mix(gcol,colourGround,  f);
            }
        if(abs(pn.x)<=lineWidth/2.0 && lpn>=0.19){
                gcol = colourPaint;
            }
        }

    }

    gcol = gcol*colourAmbient;
    float TAdist = sdBox(vec2(pn.x+(0.5*ar), pn.y), vec2(0.0, 0.15));
    gcol = gcol*(1.0+(colourTeamA/sqrt(TAdist*50.0)));

    //float modRadius = radius*(0.6 + cgdist/2.0);//*(1.0/pow((pn.x+(0.5*ar)), 2.0));
    float modRadius = radius*(0.7+(cgdist*cgdist*0.15));
    vec2 isInLight = tanLineDist(centre, modRadius, vec2(ar, -0.65), -p.yx);
    vec2 isInLight2 = tanLineDist(centre, modRadius, vec2(ar, -0.35), -p.yx);

    float TBdist = sdBox(vec2(pn.x-(0.5*ar), pn.y), vec2(0.0, 0.15));
    float inLight= 0.0;

    if(isInLight.y>=0.0 || isInLight2.y<=0.0){
        inLight=1.0;} else {
            inLight= 1.0/(abs(isInLight2.y)/abs(isInLight.y) +1.0);
        }

    float inLight2= 0.0;

    if(isInLight.x>=0.0 || isInLight2.x<=0.0){
        inLight2=1.0;} else {
            inLight2= 1.0/(abs(isInLight.x)/abs(isInLight2.x) +1.0);
        } 

    if(isInLight.y>=0.0 && isInLight2.x<=0.0){ inLight=0.0;}
    if(isInLight.y<=0.0 && isInLight2.x>=0.0){ 
        float inLightCross = (inLight+inLight2);
        inLight=inLight2 = inLightCross;



    }
    if(gdist<cgdist){inLight=1.0;}
    inLight = min(inLight, inLight2);
    if(isInLight.y>0.0 && isInLight2.x<=0.0  && (isInLight.x>0.0 || isInLight2.y<0.0)){inLight=1.0;}
    f = smoothstep(0.0, 1.0,inLight);
    gcol = mix(gcol,gcol*( 1.0+(colourTeamB/sqrt(TBdist*50.0))),  f);


    if(p.x<0.0||p.y<0.0||p.x>1.0*ar||p.y>1.0){gcol = vec3(0.0,0.0,0.0);}
    gcol = gcol*(2.0+(colourLight/(sqrt(lpn+0.2)/1.2)));
    //gcol.b *= pn.x*ar;
    return gcol;

}

