float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y; // Normalise just the y coordinates to 1, to avoid distortion.

    float xoffset = (iResolution.x-iResolution.y)/(2.0*iResolution.y);

    uv.x -= xoffset; // set the x so the the 0-1 square is in the middle.

    float pixel = 1.0/iResolution.y; // and this gives us the size of a single pixel

    float radius = 0.01 + (iMouse.x/iResolution.y)/10.0;

    vec2 uv2 = uv + pixel; 

    vec2 centre = vec2((iMouse.x/iResolution.y)-xoffset,iMouse.y/iResolution.y) ;//vec2(0.5 + sin(iTime*2.)/5.0, 0.5 + cos(iTime*2.)/5.0);
    vec2 distv = uv.xy - centre;
    vec2 distv2 = uv2.xy - centre;
    float dist = length(distv);
    float dist2 = length(distv2);
    // 1. Get height of point of surface on sphere

    float height = sqrt((radius*radius)-(dist*dist));
    float height2 = sqrt((radius*radius)-(dist2*dist2));
    vec3 col;// = vec3(1.0, height, height);
    // 2. Create vector of position of surface on sphere, relative to centre of sphere
    vec3 position = vec3(uv.x, height, uv.y);
    vec3 position2 = vec3(uv2.x, height2, uv2.y);
    //col = position *2.0;
    // 3. Get normal of point on surface
    vec3 normal = vec3(distv.x, height, distv.y)/radius;
    vec3 normal2 = vec3(distv2.x, height2, distv2.y)/radius;
    //col = normal*2.0;
    // 4. Get reflection vector of point
    vec3 incident = vec3(0.0, -1.0, 0.0);

    vec3 reflection = reflect(incident, normal);
    vec3 reflection2 = reflect(incident, normal2);
    //col.g = col.g*-1.0;
    //col = reflection*1.0;
    // 5. Find intersection between reflection vector and plane
    vec3 q = position + reflection * 100.0;
    vec3 q2 = position2 + reflection2 * 100.0;
    float a = 0.0;
    float b = 0.0;
    float c = 1.0;
    float d = 1.0;//1.7+sin(iTime);
    float tDenom = a*(q.x-position.x) + b*(q.y-position.y) + c*(q.z-position.z);
    float tDenom2 = a*(q2.x-position2.x) + b*(q2.y-position2.y) + c*(q2.z-position2.z);
    bool intersects;
    bool intersects2;
    vec3 intersection;
    vec3 intersection2;
    float t;
    if (tDenom < 0.01) {
        intersects = false;
    } else {
        t = - ( a*position.x + b*position.y + c*position.z - d ) / tDenom;
        intersects = true;
        intersection = vec3((position.x+t*(q.x-position.x)),(position.y+t*(q.y-position.y)),(position.z+t*(q.z-position.z)));
        if (tDenom2 < 0.01) {
            intersects2 = false;
        } else {
            intersects2 = true;
            t = - ( a*position2.x + b*position2.y + c*position2.z - d ) / tDenom2;
            intersection2 = vec3((position2.x+t*(q2.x-position2.x)),(position2.y+t*(q2.y-position2.y)),(position2.z+t*(q2.z-position2.z)));
        }
    }
    // 6. Get colour of point at position on plane
    vec2 box = vec2(0.5,0.2);
    vec2 boxposition = vec2(0.5, 0.2);

    float boxdist = sdBox(boxposition-intersection.xy, box);

    // get distance from point on sphere to intersection

    float bdist = distance(intersection2, intersection);

    float f;
    if(intersects && boxdist<bdist && intersection.z<2.0){
    f = smoothstep(0.0, bdist , boxdist);
    float boxdistao = 1.0;//0.8+(-9.0*boxdist);
    col = mix(vec3(boxdistao,boxdistao,boxdistao),col,  f);
    }

    a = 0.0;
    b = -1.0;
    c = 0.0;
    d = 0.0;
    tDenom = a*(q.x-position.x) + b*(q.y-position.y) + c*(q.z-position.z);
    if (tDenom < 0.1) {
        intersects = false;
    } else {
        t = - ( a*position.x + b*position.y + c*position.z - d ) / tDenom;
        intersects = true;
        intersection = vec3((position.x+t*(q.x-position.x)),(position.y+t*(q.y-position.y)),(position.z+t*(q.z-position.z)));
    }
    box = vec2(0.5,0.5);
    boxposition = vec2(0.5, 0.5);
    boxdist = sdBox(boxposition-intersection.xz, box);
    vec2 intersectionoffset = vec2(boxposition.x - box.x/2.0, boxposition.y - box.y/2.0);
    bdist = iResolution.y * distance(position, intersection);
    if(intersects){
        f = smoothstep(-pixel*4.0*sqrt(bdist), pixel*4.0*sqrt(bdist) , boxdist);
        vec3 groundcol = texture(iChannel0, vec2(intersection.x,intersection.z)).rgb;
        //groundcol.r *= 2.0;
        if (intersection.x<0.0 || intersection.x>1.0 || intersection.z<0.0 || intersection.z>1.0){
        groundcol=vec3(0.0,0.0,0.0);
//col = vec3(0.0,0.0,0.0);
        }
        col = mix(groundcol,col,  f);
    }
    vec3 ground = texture(iChannel0, vec2(uv.x, uv.y)).rgb;
	//Smoothes the edge of the sphere
	float g = smoothstep(radius-pixel, radius+pixel, dist);
	vec3 fcol = mix( col,ground, g);
    //fcol.r *= 1.5;
    //fcol *= height*5.0;
    if(dist>radius){
        fcol=ground; 
        if (uv.x<0.0 || uv.x>1.0){
        fcol=vec3(0.0,0.0,0.0);
        }
    }
    fragColor = vec4(fcol,1.0);
}

