// Fork of "Crystal's Hypertile 3 Swirl" by Crystalize. https://shadertoy.com/view/lcSXR1
// 2025-06-23 18:16:45

//many thanks to my friend, Zueuk, who supplied the distance formulae used here

const float Psym=3.;
const float Qsym=5.;
const float Rsym=4.;

const int iters=10;

const vec2 Re = vec2(1,0);
const vec2 Im = vec2(0,1);
const float TWOPI = 6.283185307179586476;
float PI = 3.141592653589793238;

vec2 cmul(vec2 z, vec2 c) {
    return vec2(z.x * c.x - z.y * c.y, z.x * c.y + z.y * c.x);
}

vec2 cdiv(vec2 z, vec2 c) {
    float r = dot(c, c);
    return vec2(z.x * c.x + z.y * c.y, z.y * c.x - z.x * c.y) / r;
}

vec2 conj(vec2 z) {
    return vec2(z.x, -z.y);
}

vec2 recip(vec2 z) {
    return conj(z)/dot(z,z);
}

vec2 hypershift(vec2 z, vec2 s) {
    return cdiv(z + s, cmul(z,conj(s))+Re);
}

vec2 cpow(vec2 z, vec2 p) {
    float a = atan(z.y, z.x);
    float lnr = 0.5 * log(dot(z,z));
    float m = exp(p.x * lnr - p.y * a);
    float angle = p.x * a + p.y * lnr;
    return vec2(cos(angle), sin(angle)) * m;
}

vec2 cabs(vec2 z, vec2 c) {
    return cpow(cpow(z,c), cdiv(Re,c));
}

vec2 swirl3sin(vec2 z, float shift, float minimum, float maximum, float type) {
    //type = negative for fixed min, type = 0 for fixed unit, type = positive for fixed max
    float minOffset = minimum > 0. ? log(minimum) * shift : 0.;
    float maxOffset = maximum > 0. ? log(maximum) * shift : 0.;

    vec2 MinMaxAngle = type < 0. ? vec2(0., maxOffset - minOffset) : type == 0. ? vec2(minOffset, maxOffset) : vec2(minOffset - maxOffset, 0.);

    float dist = maximum - minimum;

    float angle = atan(z.y, z.x);
    float range = length(z);
    if(range < minimum) {
        angle += MinMaxAngle.x;
    } else if (range > maximum) {
        angle += MinMaxAngle.y;
    } else {
        angle += log((1. - cos((range - minimum) / dist * PI)) * .5 * dist + minimum) * shift - minOffset + MinMaxAngle.x;
    }
    return vec2(cos(angle),sin(angle)) * range;
}

vec2 hypertile3swirl(vec2 z, float p, float q, float r, float swirl, float sBuffer) {
    float pp = PI/p;
    float pq = PI/q;
    float spq = sin(pq);
    float pr = PI/r;
    float spr = sin(pr);
    float a1 = acosh((cos(pp) + cos(pq) * cos(pr)) / (spq * spr));
    float a2 = asinh(spq / sin(pp) * sinh(a1));
    float sha2 = sinh(a2);
    float a3 = asinh(spr * sha2);
    float rcha3 = 1./cosh(a3);
    float a4 = asinh(spr * sha2 / spq);

    float h = tanh(a3 * .5);
    float b1 = tanh(acosh(cosh(a4) * rcha3) * .5);
    float b2 = tanh(acosh(cosh(a2) * rcha3) * .5);
    float sSize = h - sBuffer;
    for(int i = 0; i < iters; i++) {
        z = hypershift(z,-b1*Re);
        z = -cabs(-z, Re*q);
        z = hypershift(z,b1*Re);
        z = hypershift(z,b2*Re);
        z = cabs(z, Re*r);
        z = hypershift(z,-b2*Re);
    }
    z = hypershift(z, h * Im);
    z = swirl3sin(z/sSize, swirl, 0., 1., 0.);
    z = hypershift(z*sSize, -h * Im);
    z = hypershift(z, -h * Im);
    z = swirl3sin(z/sSize, swirl, 0., 1., 0.);
    z = hypershift(z*sSize, h * Im);
    for(int i = 0; i < iters/2; i++) {
        z = hypershift(z,-b1*Re);
        z = -cabs(-z, Re*q);
        z = hypershift(z,b1*Re);
        z = hypershift(z,b2*Re);
        z = cabs(z, Re*r);
        z = hypershift(z,-b2*Re);
    }
    z = z.x>0.?hypershift(z,-b1*Re)/b1:hypershift(z,b2*Re)/b2;
    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord.xy - 0.5*iResolution.xy) / -iResolution.y;
    PI = 4.*(iMouse.x/iResolution.x);
    uv = dot(uv,uv)<1.?uv:recip(uv);
    uv = hypertile3swirl(uv, Psym, Qsym, Rsym, 1., -0.05);

    vec3 col = 0.5+0.5*cos(abs(uv.x)*2.+vec3(0,1,2));

    fragColor = vec4(col,1.0);
}

#define A(C, Z) \
for (float i,d=.05*dot(fract(sin(I)), sin(I)),s,n; i++ < 90.;) { \
    vec3 p = vec3((I + I - r.xy) / r.y*R*d, d-2.);\
    p.yz*=R;\
    vec3 k = p;\
    p *= .7 + .2 * tanh((abs(fract(t * .5) - .5) - .25) * 2e1-i*.01);\
    for (n = .0; n++ < 3.;) \
        p += asin(sin(p.zyx*n))/n; \
    d += s = .01 + .14 * abs(max(dot(abs(fract(p)-.5), vec3(.8))+Z *.04,length(k)-7.) - i /4e2); \
    O.C += (.5 + .5 * sin(i*.3+ Z*5.))*1.2/s;\
}

void mainImage(out vec4 O, vec2 I)
{   
    float t = iTime/3.;
    vec3 r = iResolution;
    mat2 R = mat2(cos(t/2. + vec4(0, 33, 11, 0)));
    O *= 0.;
    A(r, -1.)
    A(g, 0.)
    A(b, 1.)
    O = tanh(O * O / 1e6);
}

void mainImage(out vec4 color, in vec2 fragCoord)
{
    // Read slider values from Buffer A
    vec3 sliderValues = vec3(
        texelFetch(iChannel0, ivec2(0, 0), 0).r,
        texelFetch(iChannel0, ivec2(1, 0), 0).r,
        texelFetch(iChannel0, ivec2(2, 0), 0).r
    );

    // Center the coordinates
    vec2 uv = fragCoord - iResolution.xy * 0.5;
    uv *= 35./iResolution.y;
    // Interactive parameters from sliders
    float waveSpacing = 1.5 + sliderValues.x * 20.0;
    float centralFreq = 0.1 + sliderValues.y * 20.0;
    float outerFreq = 0.7 + sliderValues.z * 2.0; 
    // Track waves separately for coloring
    float centerWave = sin(length(uv) * centralFreq);
    float topWave = sin(length(uv + vec2(0.0, waveSpacing)) * outerFreq);
    float bottomWave = sin(length(uv + vec2(0.0, -waveSpacing)) * outerFreq);
    float leftWave = sin(length(uv + vec2(-waveSpacing, 0.0)) * outerFreq);
    float rightWave = sin(length(uv + vec2(waveSpacing, 0.0)) * outerFreq);
    // Group waves by axis
    float verticalWaves = topWave + bottomWave;
    float horizontalWaves = leftWave + rightWave; 
    // Total interference pattern
    float totalWaves = centerWave + topWave + bottomWave + leftWave + rightWave;
    totalWaves = totalWaves * 0.2 + 0.5; 
    vec3 waveColor = vec3(
        0.5 + 0.5 * horizontalWaves / 2.0,
        0.5 + 0.5 * verticalWaves / 2.0,
        0.5 + 0.5 * centerWave
    );
    vec3 finalColor = mix(vec3(0.0), waveColor, smoothstep(0.3, 0.7, totalWaves));
    // Soft circular vignette
    float dist = length(uv);
    float circleMask = 1.0 - smoothstep(15.0, 16.5, dist);
    finalColor *= circleMask;
    // Draw sliders on top
    vec2 sliderPos = vec2(iResolution.x - 90.0, 10.0);
    float sliderWidth = 15.0;
    float sliderHeight = 120.0;
    float sliderSpacing = 30.0;

    for (int i = 0; i < 3; i++) {
        vec2 thisSliderPos = sliderPos + vec2(float(i) * sliderSpacing, 0.0);
        // Slider background
        if (fragCoord.x > thisSliderPos.x && fragCoord.x < thisSliderPos.x + sliderWidth &&
            fragCoord.y > thisSliderPos.y && fragCoord.y < thisSliderPos.y + sliderHeight) {
            finalColor = vec3(0.15); 
            // Slider fill
            if (fragCoord.y < thisSliderPos.y + sliderValues[i] * sliderHeight) {
                finalColor = vec3(0.6, 0.6, 0.7); 
            }
        }
        // Slider outline
        if (fragCoord.x > thisSliderPos.x - 1.0 && fragCoord.x < thisSliderPos.x + sliderWidth + 1.0 &&
            fragCoord.y > thisSliderPos.y - 1.0 && fragCoord.y < thisSliderPos.y + sliderHeight + 1.0 &&
            (fragCoord.x < thisSliderPos.x || fragCoord.x > thisSliderPos.x + sliderWidth ||
             fragCoord.y < thisSliderPos.y || fragCoord.y > thisSliderPos.y + sliderHeight)) {
            finalColor = vec3(0.4);
        }
    }
    color = vec4(finalColor, 1.0);
}

void mainImage(out vec4 color, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

