    vec2 sliderPos = vec2(iResolution.x - 90.0, 10.0);
    float sliderWidth = 25.0;  // Bigger click area
    float sliderHeight = 120.0;
    float sliderSpacing = 30.0;

    // Read previous state
    vec3 sliderValues = vec3(
        texelFetch(iChannel0, ivec2(0, 0), 0).r,
        texelFetch(iChannel0, ivec2(1, 0), 0).r,
        texelFetch(iChannel0, ivec2(2, 0), 0).r
    );
    float hasInteracted = texelFetch(iChannel0, ivec2(3, 0), 0).r;

    if (iFrame < 1) {
        sliderValues = vec3(0.5, 0.5, 0.5);
        hasInteracted = 0.0;
    }

    if (hasInteracted < 0.5) {
        sliderValues.x = 0.5 + 0.5 * sin(iTime * 0.7); 
        sliderValues.y = 0.5 + 0.5 * sin(iTime * 1.3); 
        sliderValues.z = 0.5 + 0.5 * sin(iTime * 2.1);  
    }

    if (iMouse.z > 0.0) {
        vec2 mouse = iMouse.xy;

        for (int i = 0; i < 3; i++) {
            vec2 thisSliderPos = sliderPos + vec2(float(i) * sliderSpacing, 0.0);

            if (mouse.x > thisSliderPos.x && mouse.x < thisSliderPos.x + sliderWidth &&
                mouse.y > thisSliderPos.y && mouse.y < thisSliderPos.y + sliderHeight) {

                float newValue = clamp((mouse.y - thisSliderPos.y) / sliderHeight, 0.0, 1.0);
                sliderValues[i] = newValue;
                hasInteracted = 1.0; 
            }
        }
    }

    // Store values in first 4 pixels
    if (fragCoord.y < 1.0) {
        if (fragCoord.x < 1.0) color = vec4(sliderValues.x, 0, 0, 1);
        else if (fragCoord.x < 2.0) color = vec4(sliderValues.y, 0, 0, 1);
        else if (fragCoord.x < 3.0) color = vec4(sliderValues.z, 0, 0, 1);
        else if (fragCoord.x < 4.0) color = vec4(hasInteracted, 0, 0, 1);
        else color = vec4(0);
    } else {
        color = vec4(0);
    }
}

