void mainImage(out vec4 color, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    // Slider configuration
    vec2 sliderPos = vec2(iResolution.x - 120.0, 10.0);
    float sliderWidth = 25.0;
    float sliderHeight = 120.0;
    float sliderSpacing = 30.0;

    // Read previous state from row 0
    vec4 sliderValues = vec4(
        texelFetch(iChannel0, ivec2(0, 0), 0).r,
        texelFetch(iChannel0, ivec2(1, 0), 0).r,
        texelFetch(iChannel0, ivec2(2, 0), 0).r,
        texelFetch(iChannel0, ivec2(3, 0), 0).r
    );
    float hasInteracted = texelFetch(iChannel0, ivec2(4, 0), 0).r;

    if (iFrame < 1) {
        sliderValues = vec4(0.5);
        hasInteracted = 0.0;
    }

    if (hasInteracted < 0.5) {
        sliderValues.x = 0.5 + 0.3 * sin(iTime / 3.7); 
        sliderValues.y = 0.5 + 0.3 * sin(iTime / 4.3); 
        sliderValues.z = 0.5 + 0.3 * sin(iTime / 2.1);  
        sliderValues.w = 0.5 + 0.3 * sin(iTime / 2.1); 
    }
    float v4 = pow(sliderValues.w, 3.);
    float s4 = 15.;

    if (iMouse.z > 0.0) {
        vec2 mouse = iMouse.xy;

        for (int i = 0; i < 4; i++) {
            vec2 thisSliderPos = sliderPos + vec2(float(i) * sliderSpacing, 0.0);

            if (mouse.x > thisSliderPos.x && mouse.x < thisSliderPos.x + sliderWidth &&
                mouse.y > thisSliderPos.y && mouse.y < thisSliderPos.y + sliderHeight) {

                float newValue = clamp((mouse.y - thisSliderPos.y) / sliderHeight, 0.0, 1.0);
                sliderValues[i] = newValue;
                hasInteracted = 1.0; 
            }
        }
    }

    ivec2 coord = ivec2(fragCoord);

    if (coord.y == 0) {
        // Row 0: Store slider values
        if (coord.x == 0) color = vec4(sliderValues.x, 0, 0, 1);
        else if (coord.x == 1) color = vec4(sliderValues.y, 0, 0, 1);
        else if (coord.x == 2) color = vec4(sliderValues.z, 0, 0, 1);
        else if (coord.x == 3) color = vec4(sliderValues.w, 0, 0, 1);
        else if (coord.x == 4) color = vec4(hasInteracted, 0, 0, 1);
        else color = vec4(0);
    }
    else if (coord.y >= 1 ) { // incresa to 20 just in case we need it.
        // Rows 1-8: Store current frame's 512 audio samples
        // Map pixel position to audio sample index
        int row = coord.y - 1;  // 0-7
        int sampleIndex = row * int(iResolution.x) + coord.x;

        if (sampleIndex < 512) {
            // Store current frame's audio sample
            float audioSample = texture(iChannel1, vec2(float(sampleIndex) / 512.0, 1.0)).r;
            audioSample = pow(audioSample*1.2, 1.2);
            color = (vec4(audioSample, audioSample, audioSample, 1.0) + texelFetch(iChannel0, ivec2(coord.x, coord.y + 1), 0)*(v4 * s4))/((v4 * s4)+1.);
        } else {
            // Beyond 512 samples, store previous frame's data for rolling history
            // Shift data from row above (previous frame position)
            if (coord.y > 1) {
                color = texelFetch(iChannel0, ivec2(coord.x, coord.y - 1), 0);
            } else {
                color = vec4(0);
            }
        }
    }
    else {
        color = vec4(0);
    }
}

// from https://www.shadertoy.com/view/wsKGDc


#define MAX_ITER 1000.
#define R 100.
#define S(a,b,c) smoothstep(a,b,c)
#define TWO_PI 6.28318530718
#define PI 3.14159265359

//conversion helper
float f(float n, vec3 hsl){
    float k = mod(n+hsl.x*12., 12.);
    float a = hsl.y*min(hsl.z, 1.-hsl.z);
    return hsl.z-a*max(min(k-3., min(9.-k, 1.)),-1.);
}
// hsl in range <0, 1>^3
vec3 hsl2rgb(vec3 hsl){
    return vec3(f(0.,hsl), f(8.,hsl), f(4.,hsl));
}

vec3 hue2rgb(float hue){

    float fftc = texture(iChannel0, vec2(hue,0.0)).r;
    if(hue<1.0){hue=fftc;};
    //return (hsl2rgb(vec3(fftc, 1., .5))+hsl2rgb(vec3(hue, 1., .5)))/2.;
    return hsl2rgb(vec3(hue, 1., .5));
}

vec2 Rain(vec2 uv, float t){
    t*= 40.;
    vec2 aspect = vec2(3.,1.);
    vec2 st = uv*aspect;

    vec2 id = floor(st);
    st.y+=t*.22;
    float n = fract(sin(id.x*256.45)*85.);
    st.y += n;
    uv.y += n;
    id = floor(st);
    st = fract(st)-.5;

    t+= fract(sin(id.x*56.45+id.y*485.)*155.)*6.28;

    float y = -sin(t+sin(t+.5*sin(t)))*0.43;
    float x = (fract(sin(id.x*384.45)*id.y*288.)-.5)*.6;
    vec2 p1 = vec2(x, y);
    vec2 o1 = (st-p1)/aspect;
    float d = length(o1);

    float m1 = S(.07, .0, d);

    vec2 o2 = (fract(uv*aspect.x*vec2(1.,2.)-vec2(p1.x,0.))-.5)/vec2(1.,2.);
    d = length(o2);

    float m2 = S(0.2*(.5-st.y), .0, d) * S(-.1, .1, st.y-p1.y);

    //if(st.x>.46 || st.y>.49) m1 = 1.;

    return vec2(m1*o1*30.+m2*o2*10.);
}

vec3 julia(float zx, float zy, float cx, float cy){
    float iter = 0.;
    for(int i = 0; i<int(MAX_ITER); i++){
        if(zx * zx + zy * zy < R*R && iter<MAX_ITER){
			float xtemp = zx*zx - zy*zy;
        	zy = 2. * zx * zy + cy;
        	zx = xtemp + cx;
        	iter+=1.;
        } else {
            break;
        }
    }

    /*if(iter>=MAX_ITER)
        return vec3(1,1,0);*/
    float dist = length(vec2(zx,zy));
    float fracIter = log2(log(dist) / log(R)) - 1.;
    iter -= fracIter;
    return hue2rgb(sqrt(iter/10.));
}

