// Fork of "Domain Repetition Hair/Fur" by Cotterzz. https://shadertoy.com/view/wfXczr
// 2025-08-15 04:55:44

// Lots added here, more params..
// lighting/shadows which I think are expensive,
// ..and with hair you can't have one without the other
// The most imprtant though is offset so the hairs aren't in straight rows. :)

// There seem to be a few artifacts with certain parameters.

// Set multisampling to 0.0 to make it ymouse dependent
// Or set it to 1. + for number of samples (so 1.0 is effectively off, 4.0 is 2x2 multisampling etc etc..)

const float MULTISAMPLING = 2.0; // Number of samples per pixel for anti-aliasing
const float THICKNESS = 0.01; // Base thickness of individual hair strands
const float SPACING = 0.03; // Distance between adjacent hair strands on the sphere surface
const float INNER_RADIUS = 0.4; // Radius of the base sphere where hair roots attach
const float OUTER_RADIUS = 0.9; // Maximum radius where hair tips can extend
const float MAX_DROOP = 0.3; // Maximum vertical droop amount for gravity effect
const float CURVE = 0.1; // Amount of inward curve/bend in drooping hair
const float CURL_AMPLITUDE = 0.0; // Amplitude of hair curl/wave effect (0 = straight)
const float CURL_FREQUENCY = 10.0; // Frequency of curl oscillations along hair length
const float UNEVEN_LENGTH = 0.0; // Variation in hair lengths (0 = uniform length)
const float RANDOM_OFFSET = 0.45; // Random positional offset for hair strand placement

vec3 straightPos; // Position in space before droop is applied (straight hair position)
vec2 sphericalCoords; // Spherical coordinates (latitude, longitude) of current point
vec2 gridCell; // Grid cell index for hair strand identification
float radialDistance; // Distance from origin to current point
float normalizedRadius; // Normalized distance from inner to outer radius (0-1)

// Signed distance function for the base sphere
float sphereSDF(vec3 p) {
    return length(p) - INNER_RADIUS; // Distance from point to sphere surface
}

// Generate 2D hash values for procedural randomness
vec2 hash2(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3))); // Mix coordinates with primes
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123); // Return values in range [-1, 1]
}

// Generate single hash value for procedural randomness
float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.78)); // Scale and wrap coordinates
    p += dot(p, p + 34.56); // Self-mixing for better distribution
    return fract(p.x * p.y); // Return value in range [0, 1]
}

// Convert 3D Cartesian coordinates to spherical (latitude, longitude)
vec2 cartesianToSpherical(vec3 p) {
    float r = length(p); // Calculate radius
    if (r < 0.001) return vec2(0.0); // Avoid division by zero at origin
    vec3 n = p / r; // Normalize to unit sphere
    return vec2(acos(clamp(n.y, -1.0, 1.0)), atan(n.z, n.x)); // Return (theta, phi) angles
}

// Apply gravity droop effect to hair position
vec3 applyDroop(vec3 p) {
    float r = length(p); // Distance from origin
    float normalized = (r - INNER_RADIUS) / (OUTER_RADIUS - INNER_RADIUS); // Normalize to hair length
    float droopFactor = normalized * normalized; // Quadratic falloff for natural droop

    vec3 droopedPos = p; // Start with original position
    droopedPos.y -= droopFactor * MAX_DROOP; // Apply downward droop

    vec3 toCenter = -normalize(vec3(p.x, 0.0, p.z)); // Horizontal direction toward center
    droopedPos += toCenter * droopFactor * CURVE; // Apply inward curve

    return droopedPos; // Return drooped position
}

// Inverse transformation to find straight position from drooped position
vec3 inverseDroop(vec3 p) {
    vec3 straight = p; // Initial guess
    for (int i = 0; i < 4; i++) { // Iterate to converge on solution
        straight += (p - applyDroop(straight)) * 0.7; // Newton-like iteration with damping
    }
    return straight; // Return approximated straight position
}

// Calculate and cache hair-related data for current position
void getHairData(vec3 p) {
    straightPos = inverseDroop(p); // Find pre-droop position
    sphericalCoords = cartesianToSpherical(straightPos); // Convert to spherical coordinates
    radialDistance = length(straightPos); // Calculate distance from origin
    normalizedRadius = (radialDistance - INNER_RADIUS) / (OUTER_RADIUS - INNER_RADIUS); // Normalize to hair length

    float gridSpacing = 0.01; // Base grid spacing for hair identification
    float lonSpacing = gridSpacing / max(sin(sphericalCoords.x), 0.1); // Adjust for sphere convergence at poles
    gridCell = vec2( // Calculate grid cell indices
        round(sphericalCoords.x / gridSpacing), // Latitude cell
        round(sphericalCoords.y / lonSpacing) // Longitude cell
    );
}

// Signed distance function for radial hair strands
float sdRadialLine(vec3 p, float spacing, float thickness) {
    getHairData(p); // Calculate hair data for current position

    vec2 latLon = sphericalCoords; // Get spherical coordinates
    float lonSpacing = spacing / max(sin(latLon.x), 0.1); // Adjust spacing for latitude
    float latIndex = round(latLon.x / spacing); // Grid index for latitude
    float lonIndex = round(latLon.y / lonSpacing); // Grid index for longitude

    if (CURL_AMPLITUDE > 0.0) { // Apply curl if enabled
        latLon.y += sin(length(p) * CURL_FREQUENCY) * CURL_AMPLITUDE; // Add sinusoidal curl
    }

    vec2 randomOffset = hash2(vec2(latIndex, lonIndex)) * RANDOM_OFFSET; // Random offset for natural look
    vec2 gridCenter = vec2( // Calculate actual hair strand center
        latIndex * spacing + randomOffset.x * spacing, // Latitude position with offset
        lonIndex * lonSpacing + randomOffset.y * lonSpacing // Longitude position with offset
    );

    vec2 toLine = latLon - gridCenter; // Vector from hair center to current point
    toLine.y *= sin(latLon.x); // Correct for spherical distortion
    float distToLine = length(toLine); // Distance to hair strand axis

    if (distToLine > spacing) return 1e10; // Early exit if too far from any hair

    float taperFactor = 1.0 - normalizedRadius; // Taper hair from root to tip
    float thicknessVariation = 0.4 + 0.8 * hash(vec2(latIndex, lonIndex)); // Random thickness variation
    float currentThickness = thickness * taperFactor * thicknessVariation; // Final thickness

    if (UNEVEN_LENGTH > 0.0) { // Apply length variation if enabled
        float hairLengthRandom = hash(vec2(latIndex, lonIndex) + 42.0); // Random length factor
        float minLength = 1.0 / (1.0 + UNEVEN_LENGTH); // Minimum hair length
        float maxLength = 0.97; // Maximum hair length (slightly less than full)
        float adjustedRadius = normalizedRadius / mix(minLength, maxLength, hairLengthRandom); // Adjust for length

        if (adjustedRadius > 1.0) return 1e10; // Hair has ended

        currentThickness *= (1.0 - normalizedRadius) * (1.0 - adjustedRadius); // Extra taper for varied length
    }

    return distToLine - currentThickness; // Return signed distance to hair strand
}

// Raymarch through the scene to find intersections
vec4 raymarch(vec3 ro, vec3 rd) {
    float t = 0.0; // Distance along ray

    for (int i = 0; i < 200; i++) { // Maximum iterations
        vec3 p = ro + rd * t; // Current position along ray
        float distFromCenter = length(p); // Distance from origin
        float d = sphereSDF(p); // Distance to base sphere

        if (distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) { // Within hair volume
            d = min(d, sdRadialLine(p, SPACING, THICKNESS)); // Include hair in distance field
        }

        if (d < 0.0005) { // Hit surface
            float objectId = 0.0; // 0 = sphere, 1 = hair
            float opacity = 1.0; // Opacity for blending

            if (distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) { // In hair region
                if (sdRadialLine(p, SPACING, THICKNESS) < sphereSDF(p)) { // Hair is closer than sphere
                    objectId = 1.0; // Mark as hair hit
                    opacity = 1.0 - normalizedRadius * 0.8; // Fade out toward tips
                }
            }

            return vec4(t, objectId, hash(gridCell), opacity); // Return hit data
        }

        t += d * 0.8; // Step along ray (0.8 for safety)
        if (t > 20.0) break; // Maximum distance reached
    }

    return vec4(-1.0); // No hit
}

// Combined distance field for shadow calculations
float mapScene(vec3 p) {
    float distFromCenter = length(p); // Distance from origin
    if (distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) { // In hair volume
        return min(sphereSDF(p), sdRadialLine(p, SPACING, THICKNESS)); // Combine sphere and hair
    }
    return sphereSDF(p); // Just sphere outside hair volume
}

// Calculate soft shadows using sphere tracing
float calcSoftShadow(vec3 ro, vec3 rd, float tmin, float tmax) {
    float res = 1.0; // Shadow accumulator (1 = no shadow)
    float t = tmin; // Start distance

    for (int i = 0; i < 6; i++) { // Limited iterations for performance
        float h = mapScene(ro + rd * t); // Distance to nearest surface
        res = min(res, 2.0 * h / t); // Accumulate shadow based on proximity
        t += clamp(h, 0.02, 0.2); // Step forward with clamped step size
        if (res < 0.005 || t > tmax) break; // Early exit if fully shadowed or reached light
    }

    return clamp(res, 0.0, 1.0); // Return shadow factor [0,1]
}

// Calculate surface normal using finite differences
vec3 calcNormal(vec3 p, float objectId) {
    if (objectId < 0.5) { // Sphere surface
        return normalize(p); // Analytical normal for sphere
    }

    vec2 e = vec2(0.001, 0.0); // Small epsilon for finite difference
    float base = mapScene(p); // Distance at center point
    return normalize(vec3( // Calculate gradient
        mapScene(p + e.xyy) - base, // X component
        mapScene(p + e.yxy) - base, // Y component
        mapScene(p + e.yyx) - base  // Z component
    ));
}

// Calculate ray-sphere intersection points
vec2 raySphere(vec3 ro, vec3 rd, float r) {
    float b = dot(ro, rd); // Projection of origin onto ray
    float c = dot(ro, ro) - r * r; // Origin distance squared minus radius squared
    float h = b * b - c; // Discriminant
    if (h < 0.0) return vec2(-1.0); // No intersection
    h = sqrt(h); // Square root of discriminant
    return vec2(-b - h, -b + h); // Near and far intersection distances
}

// Main rendering function for single sample
void mainImage0(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y; // Normalized screen coordinates

    vec2 pan = -5.*((iMouse.xy/iResolution.xy)-0.5); // Camera rotation from mouse
    float camRadius = 3.0; // Camera distance from origin
    float camHeight = pan.y; // Camera height from mouse Y

    vec3 ro = vec3(camRadius * cos(pan.x), camHeight, camRadius * sin(pan.x)); // Camera position
    vec3 target = vec3(0.0); // Look-at target (origin)
    vec3 forward = normalize(target - ro); // Camera forward direction
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward)); // Camera right direction
    vec3 up = cross(forward, right); // Camera up direction
    vec3 rd = normalize(forward + uv.x * right + uv.y * up); // Ray direction through pixel

    vec3 col = vec3(0.7); // Background color
    vec3 lightPos = vec3(2.0, 1.0, 2.0); // Light source position

    vec2 outerHit = raySphere(ro, rd, OUTER_RADIUS); // Check ray-bounding sphere intersection
    if (outerHit.x > 0.0) { // Ray enters bounding sphere
        vec3 pEnter = ro + rd * outerHit.x + rd * 0.001; // Entry point with small offset
        vec4 hit = raymarch(pEnter, rd); // Raymarch through hair volume

        if (hit.x > 0.0) { // Hit something
            vec3 pHit = pEnter + rd * hit.x; // Hit position
            vec3 normal = calcNormal(pHit, hit.y); // Surface normal
            vec3 lightDir = normalize(lightPos - pHit); // Direction to light

            float diffuse = max(dot(normal, lightDir), 0.0); // Lambertian diffuse
            float shadow = calcSoftShadow(pHit + normal * 0.001, lightDir, 0.001, length(lightPos - pHit)); // Shadow factor
            diffuse *= shadow; // Apply shadow to diffuse

            float ambient = 0.4; // Ambient light level
            vec3 hitColor; // Final hit color

            if (hit.y > 0.5) { // Hit hair
                vec3 hairColors[4] = vec3[4]( // Hair color palette
                    vec3(0.95, 0.8, 0.4), // Blonde
                    vec3(0.8, 0.5, 0.3),  // Brown
                    vec3(0.9, 0.4, 0.4),  // Red
                    vec3(0.5, 0.7, 0.9)   // Blue
                );

                int colorIndex = min(int(hit.z * 4.0), 3); // Select color based on hash
                vec3 hairBaseColor = hairColors[colorIndex]; // Get base hair color
                hitColor = hairBaseColor * (diffuse + ambient); // Apply lighting

                vec3 viewDir = -rd; // View direction
                vec3 halfDir = normalize(lightDir + viewDir); // Half vector for specular
                float specular = pow(max(dot(normal, halfDir), 0.0), 8.0); // Blinn-Phong specular
                hitColor += specular * 0.4; // Add specular highlight

                col = mix(col, hitColor, hit.w*1.5); // Blend with background using opacity
            } else { // Hit sphere
                col = vec3(0.9, 0.8, 0.8) * (diffuse + ambient); // Simple sphere shading
            }
        }
    }

    fragColor = vec4(col, 1.0); // Output final color
}

// Multisampling wrapper using Weyl sequence for anti-aliasing
void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float samples = MULTISAMPLING<1.?floor(1.+((iMouse.y/iResolution.y)*16.)):MULTISAMPLING; // Sample count (mouse Y controls if MULTISAMPLING < 1)
    vec2 jitter = vec2(0.5); // Initial jitter offset
    fragColor = vec4(0.0); // Initialize accumulator
    vec4 color; // Temporary color storage

    mainImage0(color, fragCoord); // Render center sample
    for (float k = samples; k-- > 0.5;) { // Loop for additional samples
        mainImage0(color, fragCoord + jitter - 0.5); // Render with jittered position
        fragColor += color; // Accumulate color
        jitter = fract(jitter + vec2(0.755, 0.57).yx); // Update jitter with Weyl sequence
    }

    fragColor /= samples; // Average all samples
    fragColor.a = 1.0; // Set full opacity
}

// First proof of concept for raymarched hair/fur
// I wondered if we couldn't just use domain repetition, in between an outer and inner sphere,
// using perpendicular intervals to make a grid for the hair, with the hairs as grid points on a polar plane.
// Still lots of things to do like shaping the hair more, light/shadow and adapting to more complex shapes
// I also need to use cell noise on the grid positions so the hairs arent in perfect rows.
// It's very fast though, without AA it's 60fps on intel 630 at 1200x675 with seemingly any number of hairs.

// Check out v2 here: https://www.shadertoy.com/view/3fsyzn

#define AA 4.0 // 1. is off, 4. is 2x2 etc...

float sdSphere(vec3 p, float r) {return length(p) - r;}

vec2 cartesianToSpherical(vec3 p) {
    float r = length(p);
    if(r < 0.001) return vec2(0.0);
    vec3 n = p / r;
    float lon = atan(n.z, n.x);
    float lat = acos(clamp(n.y, -1.0, 1.0));
    return vec2(lat, lon);
}

vec3 applyDroop(vec3 p) {
    float radialDist = length(p);
    float normalizedRadius = (radialDist - 0.4) / 0.7; // 0 at inner sphere, 1 at outer

    // Calculate droop amount based on distance from inner sphere
    float droopFactor = normalizedRadius * normalizedRadius;
    float droopAmount = droopFactor * 0.4; 

    vec3 droopedPos = p;
    droopedPos.y -= droopAmount;

    float curlFactor = droopFactor * 0.1;
    vec3 toCenter = -normalize(vec3(p.x, 0.0, p.z));
    droopedPos += toCenter * curlFactor;

    return droopedPos;
}

// Distance function for hair based on lat/lon grid with drooping
float sdRadialLine(vec3 p, float gridSpacing, float thickness) {
    vec3 straightPos = p;

    for(int i = 0; i < 3; i++) {
        vec3 drooped = applyDroop(straightPos);
        vec3 error = p - drooped;
        straightPos += error * 0.7;
    }

    vec2 spherical = cartesianToSpherical(straightPos);

    float lat = spherical.x;
    float lon = spherical.y;

    float lonSpacing = gridSpacing / max(sin(lat), 0.1);

    vec2 gridPos = vec2(lat, lon);
    vec2 gridCell = vec2(
        round(gridPos.x / gridSpacing) * gridSpacing,
        round(gridPos.y / lonSpacing) * lonSpacing
    );
    vec2 toLine = gridPos - gridCell;

    toLine.y *= sin(lat);
    float distToLine = length(toLine);

    if(distToLine > gridSpacing * 0.8) return 1e10;

    float radialDist = length(straightPos);
    float normalizedRadius = (radialDist - 0.5) / 0.5;

    float taperFactor = 1.0 - normalizedRadius;
    float currentThickness = thickness * taperFactor;

    return distToLine - currentThickness;
}


float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.78));
    p += dot(p, p + 34.56);
    return fract(p.x * p.y);
}

vec2 getHairID(vec3 p, float gridSpacing) {
    vec3 straightPos = p;
    for(int i = 0; i < 3; i++) {
        vec3 drooped = applyDroop(straightPos);
        vec3 error = p - drooped;
        straightPos += error * 0.7;
    }

    vec2 spherical = cartesianToSpherical(straightPos);
    float lat = spherical.x;
    float lon = spherical.y;

    float lonSpacing = gridSpacing / max(sin(lat), 0.1);

    vec2 gridCell = vec2(
        round(lat / gridSpacing),
        round(lon / lonSpacing)
    );

    return gridCell;
}

float map(vec3 p) {return sdSphere(p, 0.4);}

float mapWithLines(vec3 p, float lineSpacing, float lineThickness) {
    float innerSphere = sdSphere(p, 0.4);
    float lines = sdRadialLine(p, lineSpacing, lineThickness);
    return min(innerSphere, lines);
}

bool isBetweenSpheres(vec3 p) {
    float distFromCenter = length(p);
    return distFromCenter > 0.4 && distFromCenter < 1.0;
}

float getHairOpacity(vec3 p) {
    vec3 straightPos = p;
    for(int i = 0; i < 3; i++) {
        vec3 drooped = applyDroop(straightPos);
        vec3 error = p - drooped;
        straightPos += error * 0.7;
    }

    float radialDist = length(straightPos);
    float normalizedRadius = (radialDist - 0.5) / 0.5;
    return 1.0 - normalizedRadius * 0.8;
}

vec4 raymarchWithLines(vec3 ro, vec3 rd, float lineSpacing, float lineThickness) {
    float t = 0.0;

    for(int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;

        float d;
        if(isBetweenSpheres(p)) {
            d = mapWithLines(p, lineSpacing, lineThickness);
        } else {
            d = map(p);
        }

        if(d < 0.001) {
            float objectId = 0.0;
            float opacity = 1.0;
            vec2 hairID = getHairID(p, lineSpacing);
            if(isBetweenSpheres(p)) {
                float innerDist = sdSphere(p, 0.3);
                float lineDist = sdRadialLine(p, lineSpacing, lineThickness);

                if(lineDist < innerDist) {
                    objectId = 1.0;
                    opacity = getHairOpacity(p);
                }
            }


            return vec4(t, objectId, hash(hairID), opacity);
        }

        t += d * 0.8;

        if(t > 10.0) break;
    }

    return vec4(-1.0, -1.0, 0.0, 0.0);
}

vec2 raySphere(vec3 ro, vec3 rd, float r) {
    float b = dot(ro, rd);
    float c = dot(ro, ro) - r*r;
    float h = b*b - c;
    if(h < 0.0) return vec2(-1.0);
    h = sqrt(h);
    return vec2(-b - h, -b + h);
}

void mainImage0(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float time = iTime * 0.5;
    float radius = 3.0;
    float height = sin(time * 0.7) * 0.5;

    vec3 ro = vec3(
        radius * cos(time),
        height,
        radius * sin(time)
    );

    vec3 target = vec3(0.0, 0.0, 0.0);
    vec3 forward = normalize(target - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);

    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    vec3 col = vec3(0.3);

    float lineSpacing = iMouse.x/iResolution.x/3.;
    float lineThickness = iMouse.y/iResolution.y/50.;

    if(lineSpacing <= 0.) lineSpacing = 0.1;
    if(lineThickness <= 0.) lineThickness = 0.002;

    vec2 outerHit = raySphere(ro, rd, 1.0);

    if(outerHit.x > 0.0) {
        vec3 pEnter = ro + rd * outerHit.x;

        vec3 roInner = pEnter + rd * 0.001;
        vec4 hit = raymarchWithLines(roInner, rd, lineSpacing, lineThickness);

        if(hit.x > 0.0) {
            vec3 pHit = roInner + rd * hit.x;
            vec3 nHit = normalize(pHit);

            vec3 lightPos = vec3(2.0, 2.0, -2.0);
            vec3 lightPos2 = vec3(0.0, -3.0, -0.0);
            vec3 lightDirInner = normalize(lightPos - pHit);
            vec3 lightDirInner2 = normalize(lightPos2 - pHit);
            float diffInner = max(dot(nHit, lightDirInner), 0.0);
            float diffInner2 = max(dot(nHit, lightDirInner2), 0.0);
            float ambient = 0.5;

            vec3 hitCol;
            if(hit.y > 0.5) {
                float hairRandom = hit.z;
                vec3 haircolA = vec3(0.85, 0.6, 0.3);
                vec3 haircolB = vec3(0.5, 0.4, 0.0);
                vec3 hairBaseCol = mix(haircolA, haircolB, hairRandom);
                hitCol = hairBaseCol * (diffInner + ambient);
                hitCol += vec3(0.3,0,0) * (diffInner2 + ambient);
                float spec = pow(max(dot(reflect(-lightDirInner, nHit), -rd), 0.0), 16.0);
                hitCol += vec3(0.4) * spec;
                float opacity = hit.w;
                col = mix(col, hitCol, opacity);
            } else {
                hitCol = vec3(0.5);
                col = hitCol;
            }
        }
    }

    fragColor = vec4(col, 1.0);
}

// multisampling, from "Weyl Supersample Any Shader" by spalmer https://www.shadertoy.com/view/WXjXRt
// (forked from from "postprocess existing shaders" by FabriceNeyret2 https://www.shadertoy.com/view/NdyfRz 
void mainImage(out vec4 o, vec2 u) 
{ 
    float s = AA, k; 
    vec2 j = vec2(.5); 
    o = vec4(0); 
    vec4 c; 
    mainImage0(c, u); 
    for (k = s; k-- > .5; ) { 
        mainImage0(c, u + j - .5); 
        o += c; 
        j = fract(j + vec2(.755, .57).yx); 
    };o /= s;o.a==1.;
}

// Fork of "Domain Repetition Hair/Fur" by Cotterzz. https://shadertoy.com/view/wfXczr
// 2025-08-15 04:55:44

// Lots added here, more params..
// lighting/shadows which I think are expensive,
// ..and with hair you can't have one without the other
// The most imprtant though is offset so the hairs aren't in straight rows. :)

// There seem to be a few artifacts with certain parameters.

// Set multisampling to 0.0 to make it xmouse dependent - left to right is 1x to 16x
// Or set it to 1. + for number of samples (so 1.0 is effectively off, 4.0 is 2x2 multisampling etc etc..)

const float MULTISAMPLING = 0.0;
const float THICKNESS = 0.01;
const float SPACING = 0.03;
const float INNER_RADIUS = 0.4;
const float OUTER_RADIUS = 0.9;
const float MAX_DROOP = 0.3;
const float CURVE = 0.1;
const float CURL_AMPLITUDE = 0.0;
const float CURL_FREQUENCY = 10.0;
const float UNEVEN_LENGTH = 0.0;
const float RANDOM_OFFSET = 0.45;

vec3 straightPos;
vec2 sphericalCoords;
vec2 gridCell;
float radialDistance;
float normalizedRadius;

float sphereSDF(vec3 p) {
    return length(p) - INNER_RADIUS;
}

vec2 hash2(vec2 p) {
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}

float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.78));
    p += dot(p, p + 34.56);
    return fract(p.x * p.y);
}

vec2 cartesianToSpherical(vec3 p) {
    float r = length(p);
    if (r < 0.001) return vec2(0.0);
    vec3 n = p / r;
    return vec2(acos(clamp(n.y, -1.0, 1.0)), atan(n.z, n.x));
}

vec3 applyDroop(vec3 p) {
    float r = length(p);
    float normalized = (r - INNER_RADIUS) / (OUTER_RADIUS - INNER_RADIUS);
    float droopFactor = normalized * normalized;

    vec3 droopedPos = p;
    droopedPos.y -= droopFactor * MAX_DROOP;

    vec3 toCenter = -normalize(vec3(p.x, 0.0, p.z));
    droopedPos += toCenter * droopFactor * CURVE;

    return droopedPos;
}

vec3 inverseDroop(vec3 p) {
    vec3 straight = p;
    for (int i = 0; i < 4; i++) {
        straight += (p - applyDroop(straight)) * 0.7;
    }
    return straight;
}

void getHairData(vec3 p) {
    straightPos = inverseDroop(p);
    sphericalCoords = cartesianToSpherical(straightPos);
    radialDistance = length(straightPos);
    normalizedRadius = (radialDistance - INNER_RADIUS) / (OUTER_RADIUS - INNER_RADIUS);

    float gridSpacing = 0.01;
    float lonSpacing = gridSpacing / max(sin(sphericalCoords.x), 0.1);
    gridCell = vec2(
        round(sphericalCoords.x / gridSpacing),
        round(sphericalCoords.y / lonSpacing)
    );
}

float sdRadialLine(vec3 p, float spacing, float thickness) {
    getHairData(p);

    vec2 latLon = sphericalCoords;
    float lonSpacing = spacing / max(sin(latLon.x), 0.1);
    float latIndex = round(latLon.x / spacing);
    float lonIndex = round(latLon.y / lonSpacing);

    if (CURL_AMPLITUDE > 0.0) {
        latLon.y += sin(length(p) * CURL_FREQUENCY) * CURL_AMPLITUDE;
    }

    vec2 randomOffset = hash2(vec2(latIndex, lonIndex)) * RANDOM_OFFSET;
    vec2 gridCenter = vec2(
        latIndex * spacing + randomOffset.x * spacing,
        lonIndex * lonSpacing + randomOffset.y * lonSpacing
    );

    vec2 toLine = latLon - gridCenter;
    toLine.y *= sin(latLon.x);
    float distToLine = length(toLine);

    if (distToLine > spacing) return 1e10;

    float taperFactor = 1.0 - normalizedRadius;
    float thicknessVariation = 0.4 + 0.8 * hash(vec2(latIndex, lonIndex));
    float currentThickness = thickness * taperFactor * thicknessVariation;

    if (UNEVEN_LENGTH > 0.0) {
        float hairLengthRandom = hash(vec2(latIndex, lonIndex) + 42.0);
        float minLength = 1.0 / (1.0 + UNEVEN_LENGTH);
        float maxLength = 0.97;// * (1.0 + UNEVEN_LENGTH);
        float adjustedRadius = normalizedRadius / mix(minLength, maxLength, hairLengthRandom);

        if (adjustedRadius > 1.0) return 1e10;

        currentThickness *= (1.0 - normalizedRadius) * (1.0 - adjustedRadius);
    }

    return distToLine - currentThickness;
}

vec4 raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;

    for (int i = 0; i < 200; i++) {
        vec3 p = ro + rd * t;
        float distFromCenter = length(p);
        float d = sphereSDF(p);

        if (distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) {
            d = min(d, sdRadialLine(p, SPACING, THICKNESS));
        }

        if (d < 0.0005) {
            float objectId = 0.0;
            float opacity = 1.0;

            if (distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) {
                if (sdRadialLine(p, SPACING, THICKNESS) < sphereSDF(p)) {
                    objectId = 1.0;
                    opacity = 1.0 - normalizedRadius * 0.8;
                }
            }

            return vec4(t, objectId, hash(gridCell), opacity);
        }

        t += d * 0.8;
        if (t > 20.0) break;
    }

    return vec4(-1.0);
}

float mapScene(vec3 p) {
    float distFromCenter = length(p);
    if (distFromCenter > INNER_RADIUS && distFromCenter < OUTER_RADIUS) {
        return min(sphereSDF(p), sdRadialLine(p, SPACING, THICKNESS));
    }
    return sphereSDF(p);
}

float calcSoftShadow(vec3 ro, vec3 rd, float tmin, float tmax) {
    float res = 1.0;
    float t = tmin;

    for (int i = 0; i < 6; i++) {
        float h = mapScene(ro + rd * t);
        res = min(res, 2.0 * h / t);
        t += clamp(h, 0.02, 0.2);
        if (res < 0.005 || t > tmax) break;
    }

    return clamp(res, 0.0, 1.0);
}

vec3 calcNormal(vec3 p, float objectId) {
    if (objectId < 0.5) {
        return normalize(p);
    }

    vec2 e = vec2(0.001, 0.0);
    float base = mapScene(p);
    return normalize(vec3(
        mapScene(p + e.xyy) - base,
        mapScene(p + e.yxy) - base,
        mapScene(p + e.yyx) - base
    ));
}

vec2 raySphere(vec3 ro, vec3 rd, float r) {
    float b = dot(ro, rd);
    float c = dot(ro, ro) - r * r;
    float h = b * b - c;
    if (h < 0.0) return vec2(-1.0);
    h = sqrt(h);
    return vec2(-b - h, -b + h);
}

void mainImage0(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec2 pan = -5.*((iMouse.xy/iResolution.xy)-0.5);// = iTime * 0.5;
    float camRadius = 3.0;
    float camHeight = pan.y;

    vec3 ro = vec3(camRadius * cos(pan.x), camHeight, camRadius * sin(pan.x));
    vec3 target = vec3(0.0);
    vec3 forward = normalize(target - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    vec3 col = vec3(0.7);
    vec3 lightPos = vec3(2.0, 1.0, 2.0);

    vec2 outerHit = raySphere(ro, rd, OUTER_RADIUS);
    if (outerHit.x > 0.0) {
        vec3 pEnter = ro + rd * outerHit.x + rd * 0.001;
        vec4 hit = raymarch(pEnter, rd);

        if (hit.x > 0.0) {
            vec3 pHit = pEnter + rd * hit.x;
            vec3 normal = calcNormal(pHit, hit.y);
            vec3 lightDir = normalize(lightPos - pHit);

            float diffuse = max(dot(normal, lightDir), 0.0);
            float shadow = calcSoftShadow(pHit + normal * 0.001, lightDir, 0.001, length(lightPos - pHit));
            diffuse *= shadow;

            float ambient = 0.4;
            vec3 hitColor;

            if (hit.y > 0.5) {
                vec3 hairColors[4] = vec3[4](
                    vec3(0.95, 0.8, 0.4),
                    vec3(0.8, 0.5, 0.3),
                    vec3(0.9, 0.4, 0.4),
                    vec3(0.5, 0.7, 0.9)
                );

                int colorIndex = min(int(hit.z * 4.0), 3);
                vec3 hairBaseColor = hairColors[colorIndex];
                hitColor = hairBaseColor * (diffuse + ambient);

                vec3 viewDir = -rd;
                vec3 halfDir = normalize(lightDir + viewDir);
                float specular = pow(max(dot(normal, halfDir), 0.0), 8.0);
                hitColor += specular * 0.4;

                col = mix(col, hitColor, hit.w*1.5);
            } else {
                col = vec3(0.9, 0.8, 0.8) * (diffuse + ambient);
            }
        }
    }

    fragColor = vec4(col, 1.0);
}

// multisampling, from "Weyl Supersample Any Shader" by spalmer https://www.shadertoy.com/view/WXjXRt
// (forked from from "postprocess existing shaders" by FabriceNeyret2 https://www.shadertoy.com/view/NdyfRz 
void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float samples = MULTISAMPLING<1.?floor(max(1.,((iMouse.x/iResolution.x)*16.))):MULTISAMPLING;
    vec2 jitter = vec2(0.5);
    fragColor = vec4(0.0);
    vec4 color;

    mainImage0(color, fragCoord);
    for (float k = samples; k-- > 0.5;) {
        mainImage0(color, fragCoord + jitter - 0.5);
        fragColor += color;
        jitter = fract(jitter + vec2(0.755, 0.57).yx);
    }

    fragColor /= samples;
    fragColor.a = 1.0;
}

