// Fork of "Colourful Apollonian III - short" by FabriceNeyret2. https://shadertoy.com/view/cl3GDl
//     and "Colourful Apollonian III"         by mla.            https://shadertoy.com/view/wdsfWX
float ang;
vec4 getApollonianColor(vec2 uv, vec2 interactionPoint) {
    vec2 z = uv;

    float N = 3.;
    float a = 3.14159/N;
    float D = 1./cos(a);
    float T = tan(a);
    float K = D - T;

    float maxDepth = 20.;
    float depth = 0.;
    vec2 t = vec2(0);
    float s = T*T;

    float outerBound = (D + T) / 3.77;
    if (length(uv) > outerBound) {
        return vec4(0, 0, 0, 0);
    }

    if (length(interactionPoint) < outerBound && length(interactionPoint) > 0.01) {
        s = dot(interactionPoint, interactionPoint);
        vec2 invCenter = interactionPoint / s;
        float invScale = 1./s - 1.;
        z = (z - invCenter) / dot(z - invCenter, z - invCenter) * invScale + invCenter;
    }

    s = T*T;

    for (; depth < maxDepth && length(z) > K; depth++) {
        z *= -K / dot(z,z);

        for (float j = 0.; j++ < N;) {
            t = D * sin(6.28*j/N + vec2(0,1.57));
            if (length(z-t) < T) {
                z = (z-t) / dot(z-t,z-t) * s + t;
                j = N;
            }
        }
    }

    float alpha = 0.0;
    if (length(z) < K) alpha = 1.0;

    for (float j = 0.; j < N; j++) {
        vec2 center = D * sin(6.28*j/N + vec2(0,1.57));
        if (length(z - center) < T) alpha = 1.0;
    }

    if (alpha < 0.05 || depth > 19.9 || (ang>0.3&&ang<1.6)) {
        return vec4(0.0);
    }

    vec3 color = 0.5 + 0.5 * cos(depth * 0.5 + vec3(0, 2.1, 4.2));
    return vec4(color, 1.0);
}

float sdTorusDisks(vec3 p, out float diskIndex) {
    float torusRadius = 1.5;
    float diskRadius = 0.55;
    float diskThickness = 0.005;
    float spacing = 0.005;

    float angle = atan(p.z, p.x);

    float nearestDiskAngle = round(angle / spacing) * spacing;
    diskIndex = nearestDiskAngle / spacing; 

    float c = cos(-nearestDiskAngle);
    float s = sin(-nearestDiskAngle);
    vec3 pRot;
    pRot.x = c * p.x - s * p.z;
    pRot.y = p.y;
    pRot.z = s * p.x + c * p.z;

    float distAlongRadius = pRot.x - torusRadius;
    float distInDisk = length(vec2(distAlongRadius, pRot.y)) - diskRadius;

    return max(distInDisk, abs(pRot.z) - diskThickness);
}

void mainImage0(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy;
    vec2 uv = (u - 0.5*R) / R.y;

    vec3 ro = vec3(3.33, 1.5, 4.5);
    vec3 ta = vec3(0, 0, 0);
    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww, vec3(0,1,0)));
    vec3 vv = cross(uu, ww);
    vec3 rd = normalize(uv.x*uu + uv.y*vv + 2.0*ww);

    float t = 0.0;
    vec3 col = vec3(0.05);

    for (int i = 0; i < 220; i++) {
        vec3 p = ro + rd * t;

        float diskIndex;
        float d = sdTorusDisks(p, diskIndex);
        float angle = atan(p.z, p.x);
        ang = angle;
        if (d < 0.001) {
            float r = length(p.xz);
            vec2 diskUV = vec2(r - 1.5, p.y);

            vec2 interactionPoint = vec2(
                sin(angle+iTime/2.) * 0.6,
                cos(angle+iTime/1.7) * 0.6
            );

            vec4 gasket = getApollonianColor(diskUV*2.2, interactionPoint);

            if (gasket.a > 0.1) {
                col = gasket.rgb;

                col *= 0.9 + 0.1 * sin(diskIndex * 0.001);

                vec3 n = normalize(vec3(p.x, 0, p.z));
                float diff = 0.7 + 0.3 * dot(n, normalize(vec3(1, 1, 0)));
                col *= diff;
            } else {
                t += 0.01;
                continue;
            }
            break;
        }

        t += max(d, 0.0001);
        if (t > 20.0) break;
    }

    O = vec4(col, 1.0);
}
void mainImage(out vec4 o, vec2 u) 
{ 
    float s =2., k; 
    vec2 j = vec2(.5); 
    o = vec4(0); 
    vec4 c; 
    mainImage0(c, u); 
    for (k = s; k-- > .5; ) { 
