float ang;
vec4 getCymaticColor(vec2 uv, vec3 inpt){
    uv *= 0.1; // Scale down for better view
    if (length(uv) > 16.) {return vec4(0);};
    // Interactive parameters
    float waveSpacing = inpt.x;  // Distance of outer sources
    float centralFreq = inpt.y;  // Central wave frequency
    float outerFreq = inpt.z;                      // Animated outer frequency    

    // Track waves separately for coloring
    float centerWave = sin(length(uv) * centralFreq);

    float topWave = sin(length(uv + vec2(0.0, waveSpacing)) * outerFreq);
    float bottomWave = sin(length(uv + vec2(0.0, -waveSpacing)) * outerFreq);
    float leftWave = sin(length(uv + vec2(-waveSpacing, 0.0)) * outerFreq);
    float rightWave = sin(length(uv + vec2(waveSpacing, 0.0)) * outerFreq);

    // Group waves by axis
    float verticalWaves = topWave + bottomWave;
    float horizontalWaves = leftWave + rightWave;

    // Total interference pattern
    float totalWaves = centerWave + topWave + bottomWave + leftWave + rightWave;
    totalWaves = pow(totalWaves, .1)*2.;
    //totalWaves = totalWaves * 0.2 + 0.5;


    // Option 2: Color by dominant direction
vec3 waveColor = vec3(
    0.5 + 0.5 * horizontalWaves / 2.0,  // Red: horizontal
    0.5 + 0.5 * verticalWaves / 2.0,    // Green: vertical
    0.5 + 0.5 * centerWave              // Blue: center
);


    // Mix between black and colored based on total wave intensity
    // This keeps the dark interference patterns black
   vec3 finalColor = waveColor;//mix(vec3(0.0), waveColor, smoothstep(0.5, 0.6, totalWaves));
    //vec3 finalColor = mix(vec3(0.0), waveColor, smoothstep(0.3, 0.7, totalWaves));
     //vec3 finalColor = mix(vec3(0.0), waveColor, totalWaves);

    if (totalWaves < 0.01 || (ang>0.0&&ang<1.6)) {
        return vec4(0.0);
    }

    return vec4(finalColor, totalWaves);

}



float sdTorusDisks(vec3 p, out float diskIndex) {
    float torusRadius = 1.5;
    float diskRadius = 0.55;
    float diskThickness = 0.0005;
    float spacing = 0.0005;

    float angle = atan(p.z, p.x);

    float nearestDiskAngle = round(angle / spacing) * spacing;
    diskIndex = nearestDiskAngle / spacing; 

    float c = cos(-nearestDiskAngle);
    float s = sin(-nearestDiskAngle);
    vec3 pRot;
    pRot.x = c * p.x - s * p.z;
    pRot.y = p.y;
    pRot.z = s * p.x + c * p.z;

    float distAlongRadius = pRot.x - torusRadius;
    float distInDisk = length(vec2(distAlongRadius, pRot.y)) - diskRadius;

    return max(distInDisk, abs(pRot.z) - diskThickness);
}

