float getAudioSample(sampler2D buffer, float sampleOffset, vec2 resolution) {
    int totalOffset = int(sampleOffset) % 512;  // Wrap around 512 samples
    int row = 1 + (totalOffset / int(resolution.x));
    int col = totalOffset % int(resolution.x);

    if (row <= 8 && col < int(resolution.x)) {
        return texelFetch(buffer, ivec2(col, row), 0).r * 2.0 - 1.0;  
    }
    return 0.0;
}
float ang;
vec4 getCymaticColor(vec2 uv, float delay){
   uv *= 0.1; // Scale down for better view
    if (length(uv) > 16.) {return vec4(0);};

        // Interactive parameters from sliders
    float waveSpeed =  5.;       // Wave propagation speed

    waveSpeed += delay;
    float reflectionStrength = 0.5;           // Reflection intensity
    float visualGain  =5.5;        // Visualization sensitivity
    // Define virtual sound sources
    vec2 source1 = vec2(0.0, 0.0);      // Center
    vec2 source2 = vec2(-12.0, 0.0);    // Left
    vec2 source3 = vec2(12.0, 0.0);     // Right
    vec2 source4 = vec2(0.0, -12.0);    // Bottom
    vec2 source5 = vec2(0.0, 12.0);     // Top

    // Calculate sample delay based on distance (in sample units)
    // Assuming ~60fps and 44100Hz audio, each frame has ~735 samples
    // But we only get 512, so scale accordingly
    float sampleRate = 512.0; // samples per frame

    float delay1 = length(uv - source1) * waveSpeed;
    float delay2 = length(uv - source2) * waveSpeed;
    float delay3 = length(uv - source3) * waveSpeed;
    float delay4 = length(uv - source4) * waveSpeed;
    float delay5 = length(uv - source5) * waveSpeed;

    // Get audio samples at each delay
    float wave1 = getAudioSample(iChannel0, delay1, iResolution.xy);
    float wave2 = getAudioSample(iChannel0, delay2, iResolution.xy) * reflectionStrength * 0.8;
    float wave3 = getAudioSample(iChannel0, delay3, iResolution.xy) * reflectionStrength * 0.8;
    float wave4 = getAudioSample(iChannel0, delay4, iResolution.xy) * reflectionStrength * 0.7;
    float wave5 = getAudioSample(iChannel0, delay5, iResolution.xy) * reflectionStrength * 0.7;

    // Create interference pattern
    float totalWaves = (wave1 + wave2 + wave3 + wave4 + wave5) * visualGain;

    // Add some continuous wave effect based on accumulated phase
    float phase = totalWaves * 10.0 + iTime * 2.0;
    float continuousWave = sin(length(uv) * 0.5 + phase);

    // Mix direct audio with continuous visualization
    totalWaves = totalWaves * 0.7 + continuousWave * 0.3 * abs(totalWaves);


    // Create color based on wave components
    vec3 waveColor = vec3(
        0.5 + 0.3 * (wave2 + wave3),  // Horizontal (red channel)
        0.5 + 0.3 * (wave4 + wave5),  // Vertical (green channel)
        0.5 + 0.5 * wave1              // Center (blue channel)
    );


   // Apply wave intensity
    float intensity = 0.5 + 0.5 * tanh(totalWaves);
    vec3 finalColor = waveColor * intensity;

    // Add contour lines for cymatic pattern emphasis
    float contourLines = abs(fract(totalWaves * 3.0 + 0.5) - 0.5);
    finalColor *= 0.6 + 0.4 * smoothstep(0.1, 0.2, contourLines);

    // Circular boundary
    float dist = length(uv);
    float circleMask = 1.0 - smoothstep(15.0, 16.5, dist);
    finalColor *= circleMask;

    if (totalWaves < 0.01 || (ang>0.0&&ang<1.6)) {
        return vec4(0.0);
    }
      //finalColor = tanh(pow(finalColor*3., vec3(1.2)));

    return vec4(finalColor, totalWaves);

}



