        // Map pixel position to audio sample index
        int row = coord.y - 1;  // 0-7
        int sampleIndex = row * int(iResolution.x) + coord.x;

        if (sampleIndex < 512) {
            // Store current frame's audio sample
            float audioSample = texture(iChannel1, vec2(float(sampleIndex) / 512.0, 1.0)).r;
            color = vec4(audioSample, audioSample, audioSample, 1.0);
        } else {
            // Beyond 512 samples, store previous frame's data for rolling history
            // Shift data from row above (previous frame position)
            if (coord.y > 1) {
                color = texelFetch(iChannel0, ivec2(coord.x, coord.y - 1), 0);
            } else {
                color = vec4(0);
            }
        }
    }
    else {
        color = vec4(0);
    }
}

