        sphere.normal = normalize(subpoint-sphere.position.xyz);
        float dispb = shape.size.x-distance(sphere.position.xyz, shape.position);
        if(dispb>0.){
            if(dispb<npixel && AA){
                if(sphere.position.z<shape.position.z){
                    sphere.colour.a = dispb/npixel;
                } else {
                    sphere.normal = mix(sphere.normal,oldnorm, 1.-(dispb/npixel));
                }
            }
            sphere.sd = -dispb;
            return sphere;
        } else {
            return Surface(vec3(0), vec4(0), vec4(0), false, -dispb);
        }

    } else if(dispa2>0.) {
        float dist = distance(coords, subpoint2.xy);
        vec2 distv = coords-subpoint2.xy;
        float height = -sqrt((shape.srad*shape.srad)-(dist*dist));
        sphere.position.xyz = vec3(coords, height+subpoint2.z);
        vec3 oldnorm = sphere.normal;
        sphere.normal = normalize(subpoint2-sphere.position.xyz);
        float dispb = shape.size.x-distance(sphere.position.xyz, shape.position);
        if(dispb>0.){
            if(dispb<npixel && AA){
                if(sphere.position.z<shape.position.z){
                    sphere.colour.a = dispb/npixel;
                } else {
                    sphere.normal = mix(sphere.normal,oldnorm, 1.-(dispb/npixel));
                }
            }
            sphere.sd = -dispb;
            return sphere;
        } else {
            return Surface(vec3(0), vec4(0), vec4(0), false, -dispb);
        }

    } else {
        if(-dispa<pixel&&subpoint.z<shape.position.z){

            sphere.colour.a = -dispa/pixel;
            sphere.sd = -dispa;
        }
        if(-dispa2<pixel&&subpoint2.z<shape.position.z){

            sphere.colour.a = -dispa2/pixel;
            sphere.sd = -dispa2;
        }
        return sphere;
    }
}

Surface ellipsoidOSF( Shape shape, vec2 coords){
    // size.x and size.y should be the same, different width and depth of ellipsoid is broken
    // size.z is for the height, which should be different or you'll just get a sphere

    vec3 n = normalize(shape.normal);

    // Generate orthonormal tangent and bitangent vectors
    vec3 t, bv;
    vec3 up = vec3(0.0, 0.0, 1.0);
    if (abs(dot(n, up)) > 0.999) {
        up = vec3(0.0, 1.0, 0.0);
    }
    t = normalize(cross(up, n));
    bv = cross(n, t);
    mat3 R = mat3(t, bv, n); // Rotation matrix

    // Calculate dx and dy from the center
    float dx = coords.x - shape.position.x;
    float dy = coords.y - shape.position.y;

    // Coefficients for the quadratic equation
    float A1 = R[0].z;
    float A2 = R[1].z;
    float A3 = R[2].z;

    float B1 = dot(R[0].xy, vec2(dx, dy)) - R[0].z * shape.position.z;
    float B2 = dot(R[1].xy, vec2(dx, dy)) - R[1].z * shape.position.z;
    float B3 = dot(R[2].xy, vec2(dx, dy)) - R[2].z * shape.position.z;

    float a = shape.size.x, b = shape.size.y, c = shape.size.z;
    float A = (A1*A1)/(a*a) + (A2*A2)/(b*b) + (A3*A3)/(c*c);
    float B = 2.0 * ( (A1*B1)/(a*a) + (A2*B2)/(b*b) + (A3*B3)/(c*c) );
    float C = (B1*B1)/(a*a) + (B2*B2)/(b*b) + (B3*B3)/(c*c) - 1.0;
    float alpha = 1.;
    float discriminant = B*B - 4.0*A*C;
    float ed = discriminant/(7./(a*b*c));
    if (discriminant < 0.0) return Surface(vec3(0), vec4(0), vec4(0), false, -ed);

    if(ed<pixel){
            alpha = ed/pixel;
        }
    float sqrtD = sqrt(discriminant);

    float t_intersect = (-B + sqrtD) / (2.0*A);
    float b_intersect = (-B - sqrtD) / (2.0*A);

    // Compute local normal components
    float Qx = (A1 * t_intersect + B1) / a;
    float Qy = (A2 * t_intersect + B2) / b;
    float Qz = (A3 * t_intersect + B3) / c;

    // Transform normal to world space and normalize
    vec3 normal = normalize(R * vec3(Qx/a, Qy/b, Qz/c));
    //return vec4(normal, t_intersect);
    return Surface(normal, vec4(coords, t_intersect, b_intersect), vec4(shape.colour, alpha), true, -ed);
}

Surface ellipSubOSF( Shape shape, vec2 coords){
    // size.x is radius of sphere
    // srad is radius of sphere to subtract
    // spolar.w is distance between centres of spheres
    // spolar.xyz is a normal to determine direction of subtract sphere
    Surface sphere = ellipsoidOSF(shape, coords);
    if(!sphere.ib){ return sphere; }
    vec3 subpoint = shape.position + (normalize(shape.spolar.xyz) * shape.spolar.w);
    float dispa = distance(subpoint, sphere.position.xyz)-shape.srad;
    float dispb = distance(subpoint, sphere.position.xyw)-shape.srad;
    float dist = distance(coords, subpoint.xy);
    vec2 distv = coords-subpoint.xy;
    float height = -sqrt((shape.srad*shape.srad)-(dist*dist));
    if(dispa>0.){


        if(dispa<npixel&&subpoint.z<=shape.position.z){  // overhang edge

            sphere.colour.a = dispa/pixel;
        } else if(AA && dispa<npixel&&subpoint.z>shape.position.z){  // inside edge

            vec3 nnormal = normalize(subpoint-vec3(coords, height+subpoint.z));
            if(abs(nnormal.x) <0.99){
                sphere.normal =mix(nnormal,sphere.normal, dispa/npixel);
            }
        }
        sphere.sd = -dispa;
        return sphere;
    } else if(dispb>0.){
        sphere.position.xyz = vec3(coords, height+subpoint.z);
        sphere.normal = normalize(subpoint-sphere.position.xyz);
        if(dispb<npixel && AA){ // inside overhang edge

            sphere.colour.a = dispb/npixel;
        }
        sphere.sd = -dispb;
        return sphere;
    } else { return Surface(vec3(0), vec4(0), vec4(0), false, -dispb);}
}

