    vec3 bposition = shape.position + (normalize(shape.normal) * shape.size.x);
    if(distance(bposition, egg.position.xyz)>(shape.size.x*1.4)){
        shape.size.xy += pixel/2.;
        egg = sphereSubOSF( shape, coords );
    }
    return egg;
}


Surface eggOSF( Shape shape, vec2 coords){

    Surface egg = ellipsoidOSF(shape, coords);

    vec3 bposition = shape.position + (normalize(shape.normal) * shape.size.x);
    if(distance(bposition, egg.position.xyz)>(shape.size.x*1.4)){

        egg = sphereOSF( shape, coords );
    }
    return egg;
}


Surface shapeOSFCreate( Shape shape, vec2 coords){
    switch (shape.type) {
        case 0:
            return sphereOSF( shape, coords);
        case 1:
            return ellipsoidOSF( shape, coords);
        case 2:
            return eggOSF( shape, coords);
        case 3:
            return sphereSubOSF( shape, coords);
        case 4:
            return ellipSubOSF( shape, coords);
        case 5:
            return eggSubOSF( shape, coords);
        case 6:
            return sphere2SubOSF( shape, coords);
        case 7:
            return ellip2SubOSF( shape, coords);
        case 8:
            return egg2SubOSF( shape, coords);

    }
}



float atan2(in float y, in float x)
{
    return x == 0.0 ? sign(y)*1.570796 : atan(y, x);
}

vec3 addRotation(Shape shape, float rot){
    float angle = atan2(shape.position.x, shape.position.z);
    float rad = length(shape.position.xz);
    angle += rot;
    //x = r cos θ , y = r sin θ
    float newx = rad*cos(angle);
    float newz = rad*sin(angle);
    return vec3(newx, shape.position.y, newz);
}

vec3 addRotationIq(Shape shape, float rot){
    float co = cos(rot);
    float si = sin(rot);
    return vec3(shape.position.z*co-shape.position.x*si, shape.position.y, shape.position.z*si+shape.position.x*co);
}

vec3 addRotationToNormalIq(vec3 norm, float rot){
    float co = cos(rot);
    float si = sin(rot);
    return vec3(norm.z*co-norm.x*si, 
                norm.y,
                norm.z*si+norm.x*co);
}

vec3 addRotationToNormal(vec3 norm, float rot){
    float angle = atan(norm.x/norm.z);
    float rad = length(norm.xz);
    angle += rot;
    float newx = rad*cos(angle);
    float newz = rad*sin(angle);
    return vec3(newx, norm.y, newz);

}

Shape rotateShape(Shape shape, float rt){
        shape.normal = addRotationToNormalIq(shape.normal, rt);
        shape.spolar.xyz = addRotationToNormalIq(shape.spolar.xyz, rt);
        shape.position = addRotationIq(shape, rt);
    return shape;
}

Surface shapeOSF( Shape shape, vec2 coords){

