float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdPlane(vec3 p, float h) {
    return p.y - h;
}

float sdPyramid(vec3 p, float h) {
    float m2 = h * h + 0.25;
    p.xz = abs(p.xz);
    if (p.z > p.x) {
        p.xz = p.zx;
    }
    p.xz -= vec2(0.5);
    vec3 q = vec3(p.z, h * p.y - 0.5 * p.x, h * p.x + 0.5 * p.y);
    float s = max(-q.x, 0.0);
    float t = clamp((q.y - 0.5 * p.z) / (m2 + 0.25), 0.0, 1.0);
    float a = m2 * (q.x + s) * (q.x + s) + q.y * q.y;
    float b = m2 * (q.x + 0.5 * t) * (q.x + 0.5 * t) + (q.y - m2 * t) * (q.y - m2 * t);
    float d2 = (min(q.y, -q.x * m2 - 0.5 * q.y) > 0.0) ? 0.0 : min(a, b);
    return sqrt((d2 + q.z * q.z) / m2) * sign(max(q.z, -p.y));
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h);
}

mat3 rotateX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(1, 0, 0, 0, c, -s, 0, s, c);
}

mat3 rotateY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, 0, s, 0, 1, 0, -s, 0, c);
}

mat3 rotateZ(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, -s, 0, s, c, 0, 0, 0, 1);
}

float hash(vec3 p) {
    p = fract(p * 0.3183099 + 0.1);
    p *= 17.0;
    return fract(p.x * p.y * p.z * (p.x + p.y + p.z));
}

float noise(vec3 x) {
    vec3 i = floor(x);
    vec3 f = fract(x);
    f = f * f * (3.0 - 2.0 * f);

    return mix(mix(mix(hash(i + vec3(0, 0, 0)), 
                       hash(i + vec3(1, 0, 0)), f.x),
                   mix(hash(i + vec3(0, 1, 0)), 
                       hash(i + vec3(1, 1, 0)), f.x), f.y),
               mix(mix(hash(i + vec3(0, 0, 1)), 
                       hash(i + vec3(1, 0, 1)), f.x),
                   mix(hash(i + vec3(0, 1, 1)), 
                       hash(i + vec3(1, 1, 1)), f.x), f.y), f.z);
}

float fbm(vec3 p) {
    float f = 0.0;
    float amp = 0.5;
    for(int i = 0; i < 5; i++) {
        f += amp * noise(p);
        p *= 2.0;
        amp *= 0.5;
    }
    return f;
}

vec3 getSkullPosition(float time) {

    float emergeFactor = smoothstep(0.0, 10.0, time);
    float t1 = time * 0.4;
    float t2 = time * 0.3;
    float t3 = time * 0.7;

    float x = sin(t1) * 2.0;
    float z = sin(t1 * 2.0) * 1.5;

    float y = -4.0 + emergeFactor * 6.0 + sin(t2) * 1.0 + cos(t3) * 0.8;

    x += cos(t2) * 1.0;
    z += sin(t2 * 0.5) * 1.0;

    return vec3(x, y, z);
}


mat3 lookAtRotation(vec3 forward, vec3 up) {
    vec3 f = normalize(forward);
    vec3 r = normalize(cross(up, f));
    vec3 u = cross(f, r);
    return mat3(r, u, f);
}

mat3 getSkullRotation(float time) {

    float dt = 0.01; 
    vec3 pos1 = getSkullPosition(time);
    vec3 pos2 = getSkullPosition(time - dt);
    vec3 velocity = (pos2 - pos1) / dt;


    if(length(velocity) < 0.01) {
        velocity = vec3(0.0, 0.0, 1.0);
    }


    vec3 up = vec3(0.0, 1.0, 0.0);
    mat3 velocityRotation = lookAtRotation(velocity, up);


    float tilt = time * 0.5;
    mat3 bankRotation = rotateZ(sin(time * 0.3) * 0.15);

    return velocityRotation * bankRotation;
}

float cloudDensity(vec3 p) {
    float s = 0.0;
    float n = 0.1;


    for(int i = 0; i < 4; i++) {
        s -= abs(dot(sin(iTime * 0.5 + p / n), vec3(1.0))) * n * 0.3;
        n *= 2.0;
    }

    return s;
}

float sceneSDF(vec3 p) {
    vec3 skullPos = getSkullPosition(iTime);
    mat3 skullRot = getSkullRotation(iTime);
    vec3 localP = skullRot * (p - skullPos);

    vec3 d_node_1_p = localP;

    d_node_1_p *= rotateX(-0.0873);
    d_node_1_p *= rotateY(-0.0349);
    float d_node_1_scale = max(0.950, 0.0001);
    d_node_1_p /= d_node_1_scale;
    vec3 d_node_1_warp = d_node_1_p;
    d_node_1_warp.x = abs(d_node_1_warp.x);
    vec3 d_node_0_p = d_node_1_warp;
    d_node_0_p -= vec3(0.700, 0.100, 0.300);
    d_node_0_p *= rotateX(0.3491);
    d_node_0_p *= rotateY(0.2967);
    d_node_0_p *= rotateZ(0.3142);
    float d_node_0_scale = max(1.000, 0.0001);
    d_node_0_p /= d_node_0_scale;
    float d_node_0 = sdSphere(d_node_0_p, 0.400);
    d_node_0 *= d_node_0_scale;
    float d_node_1 = d_node_0;
    d_node_1 *= d_node_1_scale;
    vec3 d_node_16_p = localP;
    d_node_16_p -= vec3(0.000, 0.000, 0.000);
    float d_node_16_scale = max(1.000, 0.0001);
    d_node_16_p /= d_node_16_scale;
    vec3 d_node_14_p = d_node_16_p;
    d_node_14_p -= vec3(0.000, -1.900, -0.600);
    float d_node_14_scale = max(0.550, 0.0001);
    d_node_14_p /= d_node_14_scale;
    vec3 d_node_14_warp = d_node_14_p;
    d_node_14_warp.x = abs(d_node_14_warp.x);
    vec3 d_node_13_p = d_node_14_warp;
    d_node_13_p -= vec3(0.500, 1.300, -0.800);
    float d_node_13_scale = max(2.500, 0.0001);
    d_node_13_p /= d_node_13_scale;
    vec3 d_node_13_warp = d_node_13_p;
    d_node_13_warp.x = abs(d_node_13_warp.x);
    vec3 d_node_12_p = d_node_13_warp;
    d_node_12_p -= vec3(0.100, 0.000, 1.300);
    float d_node_12_scale = max(0.400, 0.0001);
    d_node_12_p /= d_node_12_scale;
    float d_node_12 = sdBox(d_node_12_p, vec3(0.150, 0.200, 0.150));
    d_node_12 *= d_node_12_scale;
    float d_node_13 = d_node_12;
    d_node_13 *= d_node_13_scale;
    float d_node_14 = d_node_13;
    d_node_14 *= d_node_14_scale;
    vec3 d_node_4_p = d_node_16_p;
    d_node_4_p -= vec3(0.000, 0.000, 0.000);
    float d_node_4_scale = max(1.000, 0.0001);
    d_node_4_p /= d_node_4_scale;
    vec3 d_node_2_p = d_node_4_p;
    d_node_2_p -= vec3(0.000, 0.000, 0.000);
    float d_node_2_scale = max(1.000, 0.0001);
    d_node_2_p /= d_node_2_scale;
    vec3 d_node_2_warp = d_node_2_p;
    d_node_2_warp.x = abs(d_node_2_warp.x);
    vec3 d_node_6_p = d_node_2_warp;
    d_node_6_p -= vec3(0.000, 0.000, 0.000);
    float d_node_6_scale = max(0.850, 0.0001);
    d_node_6_p /= d_node_6_scale;
    vec3 d_node_6_warp = d_node_6_p;
    float d_node_6_angle = 0.200 * d_node_6_warp.x;
    mat2 d_node_6_mat = mat2(cos(d_node_6_angle), -sin(d_node_6_angle), sin(d_node_6_angle), cos(d_node_6_angle));
    vec2 d_node_6_rot = d_node_6_mat * d_node_6_warp.xy;
    d_node_6_warp.xy = d_node_6_rot;
    vec3 d_node_3_p = d_node_6_warp;
    d_node_3_p -= vec3(0.800, 0.000, 0.000);
    d_node_3_p *= rotateZ(-0.2443);
    float d_node_3_scale = max(1.000, 0.0001);
    d_node_3_p /= d_node_3_scale;
    float d_node_3 = sdBox(d_node_3_p, vec3(0.500, 0.400, 1.050));
    d_node_3 *= d_node_3_scale;
    float d_node_6 = d_node_3;
    d_node_6 *= d_node_6_scale;
    float d_node_2 = d_node_6;
    d_node_2 *= d_node_2_scale;
    vec3 d_node_11_p = d_node_4_p;
    d_node_11_p -= vec3(0.000, 0.000, 0.000);
    float d_node_11_scale = max(1.000, 0.0001);
    d_node_11_p /= d_node_11_scale;
    vec3 d_node_10_p = d_node_11_p;
    d_node_10_p -= vec3(0.000, -0.600, 0.900);
    d_node_10_p *= rotateX(0.2618);
    d_node_10_p *= rotateY(-0.7505);
    float d_node_10_scale = max(0.400, 0.0001);
    d_node_10_p /= d_node_10_scale;
    float d_node_10 = sdPyramid(d_node_10_p, 2.300);
    d_node_10 *= d_node_10_scale;
    vec3 d_node_8_p = d_node_11_p;
    d_node_8_p -= vec3(0.000, 0.000, 0.000);
    float d_node_8_scale = max(1.000, 0.0001);
    d_node_8_p /= d_node_8_scale;
    vec3 d_node_5_p = d_node_8_p;
    d_node_5_p -= vec3(0.000, 0.000, -0.700);
    float d_node_5_scale = max(1.000, 0.0001);
    d_node_5_p /= d_node_5_scale;
    float d_node_5 = sdSphere(d_node_5_p, 1.650);
    d_node_5 *= d_node_5_scale;
    vec3 d_node_9_p = d_node_8_p;
    d_node_9_p -= vec3(0.000, 0.000, 0.000);
    float d_node_9_scale = max(1.000, 0.0001);
    d_node_9_p /= d_node_9_scale;
    vec3 d_node_7_p = d_node_9_p;
    d_node_7_p -= vec3(0.000, -1.100, 0.400);
    float d_node_7_scale = max(0.800, 0.0001);
    d_node_7_p /= d_node_7_scale;
    float d_node_7 = sdBox(d_node_7_p, vec3(0.550, 0.750, 0.250));
    d_node_7 *= d_node_7_scale;
    float d_node_9 = d_node_7 - 0.370;
    d_node_9 *= d_node_9_scale;
    float d_node_8 = opSmoothUnion(d_node_5, d_node_9, 0.770);
    d_node_8 *= d_node_8_scale;
    float d_node_11 = opSmoothSubtraction(d_node_10, d_node_8, 0.290);
    d_node_11 *= d_node_11_scale;
    float d_node_4 = opSmoothSubtraction(d_node_2, d_node_11, 0.300);
    d_node_4 *= d_node_4_scale;
    float d_node_16 = opSmoothSubtraction(d_node_14, d_node_4, 0.460);
    d_node_16 *= d_node_16_scale;

    float skull = min(d_node_1, d_node_16);


    float cloudLayer = cloudDensity(p) + 4.0;

    // I dont think this works
    return opSmoothUnion(skull, cloudLayer, 1.5);
}

float eyesSDF(vec3 p) {
    vec3 skullPos = getSkullPosition(iTime);
    mat3 skullRot = getSkullRotation(iTime);
    vec3 localP = skullRot * (p - skullPos);

    vec3 d_node_1_p = localP;
    d_node_1_p -= vec3(0.000, 0.000, 0.000);
    d_node_1_p *= rotateX(-0.0873);
    d_node_1_p *= rotateY(-0.0349);
    float d_node_1_scale = max(0.950, 0.0001);
    d_node_1_p /= d_node_1_scale;
    vec3 d_node_1_warp = d_node_1_p;
    d_node_1_warp.x = abs(d_node_1_warp.x);
    vec3 d_node_0_p = d_node_1_warp;
    d_node_0_p -= vec3(0.700, 0.100, 0.300);
    d_node_0_p *= rotateX(0.3491);
    d_node_0_p *= rotateY(0.2967);
    d_node_0_p *= rotateZ(0.3142);
    float d_node_0_scale = max(1.000, 0.0001);
    d_node_0_p /= d_node_0_scale;
    float d_node_0 = sdSphere(d_node_0_p, 0.400);
    d_node_0 *= d_node_0_scale;
    float d_node_1 = d_node_0;
    d_node_1 *= d_node_1_scale;
    return d_node_1;
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        sceneSDF(p + e.xyy) - sceneSDF(p - e.xyy),
        sceneSDF(p + e.yxy) - sceneSDF(p - e.yxy),
        sceneSDF(p + e.yyx) - sceneSDF(p - e.yyx)
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 skullPos = getSkullPosition(iTime * 2.);

    float camAngle = iTime * 2.;
    vec3 camOffset = vec3(
        cos(camAngle) * 6.0 + sin(iTime * 0.2) * 2.0,
        sin(iTime * 0.4) * 1.5 + 1.0,
        sin(camAngle) * 6.0 + cos(iTime * 0.25) * 2.0
    );

    vec3 ro = skullPos + camOffset;
    vec3 lookAt = skullPos;

    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    // Skull raymarch
    float t = 0.0;
    vec3 col = vec3(0.0);
    float eyeGlow = 0.0;
    bool hitSkull = false;
    float flicker = 0.5 + 0.3 * sin(iTime * 55.0 + sin(iTime * 70.0));
    float pulse = 0.5 + 0.5 * sin(iTime * 10.0);

    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);

        // Eye glow accumulation
        float eyeDist = eyesSDF(p) * 2.;
        eyeGlow += 0.025 / (0.1 + eyeDist * eyeDist * eyeDist) * flicker;

        if (d < 0.001) {
            vec3 n = calcNormal(p);

            // Dramatic lighting from below (fire/hellish)
            vec3 lightDir1 = normalize(vec3(0.0, -1.0, 0.2));
            vec3 lightDir2 = normalize(vec3(1.0, 0.5, 1.0));

            float diff1 = max(dot(n, lightDir1), 0.0);
            float diff2 = max(dot(n, lightDir2), 0.0);
            float amb = 0.05; // Darker ambient for scarier look

            float eyeDist = eyesSDF(p);
            if(eyeDist < 0.05) {
                // Glowing eyes with flicker
                col = vec3(1.0, 0.2, 0.05) * 20.5 * flicker;
            } else {
                // Skull lighting
                vec3 baseColor = vec3(0.7, 0.65, 0.6);
                vec3 fireLight = vec3(1.0, 0.3, 0.1) * diff1 * 0.8;
                vec3 rimLight = vec3(0.3, 0.4, 0.5) * diff2 * 0.3;

                col = baseColor * (diff2 * 0.4 + amb) + fireLight + rimLight;

                // Add atmospheric fog based on distance
                float fogAmount = 1.0 - exp(-t * 0.05);
                col = mix(col, vec3(0.02, 0.01, 0.02), fogAmount * 0.5);
            }
            hitSkull = true;
            break;
        }

        if (t > 30.0) break;
        t += d * 0.8; // Slower march for better cloud integration
    }

    // Cloud raymarch accumulation (from original shader)
    vec3 cloudCol = vec3(0.0);
    float cloudDist = 0.0;

    // Volumetric cloud rendering
    for(int i = 0; i < 64; i++) {
        vec3 p = ro + rd * cloudDist;
        float s = cloudDensity(p / 10.);

        // Accumulate cloud color with depth-based fog
        float density = 1.0 / (0.07 + 0.2 * abs(s));
        vec3 fogColor = vec3(0.02, 0.01, 0.01); // Dark purplish fog
        cloudCol += fogColor * density * 0.1;

        cloudDist += 0.5;
    }

    // Combine clouds and skull
    if(!hitSkull) {
        col = cloudCol;
    } else {
        // Blend skull with clouds based on depth
        col = mix(cloudCol, col, exp(-t * 0.2));
    }

    // Add eye glow
    vec3 eyeColor = vec3(1.0, 0.15, 0.03) * eyeGlow;
    col += eyeColor;

    // Enhanced atmospheric effects
    float smokeFbm = fbm(vec3(uv * 3.0, iTime * 0.15)) * 0.15;
    col = mix(col, vec3(0.08, 0.04, 0.05), smokeFbm);


    float vignette = 1.0 - length(uv) * 0.5;
    vignette = pow(vignette, 1.5);
    col *= vignette;

    // Contrast boost for dramatic effect
    col = pow(col, vec3(0.9));
    col = col / (1.0 + col);

    // Subtle color grading - desaturate and add slight green tint for eerie feel
    float lum = dot(col, vec3(0.299, 0.587, 0.114));
    col = mix(col, vec3(lum), 0.2);
    col *= vec3(0.95, 1.0, 0.98);

    // Motion blur feedback
    fragColor = (texture(iChannel0, fragCoord / iResolution.xy, 0.) * 2.0 + vec4(col, 1.0)) / 2.8;
}

