    return sin(x)*0.5+0.5;
}

float line(in int lineWidth, in vec2 pos, in vec2 point, in vec3 iResolution) {
    float normalizedLineRadius = (float(lineWidth) / iResolution.y) / 2.;
    float edgeWidth = 1. / iResolution.y;
    if(normalizedLineRadius<1./iResolution.x)
        return 0.;
	return smoothstep(pos.y-normalizedLineRadius,pos.y-edgeWidth,point.y-normalizedLineRadius+edgeWidth) * 
        (1.-smoothstep(pos.y+normalizedLineRadius-edgeWidth, pos.y+normalizedLineRadius+edgeWidth, point.y));
}

float smoothVal(in float x, in float max) {
	return clamp(smoothstep(0.0,1.0,x/max)*(1.-smoothstep(0.0,1.0,x/max))*4.,0.,1.);
}

//f(x) = amplitude*sinNormalized(frequency*x-offsetX)+d
float normSinFunct(in float amplitude, in float freq, in float offsetX, in float offsetY, in float x) {
    return amplitude*sinNorm(freq*x-offsetX)+offsetY;
}

float rand(float seed) {
    return fract(sin(dot(vec2(seed, seed / PI) ,vec2(12.9898,78.233))) * 43758.5453);   
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

//Make random values more random
float randSeed = 0.;

#define FLOORI(x) float(int(floor(x)))

/* old function
float smoothRand(float interval, float seed) {
    float next = rand(1.+floor(iTime/interval)+seed);
    float curr = rand(floor(iTime/interval)+seed);
    randSeed++;
    return mix(curr, next, fract(iTime/interval));
}
*/

float smoothRand(float interval, float seed) {
    float next = rand(.000001*FLOORI(1000000.*(1.+FLOORI(iTime/interval)+seed+randSeed)));
    float curr = rand(.000001*FLOORI(1000000.*(FLOORI(iTime/interval)+seed+randSeed)));
    randSeed++;
    return mix(curr, next, fract(iTime/interval));
}

float f(vec2 point) {
	return sin(point.x*2.+iTime*1.275)+point.y;   
}

vec2 grad( in vec2 x )
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( f(x+h.xy) - f(x-h.xy),
                 f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}

//https://iquilezles.org/articles/distance
float color( in vec2 point, in int lineWidth, in vec3 iResolution)
{
    float v = f( point );
    vec2  g = grad( point );
    float de = abs(v)/length(g);
    float normalizedLineRadius = (float(max(5,lineWidth)) / iResolution.y) / 2.;
    float edgeWidth = 1. / iResolution.y;
    if(normalizedLineRadius<1./iResolution.x)
        return 0.;
    float eps = max(1./iResolution.x, 1./iResolution.y)*normalizedLineRadius;
    return 1.-clamp(smoothstep( 0., normalizedLineRadius, de ), 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    //init
    vec2 uv = fragCoord/iResolution.xy;
    vec2 point = vec2( fragCoord.xy - 0.5*iResolution.xy );
	point = 2.0 * point.xy / iResolution.y;
    float x,y,z = 0.;
    fragColor = vec4(0);

    //Limit "frames" for uniterrupted animation
    float frameRatio = floor(iFrameRate/ 30.);
    if(mod(float(iFrame), frameRatio) != 0.) {
        float decay = sinNorm(iTime*0.789)*0.5+0.1;
        fragColor += texture(iChannel0, uv) * (1.-(1./(decay*iFrameRate)));

        //Clamp to prevent overexposure
        fragColor.r = clamp(fragColor.r, 0., 1.);
        fragColor.g = clamp(fragColor.g, 0., 1.);
        fragColor.b = clamp(fragColor.b, 0., 1.);
        return;   
    }

    //Scaling
    const float maxZoom = 1.5;
    const float minZoom = 0.5;
    const float changeInterval = 2.;
    float nextZ = rand(1.+floor(iTime/changeInterval));
    float currZ = rand(floor(iTime/changeInterval));
    z=minZoom+(maxZoom-minZoom)*mix(currZ, nextZ, fract(iTime/changeInterval));
    point/=vec2(z);

    //Rotation
    float rot = smoothRand(0.5,354.856)*PI;
    point=vec2(cos(rot)*point.x+sin(rot)*point.y, -sin(rot)*point.x+cos(rot)*point.y);

    //Translation
    point.x+=smoothRand(1.,842.546)*2.-1.;
    //No need to translate y here, bc y is set by the function in "f(point)" and the rotation.

    //Line
    const float minLength = 0.25;
    const float maxLength=0.5;
    float lineLength=minLength+smoothRand(4.,0.846)*(maxLength-minLength)+minLength;
    float linePoint = (point.x+lineLength/2.) / lineLength;
    //				clamp - make sure the value is in bounds
    //						  smoothVal - make the line thinner at the ends
    int lineWidth = int(clamp(floor(smoothVal(linePoint*100., 100.)*iResolution.x*0.025*z), 2., floor(iResolution.x*0.025*z)));//max(3,int((iResolution.x*0.1)*  pow((point.x*(1./lineLength)),3.)  ));
    if(point.x >= -lineLength / 2. && point.x <= lineLength / 2.) { //Only show a small segment
    	fragColor+=color(vec2(point.x,point.y), lineWidth, iResolution);//line(lineWidth, vec2(x,y), point, iResolution);
	}	
    /*if(point.x-x<0.005) {
        fragColor = vec4(1.);
    }
    if(point.y-y<0.005) {
		//fragColor = vec4(1.);
    }*/
    //Color
    fragColor.rgb*=hsv2rgb(vec3(fract(iTime/7.), sinNorm(iTime*rand(135.54))*0.4+0.6,1.));
    fragColor.rgb+=pow((fragColor.r+fragColor.g+fragColor.b)/3.+0.25,3.)-pow(0.25,3.);

    //Fade
    float decay = sinNorm(iTime*0.789)*0.5+0.25;
    fragColor += texture(iChannel0, uv) * (1.-(1./(decay*iFrameRate)));

    //Clamp to prevent overexposure
    fragColor.r = clamp(fragColor.r, 0., 2.);
    fragColor.g = clamp(fragColor.g, 0., 2.);
    fragColor.b = clamp(fragColor.b, 0., 2.);
    fragColor.a = 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 data = texture(iChannel0, fragCoord.xy/iResolution.xy, 0.);
    float d = data.a;
    d *= 3.0;
    vec3 col = (d > 0.0) ? vec3(0.9, 0.6, 0.3) : vec3(0.65, 0.85, 1.0);
    col *= 1.0 - exp(-6.0 * abs(d));
    col *= 0.8 + 0.2 * cos(150.0 * d);
    col = mix(col, vec3(1.0), 1.0 - smoothstep(0.0, 0.005, abs(d)));
    fragColor = vec4(col, 1.0);
    //fragColor = pow(texture(iChannel1, fragCoord.xy/iResolution.xy, 0.)*3., vec4(.7)); //view buffer A
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 tex = texture(iChannel0, fragCoord.xy/iResolution.xy, 0.);
    float inside = 0.02;
    float dist = tex.r;
    float newDist;

    if(iMouse.z > 0.0)
    {
        newDist = (distance(iMouse.xy, fragCoord.xy)/iResolution.x) - inside;

        if(tex.b == 0.1) {
            newDist = min(dist, newDist);
        }

        // Store distance in R, final corrected in G, state in B
        fragColor = vec4(newDist, tex.g, 0.1, 1.0);
    } 
    else if(tex.b != 0.1) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } 
    else {
        fragColor = tex;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 pixelSize = 1.0/iResolution.xy;

    vec4 drawn = texture(iChannel1, uv, 0.);
    vec4 prev = texture(iChannel0, uv, 0.);

    // If mouse is pressed, keep working with drawn data but preserve corrected
    if(iMouse.z > 0.0) {
        // Pass through drawn distance, but keep the corrected value
        fragColor = vec4(drawn.r, prev.g, drawn.b, 1.0);
        fragColor.a = (fragColor.r<0.) ? prev.g == 0.0? drawn.r:prev.g : fragColor.r;
        return;
    }

    float currentDist = prev.r;
    float correctedDist = prev.g; // Final corrected distance
    float state = prev.b;

    // Pixel-relative boundary threshold
    float boundaryThreshold = pixelSize.x;

    // Initialize: mark pixels near boundary as correct
    if(state == 0.1 && abs(currentDist) < boundaryThreshold) {
        state = 0.5;
        correctedDist = currentDist;
    }

    // If this pixel needs fixing (not yet corrected)
    if(state == 0.1 && currentDist < 0.0) {

        float bestDist = currentDist;
        bool foundCorrect = false;

        // Larger kernel for faster initial propagation
        const int kernelSize = 5; // Adjust this: 1=3x3, 2=5x5, 4=9x9, 8=17x17

        for(int y = -kernelSize; y <= kernelSize; y++) {
            for(int x = -kernelSize; x <= kernelSize; x++) {
                if(x == 0 && y == 0) continue;

                vec2 offset = vec2(x, y) * pixelSize;
                vec4 neighbor = texture(iChannel0, uv + offset, 0.);

                if(neighbor.b >= 0.5) {
                    float nDist = neighbor.g; // Use corrected distance from neighbor
                    float stepDist = length(vec2(x, y)) * pixelSize.x;
                    float proposedDist = nDist - stepDist;

                    if(!foundCorrect || proposedDist > bestDist) {
                        bestDist = proposedDist;
                        foundCorrect = true;
                    }
                }
            }
        }

        if(foundCorrect) {
            correctedDist = bestDist;
            state = 0.5;
        }
    }

    // Corrected pixels: refine distance and apply smoothing
    else if(state >= 0.5 && state < 1.0 && currentDist < 0.0) {

        float bestDist = correctedDist;

        // Still use larger kernel for refinement (or reduce to 1 for accuracy)
        const int refineKernel = 2; // 5x5 for refinement phase

        for(int y = -refineKernel; y <= refineKernel; y++) {
            for(int x = -refineKernel; x <= refineKernel; x++) {
                if(x == 0 && y == 0) continue;

                vec2 offset = vec2(x, y) * pixelSize;
                vec4 neighbor = texture(iChannel0, uv + offset, 0.);

                if(neighbor.b >= 0.5) {
                    float nDist = neighbor.g; // Use corrected distance
                    float stepDist = length(vec2(x, y)) * pixelSize.x;
                    float proposedDist = nDist - stepDist;

                    if(proposedDist > bestDist) {
                        bestDist = proposedDist;
                    }
                }
            }
        }

        correctedDist = bestDist;

        // Apply smoothing
        if(state > 0.5 && state < 0.9) {
            float sumDist = 0.0;
            float sumWeight = 0.0;

            for(int y = -1; y <= 1; y++) {
                for(int x = -1; x <= 1; x++) {
                    vec2 offset = vec2(x, y) * pixelSize;
                    vec4 neighbor = texture(iChannel0, uv + offset, 0.);

                    if(neighbor.b >= 0.5) {
                        float nDist = neighbor.g; // Use corrected distance
                        float weight = 1.0 / (1.0 + length(vec2(x, y)));
                        sumDist += nDist * weight;
                        sumWeight += weight;
                    }
                }
            }

            if(sumWeight > 0.0) {
                float smoothedDist = sumDist / sumWeight;
                float smoothStrength = 0.3 * (1.0 - (state - 0.5) / 0.4);
                correctedDist = mix(correctedDist, smoothedDist, smoothStrength);
            }
        }

        state = min(state + 0.009, 1.0);
    }

    // Store: R = current/working distance, G = corrected distance, B = state
    fragColor = vec4(currentDist, correctedDist, state, 1.0);
    fragColor.a = (fragColor.r<0.) ? fragColor.g : fragColor.r;
}

// Fork of "3D implicit plotter" by FordPerfect. https://shadertoy.com/view/W3d3R8
// 2025-06-30 19:34:48

// Public Domain under http://unlicense.org, see link for details.

// A simple implicit 3D surface plotter.

float f(vec3 r)
{
    r=r.xzy;
    r-=vec3(25.,30.,30.)/80.;
    return map(r);
    //return dot(r*r,r*r)-dot(r,r)+sqrt(0.125);
    return dot(r,r)-1.0 // Another similar shape.
        +exp(-8.0*dot(r.xy,r.xy))
        +exp(-8.0*dot(r.yz,r.yz))
        +exp(-8.0*dot(r.zx,r.zx));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 xy=(2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec3 col=vec3(1.0/128.0);
    vec3 aabb_lo=vec3(-1.125);
    vec3 aabb_hi=vec3(+1.125);
    float D=length(aabb_hi-aabb_lo);
    vec3 org=1.25*vec3(length((aabb_hi-aabb_lo).xy),0.0,(aabb_hi-aabb_lo).z);
    float t=iTime;
    org.xy=mat2(cos(t),-sin(t),sin(t),cos(t))*org.xy;
    org+=0.5*(aabb_lo+aabb_hi);
    vec3 fwd=normalize(0.5*(aabb_lo+aabb_hi)-org);
    vec3 rgt=normalize(cross(fwd,vec3(0,0,1)));
    vec3 up =normalize(cross(rgt,fwd));
    vec3 dir=normalize(xy.x*rgt+xy.y*up+2.5*fwd);
    vec2 s=vec2(0.0,1e7);
    vec3 ls=(aabb_lo-org)/dir,hs=(aabb_hi-org)/dir;
    if(dir.x>0.0) s=vec2(max(s.s,ls.x),min(s.t,hs.x)); else s=vec2(max(s.s,hs.x),min(s.t,ls.x));
    if(dir.y>0.0) s=vec2(max(s.s,ls.y),min(s.t,hs.y)); else s=vec2(max(s.s,hs.y),min(s.t,ls.y));
    if(dir.z>0.0) s=vec2(max(s.s,ls.z),min(s.t,hs.z)); else s=vec2(max(s.s,hs.z),min(s.t,ls.z));
    if(s.s<s.t)
    {
        float c=-1.0;
        float a=s.s;
        float A=f(org+a*dir);
        const int n=64;
        for(int i=1;i<=n;++i)
        {
            float b=mix(s.s,s.t,float(i)/float(n));
            float B=f(org+b*dir);
            if(A*B<0.0)
            {
                for(int i=0;i<4;++i)
                {
                    if(A==B) {c=0.5*(a+b); break;}
                    c=(a*B-b*A)/(B-A);
                    float C=f(org+c*dir);
                    if(A*C<0.0) {b=c; B=C;} else {a=c; A=C;}
                }
                break;
            }
        }
        col+=1.0/128.0;
        if(c>0.0)
        {
            vec3 r=org+c*dir;
            vec2 e=vec2(0,1e-2);
            vec3 light=normalize(-fwd+rgt+0.5*up);
            vec3 normal=normalize(vec3(
                f(r+e.yxx)-f(r-e.yxx),
                f(r+e.xyx)-f(r-e.xyx),
                f(r+e.xxy)-f(r-e.xxy)));
            col=vec3(0.0625)+0.5*max(dot(normal,light),0.0);
        }
    }
    col=mix(12.92*col,1.055*pow(col,vec3(1.0/2.4))-0.055,step(0.0031308,col)); // sRGB
    fragColor=vec4(col,1.0);
}

float sdSphere( in vec3 p, in float r)
{
    return length(p)-r;
}
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0)) - r;
}
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}
float rounding( in float d, in float h )
{
    return d - h;
}
float dot2(in vec2 v ) { return dot(v,v); }
float dot2(in vec3 v ) { return dot(v,v); }
float opSmoothUnion( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}
