}
vec4 opElongate( in vec3 p, in vec3 h )
{
    return vec4( p-clamp(p,-h,h), 0.0 ); // faster, but produces zero in the interior elongated box

    //vec3 q = abs(p)-h;
    //return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
}
// Euclidean distance to a capped torus
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
    p.x = abs(p.x);
    float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float sdVerticalVesicaSegment( in vec3 p, in float h, in float w )
{
    // shape constants
    h *= 0.5;
    w *= 0.5;
    float d = 0.5*(h*h-w*w)/w;

    // project to 2D
    vec2  q = vec2(length(p.xz), abs(p.y-h));

    // feature selection (vertex or body)
    vec3  t = (h*q.x < d*(q.y-h)) ? vec3(0.0,h,0.0) : vec3(-d,0.0,d+w);

    // distance
    return length(q-t.xy) - t.z;
}

float sdCylinder( vec3 p, vec2 h )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdRoundCone(vec3 p, vec3 a, vec3 b, float r1, float r2)
{
    // sampling independent computations (only depend on shape)
    vec3  ba = b - a;
    float l2 = dot(ba,ba);
    float rr = r1 - r2;
    float a2 = l2 - rr*rr;
    float il2 = 1.0/l2;

    // sampling dependant computations
    vec3 pa = p - a;
    float y = dot(pa,ba);
    float z = y - l2;
    float x2 = dot2( pa*l2 - ba*y );
    float y2 = y*y*l2;
    float z2 = z*z*l2;

    // single square root!
    float k = sign(rr)*rr*rr*x2;
    if( sign(z)*a2*z2 > k ) return  sqrt(x2 + z2)        *il2 - r2;
    if( sign(y)*a2*y2 < k ) return  sqrt(x2 + y2)        *il2 - r1;
                            return (sqrt(x2*a2*il2)+y*rr)*il2 - r1;
}

float sdHead( vec3 p, float r ){
    // TOP - Cranium
    float cran = sdSphere(p, r);
    // BOTTOM - Jaw/Cheekbones
    vec2 os = vec2(-0.14,0.05);
    float ms = 0.1215;
    vec3 ql = p - vec3(ms,os);
    vec3 qr = p - vec3(-ms,os);
    ql = ql*rotateZ(0.26);
    qr = qr*rotateZ(-0.26);
    ql = ql*rotateX(0.42);
    qr = qr*rotateX(0.42);
    vec3 a = vec3(0.,-0.4, 0.0);
    vec3 b = vec3( 0., 0.4, 0.0);
    float l = length(b-a);
    float wv = sqrt(0.12/l);
    float jaw = opSmoothUnion(sdVerticalVesicaSegment( ql-a, l, wv*0.85 ) - wv*0.15, 
    sdVerticalVesicaSegment( qr-a, l, wv*0.85 ) - wv*0.15, 0.07);
    cran = opSmoothUnion( cran, jaw, 0.03);
    // SHAPING - sides
    float d2 = sdRoundBox(p-vec3(0.76,-0.6,-0.15), vec3(0.4,0.4,0.4), 0.02 ); 
    float d1 = sdRoundBox(p-vec3(-0.76,-0.6,-0.15), vec3(0.4,0.4,0.4), 0.02); 
    float d3 = sdRoundBox(p-vec3(0.76,0.35,-0.2), vec3(0.4,0.4,0.4), 0.02 ); 
    float d4 = sdRoundBox(p-vec3(-0.76,0.35,-0.2), vec3(0.4,0.4,0.4), 0.02 ); 
    cran = opSmoothSubtraction(d4,opSmoothSubtraction(d3,cran, .2), .2);
    cran = opSmoothSubtraction(d2,opSmoothSubtraction(d1,cran, .2), .2);
    // SHAPING - top
    float d6 = sdRoundBox(p-vec3(0.,0.8,-0.0), vec3(0.4,0.4,0.4), 0.03 ); 
    cran = opSmoothSubtraction(d6,cran,.5);
    // SHAPING - front
    float d7 = sdRoundBox(p-vec3(0.,0.42,0.82), vec3(0.4,0.4,0.4), 0.03 ); 
