/*
    Adjusted the turbulence function.
    Now it extracts the level of the note C from each octave.
    And uses that as amplitude for each of the wave distortion octaves.
    Or that's the idea anyway
*/

// Scroll speed
#define SCROLL 0.3  // Reduced for a more relaxed effect
// Number of turbulence waves
#define FIRE_NUM 10.0  // Fewer waves to reduce chaos
// Turbulence wave amplitude
#define FIRE_AMP 0.6  // Softer flickering
// Turbulence wave speed
#define FIRE_SPEED 6.5  // Slower, smoother movement
// Turbulence frequency (inverse of scale)
#define FIRE_FREQ 8.  // Lower frequency for calmer movement
// Turbulence frequency multiplier
#define FIRE_EXP 1.3 // Less variation

// Audio-reactive intensity scale
#define SOUND_INTENSITY 0.4  // Very subtle reaction to sound
#define SOUND_SMOOTHING 0.98  // Strong smoothing for gradual changes

vec3 colour = vec3(0);
// Bicubic noise texture sample
vec4 noise(vec2 p)
{
    vec2 f = floor(p);
    vec2 s = p - f;
    s *= s * (3.0 - 2.0 * s);

    return texture(iChannel0, (f + s + 0.5) / 256.0);
}

// Turbulence function
vec2 turbulence(vec2 p, float F, float N, float S, float A, float E)
{
    float freq = F;
    mat2 rot = mat2(0.6, -0.8, 0.8, 0.6);
    float speed = 0.;
    for (float i = 0.0; i < N; i++)
    {
        float freqC = 8372.02/pow(2.0, 9.0-float(i));
        float freqv = texture(iChannel1, vec2(freqC/10000.,0.0)).r;
        freqv*=(i/4.)+1.;
        float phase = freq * (p * rot).y + freqv * iTime * 0.1;

        if(i<3.){colour.r += freqv;} else if (i<5.){colour.g += freqv;} else {colour.b += freqv;}
        p += freqv * A * rot[0] * sin(phase) / freq;
        rot *= mat2(0.6, -0.8, 0.8, 0.6);
        freq *= E;
    }

    return p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Centered, aspect-corrected coordinates
    vec2 p = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;
    p*=1.4;
    float xstretch = 1.5 - 1.0 * smoothstep(-2.0, 2.0, length(p)); // Circular scaling
    float ystretch = 1.05 - 0.15 / (1.0 + dot(p, p)); // Reduced distortion
    vec2 stretch = vec2(xstretch, ystretch);
    p *= stretch;

    // Get sound input from iChannel1 and apply a gentle effect
    float rawAudio = texture(iChannel1, vec2(0.01, 0.25)).r;
    float audio = mix(1.0, 1.0 + rawAudio * SOUND_INTENSITY, SOUND_SMOOTHING);

    //vec3 pd = 

    float scroll = SCROLL  * iTime * audio;  // Gentle sound response
    p.y -= scroll * 0.5; // Keep movement subtle


    p = turbulence(p, FIRE_FREQ, FIRE_NUM, FIRE_SPEED, FIRE_AMP, FIRE_EXP);

    vec2 sp = p;
    p.y += scroll * 0.5;

    float radius = 0.03 + 0.03 * noise(sp / 0.05).x * audio; // Smooth circular pulsing
    float dist = length(p) - radius;

    vec2 uv = sp * FIRE_FREQ * 1.1;
    float n = noise(uv * 1.5).x + noise(uv * 0.5).y + noise(uv * 0.2).z;
    float light = smoothstep(0.4, 0.15, dist) / (0.1 - 0.015 * n + dist * dist);
    colour *= 0.3;colour *= colour;
    float spec = 1.0 / noise(sp * 30.0).a;
    vec3 sparks = 0.02 * spec * spec * colour / (1.0 + 6.0 * dot(p, p)); // Subtle radial sparks

    vec3 col = n * light * colour + sparks;

    col = 1.0 - exp(-col);
    fragColor = vec4(col, 1);
}

#define MARCH_STEPS 100
#define PART_COUNT 20

#define time 1.0
float PI = acos(-1.0);

float cyl(vec2 p, float r) {
  return length(p)-r;
}

vec3 moda(vec3 p, float rep, float off) {
  vec2 rp = vec2(atan(p.z,p.x)/(2.0*PI), length(p.xz));
  rp.x=(fract(rp.x*rep-0.5+off)-0.5)/rep;
  rp.x *= 2.0*PI;
  return vec3(cos(rp.x)*rp.y,p.y,sin(rp.x)*rp.y);
}

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}

float noise(vec3 p) {
  vec3 ip=floor(p);
  p=fract(p);
  p=smoothstep(0.0,1.0,p);
  vec3 st=vec3(7,37,289);
  vec4 pos=dot(ip,st) + vec4(0,st.y,st.z,st.y+st.z);
  vec4 val=mix(fract(sin(pos)*7894.552), fract(sin(pos+st.x)*7894.552), p.x);
  vec2 val2=mix(val.xz,val.yw, p.y);
  return mix(val2.x,val2.y, p.z);
}

float fractal(vec3 p) {

  float d=0.5;
  float f=0.0;
  for(int i=0; i<5; ++i) {
    f+=noise(p/d)*d;
    d*=.5;
  }
  return f;
}

float smin(float a, float b, float h) {
  float k=clamp((a-b)/h*0.5+0.5,0.0,1.0);
  return mix(a,b,k)-k*(1.0-k)*h;
}

vec3 tunnel(vec3 p){
  vec3 off=vec3(0);
  off.x += sin(p.z*0.2)*1.5;
  off.y += sin(p.z*0.3)*1.3;
  return off;
}

float map(vec3 p) {


  float t1 = time;
  float o = noise(p*3.0 + t1*0.2);
  p += (noise(p*10.0 + t1*0.7)-0.5)*0.1;
  p += tunnel(p);

  float s=10.0;
  p.xy = (fract(p.xy/s-0.5)-0.5)*s;

  vec3 p1=p;
  p1.xy *= rot(p.z*0.2+sin(p.z*1.8)*0.2 + t1*0.1);

  p1=moda(p1.xzy, 5.0, 0.0);
  float d = cyl(p1.xz-vec2(1,0),0.0+o*0.3-0.1)+0.1;

  vec3 p2=p;
  p2.xy *= rot(p.z*1.2+sin(p.z*0.8)*0.3 + t1*0.13);

  p2=moda(p2.xzy, 9.0,sin(p2.z)*0.9);
  float d2 = cyl(p2.xz-vec2(1.3,0),0.05);

  d=smin(d, d2, 0.9);

  return d;
}

vec3 norm(vec3 p) {
  vec2 off=vec2(0.01,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx))+0.000001);
}

float getao(vec3 p, vec3 n, float dist) {
  return clamp(map(p+n*dist)/dist,0.0,1.0);
}

float getsss(vec3 p, vec3 r, float dist) {
  return clamp(map(p+r*dist)*3.0,0.0,1.0);
}

float rnd(float t) {
  return fract(sin(t*789.451)*7541.223);
}

float rnd(vec2 t) {
  return fract(dot(sin(t*vec2(789.451)+t.yx*vec2(842.544)),vec2(7541.223)));
}

float dots(vec3 p, float j) {
  float v=0.0;

  p*=4.0+1.0*sin(j);
  p.x += rnd(floor(p.y));

  p*=PI;
  v += clamp(0.1-length(vec2(sin(p.x),cos(p.y))),0.0,1.0)*10.3;
  return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 s=vec3(0.0,0.0,-3);
  vec3 t=vec3(0,0,0);
  float t2 = time*0.5;
  s.x += sin(t2*0.7)*0.5;
  s.y += sin(t2*0.9)*0.5;

  s.z += t2;
  t.z += t2;
  s -= tunnel(s);
  t -= tunnel(t);
  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz*0.7);
  //vec3 r=normalize(vec3(-uv, 0.7));

  vec3 p=s;
  int i=0;
  float mask=1.0;
  float d = 10000.0;

  float rand=rnd(uv);
  float dither=0.5+0.1*rand;

  for(; i<MARCH_STEPS; ++i) {
    d=map(p);
    if(abs(d)<0.001) {
      mask=0.0;
      break;
    }
    p+=r*d*dither;
  }

  vec3 col=vec3(0);
  vec3 n = norm(p);
  vec3 l =normalize(vec3(-0.2,0.2,0.5));
  float t1=sin(time*0.2);
  l.xz *= rot(t1);
  l.xy *= rot(t1*3.2);
  vec3 h = normalize(l-r);
  float f = pow(1.0-abs(dot(n,r)), 3.0);
  float fog = pow(1.0-float(i)/100.0,2.0);

  float aodist=0.7;
  float ao = getao(p,n,aodist*0.2) * (getao(p,n,aodist*0.35)*0.5+0.5) * (getao(p,n,aodist*0.6)*0.25+0.75);
  float sss = (getsss(p,r,0.2)+getsss(p,r,0.5)*0.5)*0.9;

  vec3 back = mix(vec3(0.7,0.2,0.1), vec3(1,0.7,0.2), pow(max(0.0,dot(r,l)),5.0));

  float diff = fractal(p*15.0);
  //diff=abs(diff-0.3)*2;
  diff=pow(smoothstep(0.1,0.9,diff),5.0)*2.7+0.9;

  col += max(0.0,dot(n,l)*0.5+0.5) * fog * ao * (vec3(1,0.7,0.3) + pow(max(0.0,dot(n,h)),20.0));
  col += vec3(0.7,0.2,0.1)*0.5*pow(n.y*0.5+0.5,3.0);
  col += sss * fog * back * vec3(1,0.2,0.2) * 1.5 * diff;
  col += 3.0*f*(-n.y*0.5+0.5)*fog;

  float len = length(p-s);
  col += back * max(clamp(d,0.0,1.0), clamp(dot(p-s,p-s)/200.0,0.0,1.0));

  vec3 col2 = vec3(0);
  for(int j=1; j<PART_COUNT; ++j) {
    float dist = float(j) * 0.2/r.z;
    if(dist>len) break;
    vec3 vp = vec3(s.x,s.y,0) + r*dist;
    vp.xy *=rot(sin(vp.z*10.0+time*0.2));

    col2 += dots(vp, float(j)) * clamp(1.0-dist/float(PART_COUNT), 0.0,1.0);
  }
  col += col2 * back;

  col *= pow(clamp(1.2-length(uv),0.0,1.0)*1.3,1.7);
  //col = vec3(rnd(uv));

  fragColor = vec4(col, 1);
}

// Fork of "Turbulent Flame" by Xor. https://shadertoy.com/view/wffXDr
// 2025-03-19 21:26:09

/*
    "Turbulent Flame" by @XorDev

    For my tutorial on Turbulence:
    https://mini.gmshaders.com/p/turbulence

    Simulating proper fluid dynamics can be complicated, limited, and requires a multi-pass setup.

    Sometimes you just want some smoke, fire, or fluid, and you don't want to go through all that trouble.

    This method is very simple! Start with pixel coordinates and scale them down as desired,
    then loop through adding waves, rotating the wave direction and increasing the frequency.
    To animate it, you can add a time offset to the sine wave.
    It also helps to shift each iteration with the iterator "i" to break up visible patterns.

    The resulting coordinates will appear turbulent, and you can use these coordinates in a coloring function.

    Smooth, continious equations look best!

    To complete the flame look, we need to scroll the waves and expand the coordinate space upwards 
*/

//Fire ring radius

//Falloff gradient

//Scroll speed
#define SCROLL 1.6
//Flicker intensity
#define FLICKER 0.12
//Flicker animation speed
#define FLICKER_SPEED 6.0

//Number of turbulence waves
#define TURB_NUM 10.0
//Turbulence wave amplitude
#define TURB_AMP 0.4
//Turbulence wave speed
#define TURB_SPEED 6.0
//Turbulence frequency (inverse of scale)
#define TURB_FREQ 7.0
//Turbulence frequency multiplier
#define TURB_EXP 1.3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float RADIUS = iMouse.x/iResolution.x;
    float GRADIENT = iMouse.y/iResolution.y;
    //Screen coordinates, centered and aspect corrected
    vec2 p = (fragCoord.xy*2.0-iResolution.xy) / iResolution.y;
    p*=3.;
    p.y+=0.6;
    if(p.y<0.){p.y*=1.+abs(p.y);}
    vec2 screen = p;
    screen *=0.6;
    screen.x *= 2.2;
    screen.y-=1.2;
    //Expand vertically
    float xstretch = 2.0 - 1.5*smoothstep(-2.0,2.0,p.y);
    //Decelerate horizontally
    float ystretch = 1.0 - 0.5 / (1.0+p.x*p.x);
    //Combine
    vec2 stretch = vec2(xstretch, ystretch);
    //Stretch coordinates
    p *= stretch;

    //Scroll upward
    float scroll = SCROLL*iTime;
    p.y -= scroll;

    //Turbulence starting scale
    float freq = TURB_FREQ;

    //Turbulence rotation matrix
    mat2 rot = mat2(0.6, -0.8, 0.8, 0.6);

    //Loop through turbulence octaves
    for(float i=0.0; i<TURB_NUM; i++)
    {
        //Scroll along the rotated y coordinate
        float phase = freq * (p * rot).y + TURB_SPEED*iTime + i;
        //Add a perpendicular sine wave offset
        p += TURB_AMP * rot[0] * sin(phase) / freq;

        //Rotate for the next octave
        rot *= mat2(0.6, -0.8, 0.8, 0.6);
        //Scale down for the next octave
        freq *= TURB_EXP;
    }
    //Reverse the scrolling offset
    p.y += scroll;

    //Distance to fireball
    float dist = length(min(p,p/vec2(1,stretch.y))) - RADIUS;
    //Attenuate outward and fade vertically
    float light = 1.0/pow(dist*dist+GRADIENT*max(p.y+.5,0.0),3.0);
    //Coordinates relative to the source
    vec2 source = p + 2.0*vec2(0,RADIUS) * stretch;
    //RGB falloff gradient
    vec3 grad = 0.1 / (1.0 + 8.0*length(source) / vec3(9, 2, 1));

    //Flicker animation time
    float ft = FLICKER_SPEED * iTime;
    //Flicker brightness
    float flicker = 1.0+FLICKER*cos(ft+sin(ft*1.618-p.y));
    //Ambient lighting
    vec3 amb = 64.0*flicker/(1.0+dot(screen,screen))*grad;
    amb.r*=0.4;
    //Scrolling texture uvs
    vec2 uv = (p - SCROLL*vec2(0,iTime)) / 1e2 * TURB_FREQ;
    //Sample texture for fire
    vec3 tex = texture(iChannel0,uv).rgb;

    //Combine ambient light and fire
    vec3 col = amb + light*grad*tex;
    //Exponential tonemap
    //https://mini.gmshaders.com/p/tonemaps
    col = 1.0 - exp(-col);
    fragColor = vec4(col,1);
}

