    float endAngle = (phase + 1.) * radians(90.);

    float angle;
    if (localTime < 1.)
    {
        angle = mix(startAngle, endAngle, localTime / 1.);
    }
    else
    {
        angle = endAngle;
    }        

    p *= rotate(angle);

    float eSize = .1;        // fork edit
    float margin = eSize/2.; // fork edit

    float O = 1.;

    float a = length(vec2(p.x - .5 + eSize + margin, p.y)) - eSize;
    float b = abs(length(vec2(p.x + .5 - eSize - margin, p.y)) - eSize) - .01;
    O *= min(a, b);

    vec2 dist = abs(vec2(p.x, p.y - .5 + eSize + margin)) - vec2(eSize);
    float c = length(max(dist, .0)) + min(max(dist.x, dist.y), 0.);

    dist = abs(vec2(p.x, p.y + .5 - eSize - margin)) - vec2(eSize);
    float d = abs(length(max(dist, .0)) + min(max(dist.x, dist.y), 0.)) - .01;

    O *= min(c, d);
    return O;
}

