float func(float x,float y)
{
    return ( 2.0 * p(x,3) * p(y,3) )-( p(x,4) * p(y,2) )-( p(x,2) * p(y,4) ) + 36.0;
}

float plot_func(vec2 uv)
{
    float y = func(uv.x,uv.y);
   //float y = func(uv.x);
    return smoothstep(y-0.01,y+0.01,uv.y);
}

float draw_func(vec2 uv)
{
    float f=0.;
    for(int i=0;i<AA;i++)
    {
        for(int j=0;j<AA;j++)
        {
            vec2 offset = (vec2(float(i),float(j))-.5*float(AA))/float(AA)*2.;
            f+=plot_func(UV(uv+offset));
        }
    }
    if(f>float(AA*AA)/2.)
        f=float(AA*AA)-f;
    f=f*2./float(AA*AA);
    return f;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=UV(fragCoord);

    vec3 col = vec3(0.);

    col =CoordinateGrid(uv);

    float se = draw_func(fragCoord);
    col=mix(col,vec3(1.),se);

    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 color, in vec2 coord) {
    color = texelFetch(iChannel0, ivec2(coord), 0);
    color.a = 1.0;
}

// Fork of "ColorRWM" by jlindermeir. https://shadertoy.com/view/tXXSRr
// Real random wave model with phase-based coloring.
// 2025-04-17 01:13:11

float h(float p){return fract(sin(p*11.3158)*45413. + (iTime/1000.));}
vec2 rd(float s){float a=s*6.28318530718;return vec2(cos(a),sin(a));}
vec3 h2r(vec3 c){vec3 r=clamp(abs(mod(c.x*6.0+vec3(0,4,2),6.0)-3.0)-1.0,0.0,1.0);return c.z*mix(vec3(1),r,c.y);}
void mainImage(out vec4 o,in vec2 f){
  float k=200.0*(cos(1.0/15.0*6.28318530718)+1.1),om=3.0,s=0.0;
  vec2 uv=f/iResolution.xy,p=(uv-0.5)*2.0; p.x*=iResolution.x/iResolution.y; vec2 si=vec2(0);
  int n=100;float sd=h(1337.0);
  for(int i=0;i<n;i++){
    sd=h(float(i));
    vec2 d=rd(sd),kv=d*k;
    float ph=h(float(i)+1337.0),tp=dot(p,kv)+om*(iTime/1000.)+ph*6.28318530718;
    s+=cos(tp),si+=vec2(cos(tp),sin(tp));
  }
  s*=s*0.4;
  float ha=atan(si.y,si.x),hu=(ha+3.14159265)/(6.28318530718),v=(s/float(n))*0.5,sa=2.0*abs(v-1.0);
  o=vec4(h2r(vec3(hu,sa,v)),1.0);

  vec4 color = texelFetch(iChannel0, ivec2(f), 0);
  o=o+(color/3.1);
}

