float noiseAt(float tt, float rate)
{
	float x = tt * rate;
	float xi = floor(x);
	float xf = fract(x);
	float h0 = fract(sin(xi * 12.9898 + 78.233) * 43758.5453);
	float h1 = fract(sin((xi + 1.0) * 12.9898 + 78.233) * 43758.5453);
	float u = xf * xf * (3.0 - 2.0 * xf);
	return 1.*(mix(h0, h1, u) - 0.5);
}

vec2 mainSound( int samp, float time )
{
	// Breathy "ha ha ha" with aspirated H and vowel via formants
	float t = mod(time, 1.6); // Repeat every ~1.6 seconds
	float duration = 0.5;     // Duration of each "ha"
	float spacing = 0.4;     // Spacing between "ha"s
	float amp = 0.0;

	for (int i = 0; i < 3; i++) {
		float ti = t - float(i) * (spacing/2. + spacing/float(i+2));
		if (ti > 0.0 && ti < duration) {
			// H
			float hDur = 0.08; // breath length
			float hSound = 0.0;
			if (ti < hDur) {
				// Smooth noise then emphasize highs with a tiny temporal derivative (HP-ish)
				float nA= noiseAt(ti, 20000.0);
				float nB = noiseAt(max(ti - 0.0006, 0.0), 18000.0);
				float breath = (nA - nB); // high-frequency emphasis like a soft hiss
				// Light band-pass feel by mixing two nearby "rates" (very rough spectral shaping)
				float nC = noiseAt(ti, 9000.0);
				breath = mix(breath, (breath + nC * 0.6), 0.4);
				// Envelope: soft attack, gentle decay
				float hEnv = smoothstep(0.0, 0.07, ti) * (1.0 - smoothstep(0.01, hDur, ti));
				hSound = breath * hEnv * 0.03 ;//* float(i);
			}

			// ah
			float vStart = 0.04; // when voicing begins after breath
			float vSound = 0.0;
			if (ti > vStart) {
				float vt = ti - vStart;
				// Fundamental with tiny vibrato and subtle pitch bend per syllable
				float f0Base = 110.0; // base laugh pitch
				float f0Jitter = 1.5 + 0.01 * sin(6.2831 * 5.5 * vt);
				float f0Bend = 1.0 + 0.01 * exp(-7.0 * vt); // slight down-glide
				float f0 = f0Base * f0Jitter * f0Bend * (1.0 - 0.03 * pow(float(i),1.5)); // rise a bit each "ha"

				// Vowel "a"/"ɑ" approximate male formants
				// Frequencies (Hz) and bandwidths (Hz)
				float F1 = 800.0, BW1 = 90.0;
				float F2 = 1200.0, BW2 = 120.0;
				float F3 = 3800.0, BW3 = 200.0;

				// Envelope: quick attack to sustain, then decay
				float vEnv = smoothstep(0.0, 0.03, vt) * exp(-3. * vt);

				// Additive harmonic stack shaped by Gaussian formant envelopes
				float voiced = 0.0;
				for (int k = 1; k <= 10; k++) {
					float hk = float(k);
					float fk = f0 * hk;
					// Gaussian weights around formants
					float w1 = exp(-0.5 * pow((fk - F1) / BW1, 2.0));
					float w2 = exp(-0.5 * pow((fk - F2) / BW2, 2.0));
					float w3 = exp(-0.5 * pow((fk - F3) / BW3, 2.0));
					float w = (w1 * 0.9 + w2 * 0.9 + w3 * 0.4);
					voiced += sin(6.2831 * fk * vt) * w;
				}
				// Normalize and apply envelope
				vSound = voiced * (1.0 / 12.0) * vEnv * 0.8;

				// Subtle breath mixed into vowel for naturalness
				float nV = noiseAt(vt, 16000.0) - noiseAt(max(vt - 0.0006, 0.0), 16000.0);
				vSound += nV * 0.06 * vEnv;
			}

			amp += vSound+hSound;
		}
	}

	amp = clamp(amp, -1.0, 1.0) * 0.8;
	return vec2(amp);
}

