void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 guv = (uv*20.0)-10.0;
    float x = guv.x;
    float y = guv.y;

    float result = ( 2.0 * p(x,3) * p(y,3) )-( p(x,4) * p(y,2) )-( p(x,2) * p(y,4) ) + 36.0;

    // Output to screen
    float blue = abs(min(result, 0.0))/1000.0;
    float red = max(result, 0.0)/100.0;
    float white = 1.0/(abs(result)+0.001);

    fragColor = vec4(red+white, white, blue+white,1.0);
}

