    vec2 pointA = iMouse.xy/iResolution.xy;   // BLACK
    vec2 pointB = iMouse.zw/iResolution.xy;  // RED
    vec2 pointC = pointB*vec2(1,-1);        // GREEN
    vec2 pointD = pointB*vec2(-1,-1);       // BLUE

    float distA = distance(uv,pointA);
    float distB = distance(uv,pointB);
    float distC = distance(uv,pointC);
    float distD = distance(uv,pointD);

    float size = 0.05;
    vec3 col = vec3(1);

    col =distB<size?vec3(1,0,0):distC<size?vec3(0,1,0):distD<size?vec3(0,0,1): distA<size?vec3(0):col;

    fragColor = vec4(vec3(col),1.0);
}

