    for(int i = 0; i<int(MAX_ITER); i++){
        if(zx * zx + zy * zy < R*R && iter<MAX_ITER){
			float xtemp = zx*zx - zy*zy;
        	zy = 2. * zx * zy + cy;
        	zx = xtemp + cx;
        	iter+=1.;
        } else {
