    float dist = length(vec2(zx,zy));
    float fracIter = log2(log(dist) / log(R)) - 1.;
    iter -= fracIter;
    float firstcol = sqrt(iter/10.);
    if(firstcol<1.0){
        firstcol*=firstcol;
        vec3 nextcol = juliaB(nextzx*firstcol, nextzy*firstcol, nextcx, nextcy);
